INSERT INTO `wp_posts` VALUES ('28', '1', '2016-11-17 13:58:25', '2016-11-24 06:58:25', '<pre>//获取通知的数据\r\n$xml = $GLOBALS[\'HTTP_RAW_POST_DATA\'];\r\n//如果接受不到，使用以下方式接受\r\nif(!$xml){\r\n    $xml= file_get_contents(\"php://input\");\r\n}</pre>', '微信官方example 在php7下接收不到xml数据解决办法', '', 'publish', 'open', 'open', '', '%e5%be%ae%e4%bf%a1%e5%ae%98%e6%96%b9example-%e5%9c%a8php7%e4%b8%8b%e6%8e%a5%e6%94%b6%e4%b8%8d%e5%88%b0%e6%95%b0%e6%8d%ae%e8%a7%a3%e5%86%b3%e5%8a%9e%e6%b3%95', '', '', '2016-11-24 14:58:45', '2016-11-24 06:58:45', '', '0', 'http://luchanglong.com.cn/?p=28', '0', 'post', '', '0');
