INSERT INTO `wp_posts` VALUES ('47', '1', '2016-11-30 10:31:42', '2016-11-30 02:31:42', 'location /{\r\nif (!-e $request_filename) {\r\nrewrite ^(.*)$ /index.php?s=/$1 last;\r\nbreak;\r\n}\r\n}', 'thinkphp5 nginx 伪静态 rewrite 配置', '', 'publish', 'open', 'open', '', 'thinkphp5-nginx-%e4%bc%aa%e9%9d%99%e6%80%81-rewrite-%e9%85%8d%e7%bd%ae', '', '', '2016-11-30 10:31:42', '2016-11-30 02:31:42', '', '0', 'http://luchanglong.com.cn/?p=47', '0', 'post', '', '0');
