INSERT INTO `wp_posts` VALUES ('50', '1', '2016-12-05 14:50:23', '2016-12-05 06:50:23', '<div>$ git remote -v  查看远程仓库地址</div>\r\n<div>\r\n<div>$ git remote rm origin    删除远程仓库地址</div>\r\n<div>\r\n<div>$ git remote add origin [url] 添加远程仓库地址</div>\r\n<div>$ git remote set-url origin [url]  修改远程仓库地址</div>\r\n</div>\r\n</div>', 'git远程仓库地址 查看 添加 删除 修改', '', 'publish', 'open', 'open', '', 'git%e8%bf%9c%e7%a8%8b%e4%bb%93%e5%ba%93%e5%9c%b0%e5%9d%80-%e6%b7%bb%e5%8a%a0-%e5%88%a0%e9%99%a4-%e4%bf%ae%e6%94%b9', '', '', '2016-12-05 14:50:52', '2016-12-05 06:50:52', '', '0', 'http://luchanglong.com.cn/?p=50', '0', 'post', '', '0');
