INSERT INTO `wp_posts` VALUES ('53', '1', '2016-12-06 14:56:14', '2016-12-06 06:56:14', '<div class=\"line number1 index0 alt2\">\r\n<pre class=\"lang:php decode:true  \">function bubbleSort($numbers) {\r\n    $cnt = count($numbers);\r\n    for ($i = 0; $i &lt; $cnt; $i++) {\r\n        for ($j = 0; $j &lt; $cnt - $i - 1; $j++) {\r\n            if ($numbers[$j] &gt; $numbers[$j + 1]) {\r\n                $temp = $numbers[$j];\r\n                $numbers[$j] = $numbers[$j + 1];\r\n                $numbers[$j + 1] = $temp;\r\n            }\r\n        }\r\n    }\r\n \r\n    return $numbers;\r\n}\r\n \r\n$num = array(20, 40, 60, 80, 30, 70, 90, 10, 50, 0);\r\nvar_dump(bubbleSort($num));\r\n \r\n//输出结果如下：\r\n//array(10) {\r\n//  [0]=&gt;\r\n//  int(0)\r\n//  [1]=&gt;\r\n//  int(10)\r\n//  [2]=&gt;\r\n//  int(20)\r\n//  [3]=&gt;\r\n//  int(30)\r\n//  [4]=&gt;\r\n//  int(40)\r\n//  [5]=&gt;\r\n//  int(50)\r\n//  [6]=&gt;\r\n//  int(60)\r\n//  [7]=&gt;\r\n//  int(70)\r\n//  [8]=&gt;\r\n//  int(80)\r\n//  [9]=&gt;\r\n//  int(90)\r\n//}</pre>\r\n&nbsp;\r\n\r\n<code class=\"php keyword\"></code></div>', 'php 冒泡排序', '', 'publish', 'open', 'open', '', 'php-%e5%86%92%e6%b3%a1%e6%8e%92%e5%ba%8f', '', '', '2017-01-10 10:58:48', '2017-01-10 02:58:48', '', '0', 'http://luchanglong.com.cn/?p=53', '0', 'post', '', '0');
INSERT INTO `wp_posts` VALUES ('54', '1', '2016-12-06 14:56:14', '2016-12-06 06:56:14', '<div class=\"line number1 index0 alt2\"><code class=\"php keyword\">function</code> <code class=\"php plain\">bubbleSort(</code><code class=\"php variable\">$numbers</code><code class=\"php plain\">) {</code></div>\r\n<div class=\"line number2 index1 alt1\"><code class=\"php spaces\">    </code><code class=\"php variable\">$cnt</code> <code class=\"php plain\">= </code><code class=\"php functions\">count</code><code class=\"php plain\">(</code><code class=\"php variable\">$numbers</code><code class=\"php plain\">);</code></div>\r\n<div class=\"line number3 index2 alt2\"><code class=\"php spaces\">    </code><code class=\"php keyword\">for</code> <code class=\"php plain\">(</code><code class=\"php variable\">$i</code> <code class=\"php plain\">= 0; </code><code class=\"php variable\">$i</code> <code class=\"php plain\">&lt; </code><code class=\"php variable\">$cnt</code><code class=\"php plain\">; </code><code class=\"php variable\">$i</code><code class=\"php plain\">++) {</code></div>\r\n<div class=\"line number4 index3 alt1\"><code class=\"php spaces\">        </code><code class=\"php keyword\">for</code> <code class=\"php plain\">(</code><code class=\"php variable\">$j</code> <code class=\"php plain\">= 0; </code><code class=\"php variable\">$j</code> <code class=\"php plain\">&lt; </code><code class=\"php variable\">$cnt</code> <code class=\"php plain\">- </code><code class=\"php variable\">$i</code> <code class=\"php plain\">- 1; </code><code class=\"php variable\">$j</code><code class=\"php plain\">++) {</code></div>\r\n<div class=\"line number5 index4 alt2\"><code class=\"php spaces\">            </code><code class=\"php keyword\">if</code> <code class=\"php plain\">(</code><code class=\"php variable\">$numbers</code><code class=\"php plain\">[</code><code class=\"php variable\">$j</code><code class=\"php plain\">] &gt; </code><code class=\"php variable\">$numbers</code><code class=\"php plain\">[</code><code class=\"php variable\">$j</code> <code class=\"php plain\">+ 1]) {</code></div>\r\n<div class=\"line number6 index5 alt1\"><code class=\"php spaces\">                </code><code class=\"php variable\">$temp</code> <code class=\"php plain\">= </code><code class=\"php variable\">$numbers</code><code class=\"php plain\">[</code><code class=\"php variable\">$j</code><code class=\"php plain\">];</code></div>\r\n<div class=\"line number7 index6 alt2\"><code class=\"php spaces\">                </code><code class=\"php variable\">$numbers</code><code class=\"php plain\">[</code><code class=\"php variable\">$j</code><code class=\"php plain\">] = </code><code class=\"php variable\">$numbers</code><code class=\"php plain\">[</code><code class=\"php variable\">$j</code> <code class=\"php plain\">+ 1];</code></div>\r\n<div class=\"line number8 index7 alt1\"><code class=\"php spaces\">                </code><code class=\"php variable\">$numbers</code><code class=\"php plain\">[</code><code class=\"php variable\">$j</code> <code class=\"php plain\">+ 1] = </code><code class=\"php variable\">$temp</code><code class=\"php plain\">;</code></div>\r\n<div class=\"line number9 index8 alt2\"><code class=\"php spaces\">            </code><code class=\"php plain\">}</code></div>\r\n<div class=\"line number10 index9 alt1\"><code class=\"php spaces\">        </code><code class=\"php plain\">}</code></div>\r\n<div class=\"line number11 index10 alt2\"><code class=\"php spaces\">    </code><code class=\"php plain\">}</code></div>\r\n<div class=\"line number12 index11 alt1\"></div>\r\n<div class=\"line number13 index12 alt2\"><code class=\"php spaces\">    </code><code class=\"php keyword\">return</code> <code class=\"php variable\">$numbers</code><code class=\"php plain\">;</code></div>\r\n<div class=\"line number14 index13 alt1\"><code class=\"php plain\">}</code></div>\r\n<div class=\"line number15 index14 alt2\"></div>\r\n<div class=\"line number16 index15 alt1\"><code class=\"php variable\">$num</code> <code class=\"php plain\">= </code><code class=\"php keyword\">array</code><code class=\"php plain\">(20, 40, 60, 80, 30, 70, 90, 10, 50, 0);</code></div>\r\n<div class=\"line number17 index16 alt2\"><code class=\"php plain\">var_dump(bubbleSort(</code><code class=\"php variable\">$num</code><code class=\"php plain\">));</code></div>\r\n<div class=\"line number18 index17 alt1\"></div>\r\n<div class=\"line number19 index18 alt2\"><code class=\"php comments\">//输出结果如下：</code></div>\r\n<div class=\"line number20 index19 alt1\"><code class=\"php comments\">//array(10) {</code></div>\r\n<div class=\"line number21 index20 alt2\"><code class=\"php comments\">//  [0]=&gt;</code></div>\r\n<div class=\"line number22 index21 alt1\"><code class=\"php comments\">//  int(0)</code></div>\r\n<div class=\"line number23 index22 alt2\"><code class=\"php comments\">//  [1]=&gt;</code></div>\r\n<div class=\"line number24 index23 alt1\"><code class=\"php comments\">//  int(10)</code></div>\r\n<div class=\"line number25 index24 alt2\"><code class=\"php comments\">//  [2]=&gt;</code></div>\r\n<div class=\"line number26 index25 alt1\"><code class=\"php comments\">//  int(20)</code></div>\r\n<div class=\"line number27 index26 alt2\"><code class=\"php comments\">//  [3]=&gt;</code></div>\r\n<div class=\"line number28 index27 alt1\"><code class=\"php comments\">//  int(30)</code></div>\r\n<div class=\"line number29 index28 alt2\"><code class=\"php comments\">//  [4]=&gt;</code></div>\r\n<div class=\"line number30 index29 alt1\"><code class=\"php comments\">//  int(40)</code></div>\r\n<div class=\"line number31 index30 alt2\"><code class=\"php comments\">//  [5]=&gt;</code></div>\r\n<div class=\"line number32 index31 alt1\"><code class=\"php comments\">//  int(50)</code></div>\r\n<div class=\"line number33 index32 alt2\"><code class=\"php comments\">//  [6]=&gt;</code></div>\r\n<div class=\"line number34 index33 alt1\"><code class=\"php comments\">//  int(60)</code></div>\r\n<div class=\"line number35 index34 alt2\"><code class=\"php comments\">//  [7]=&gt;</code></div>\r\n<div class=\"line number36 index35 alt1\"><code class=\"php comments\">//  int(70)</code></div>\r\n<div class=\"line number37 index36 alt2\"><code class=\"php comments\">//  [8]=&gt;</code></div>\r\n<div class=\"line number38 index37 alt1\"><code class=\"php comments\">//  int(80)</code></div>\r\n<div class=\"line number39 index38 alt2\"><code class=\"php comments\">//  [9]=&gt;</code></div>\r\n<div class=\"line number40 index39 alt1\"><code class=\"php comments\">//  int(90)</code></div>\r\n<div class=\"line number41 index40 alt2\"><code class=\"php comments\">//}</code></div>', 'php 冒泡排序', '', 'inherit', 'closed', 'closed', '', '53-revision-v1', '', '', '2016-12-06 14:56:14', '2016-12-06 06:56:14', '', '53', 'http://luchanglong.com.cn/?p=54', '0', 'revision', '', '0');
