INSERT INTO `wp_posts` VALUES ('95', '1', '2016-12-20 23:24:35', '2016-12-20 15:24:35', '用户操作\r\nw 列出在线用户\r\ncut -d : -f 1 /etc/passwd 列出所有用户\r\ncat /etc/passwd | grep -v /sbin/nologin | cut -d : -f 1 列出可登陆用户\r\nuserdel ftpuser 删除用户\r\ncut -d : -f 1 /etc/group 列出用户组\r\ngroupdel peter 删除用户组', 'centos 管理用户、用户组命令', '', 'publish', 'open', 'open', '', 'centos-%e7%ae%a1%e7%90%86%e7%94%a8%e6%88%b7%e3%80%81%e7%94%a8%e6%88%b7%e7%bb%84%e5%91%bd%e4%bb%a4', '', '', '2016-12-20 23:37:55', '2016-12-20 15:37:55', '', '0', 'http://luchanglong.com.cn/?p=95', '0', 'post', '', '0');
