INSERT INTO `wp_posts` VALUES ('303', '1', '2017-04-09 01:31:29', '2017-04-08 17:31:29', 'https://getcomposer.org/download/\r\n\r\n命令行安装\r\n\r\n&nbsp;\r\n<pre class=\"installer\">php -r \"copy(\'https://getcomposer.org/installer\', \'composer-setup.php\');\"\r\nphp -r \"if (hash_file(\'SHA384\', \'composer-setup.php\') === \'669656bab3166a7aff8a7506b8cb2d1c292f042046c5a994c43155c0be6190fa0355160742ab2e1c88d40d5be660b410\') { echo \'Installer verified\'; } else { echo \'Installer corrupt\'; unlink(\'composer-setup.php\'); } echo PHP_EOL;\"\r\nphp composer-setup.php\r\nphp -r \"unlink(\'composer-setup.php\');\"</pre>\r\n出现错误\r\n\r\nFailed to decode zlib stream\r\n给composer-setup.php   添加everyone权限\r\n\r\n执行成功\r\n\r\n&nbsp;\r\n\r\n<img class=\"alignnone size-medium wp-image-306\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/04/111-900x358.png\" alt=\"\" width=\"900\" height=\"358\" />\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\ncomposer-setup安装\r\n\r\n1.设置apache和php打开ssl\r\n\r\n2.安装后将C:\\ProgramData\\ComposerSetup\\bin添加到系统环境变量\r\n\r\n&nbsp;\r\n\r\n&nbsp;', 'php 安装 composer', '', 'publish', 'open', 'open', '', 'php-%e5%ae%89%e8%a3%85-composer', '', '', '2017-04-09 02:14:30', '2017-04-08 18:14:30', '', '0', 'http://luchanglong.com.cn/?p=303', '0', 'post', '', '3');
INSERT INTO `wp_posts` VALUES ('304', '1', '2017-04-09 01:31:29', '2017-04-08 17:31:29', 'https://getcomposer.org/download/\r\n\r\n命令行安装\r\n<pre class=\"installer \">php -r \"copy(\'https://getcomposer.org/installer\', \'composer-setup.php\');\"\r\nphp -r \"if (hash_file(\'SHA384\', \'composer-setup.php\') === \'669656bab3166a7aff8a7506b8cb2d1c292f042046c5a994c43155c0be6190fa0355160742ab2e1c88d40d5be660b410\') { echo \'Installer verified\'; } else { echo \'Installer corrupt\'; unlink(\'composer-setup.php\'); } echo PHP_EOL;\"\r\nphp composer-setup.php\r\nphp -r \"unlink(\'composer-setup.php\');\"</pre>', 'php 安装 composer', '', 'inherit', 'closed', 'closed', '', '303-revision-v1', '', '', '2017-04-09 01:31:29', '2017-04-08 17:31:29', '', '303', 'http://luchanglong.com.cn/?p=304', '0', 'revision', '', '0');
INSERT INTO `wp_posts` VALUES ('305', '1', '2017-04-09 02:14:27', '2017-04-08 18:14:27', 'https://getcomposer.org/download/\n\n命令行安装\n\n&nbsp;\n<pre class=\"installer\">php -r \"copy(\'https://getcomposer.org/installer\', \'composer-setup.php\');\"\nphp -r \"if (hash_file(\'SHA384\', \'composer-setup.php\') === \'669656bab3166a7aff8a7506b8cb2d1c292f042046c5a994c43155c0be6190fa0355160742ab2e1c88d40d5be660b410\') { echo \'Installer verified\'; } else { echo \'Installer corrupt\'; unlink(\'composer-setup.php\'); } echo PHP_EOL;\"\nphp composer-setup.php\nphp -r \"unlink(\'composer-setup.php\');\"</pre>\n出现错误\n\nFailed to decode zlib stream\n给composer-setup.php   添加everyone权限\n\n执行成功\n\n&nbsp;\n\n<img class=\"alignnone size-medium wp-image-306\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/04/111-900x358.png\" alt=\"\" width=\"900\" height=\"358\" />\n\n&nbsp;\n\n&nbsp;\n\ncomposer-setup安装\n\n1.设置apache和php打开ssl\n\n2.安装后将C:\\ProgramData\\ComposerSetup\\bin添加到系统环境变量\n\n&nbsp;\n\n&nbsp;', 'php 安装 composer', '', 'inherit', 'closed', 'closed', '', '303-autosave-v1', '', '', '2017-04-09 02:14:27', '2017-04-08 18:14:27', '', '303', 'http://luchanglong.com.cn/?p=305', '0', 'revision', '', '0');
