INSERT INTO `wp_posts` VALUES ('312', '1', '2017-05-08 16:22:24', '2017-05-08 08:22:24', '报错\r\n\r\n# rm -rf yii\r\nrm: cannot remove ‘yii/.user.ini’: Operation not permitted\r\n\r\n解决方法：\r\n\r\n# rm -rf yii\r\n<span style=\"color: #ff0000;\">rm: cannot remove ‘yii/.user.ini’: Operation not permitted</span>\r\n\r\n# chattr -i /home/www/yii/.user.ini\r\n# rm -rf  yii', 'lnmp安装虚拟机 删除出错cannot remove ‘yii/.user.ini’: Operation not permitted', '', 'publish', 'open', 'open', '', 'lnmp%e5%ae%89%e8%a3%85%e8%99%9a%e6%8b%9f%e6%9c%ba-%e5%88%a0%e9%99%a4%e5%87%ba%e9%94%99cannot-remove-yii-user-ini-operation-not-permitted', '', '', '2017-05-08 16:22:24', '2017-05-08 08:22:24', '', '0', 'http://luchanglong.com.cn/?p=312', '0', 'post', '', '0');
