INSERT INTO `wp_posts` VALUES ('337', '1', '2017-05-24 11:52:45', '2017-05-24 03:52:45', '1.查询所有表\r\n\r\nselect table_name FROM information_schema.tables;\r\n\r\n2.查询指定前缀的表\r\n\r\nselect table_name FROM information_schema.tables where table_name like \'go_%\';\r\n\r\n3.生成从指定位置开始替换表名的sql语句（\"3\"表示从\"go_\"后面开始）\r\n\r\nSelect CONCAT( \'ALTER TABLE \', table_name, \' RENAME TO xm_\', substring(table_name,3),\';\' )FROM information_schema.tables Where table_name LIKE \'go_%\';\r\n\r\n&nbsp;\r\n\r\n将生成的重命名语句语句复制出来，拿去执行，OK', 'myql 批量生成替换表前缀sql语句', '', 'publish', 'open', 'open', '', 'myql-%e6%89%b9%e9%87%8f%e7%94%9f%e6%88%90%e6%9b%bf%e6%8d%a2%e8%a1%a8%e5%89%8d%e7%bc%80sql%e8%af%ad%e5%8f%a5', '', '', '2017-05-24 13:09:53', '2017-05-24 05:09:53', '', '0', 'http://luchanglong.com.cn/?p=337', '0', 'post', '', '0');
