INSERT INTO `wp_posts` VALUES ('352', '1', '2017-06-30 11:19:24', '2017-06-30 03:19:24', '<pre class=\"lang:php decode:true \">foreach ($list as &amp;$val){\r\n    if ($val-&gt;mobile){\r\n       $val-&gt;mobile=substr_replace($val-&gt;mobile,\'****\',3,4);//手机号码直接替换\r\n    }\r\n    if ($val-&gt;nickname){\r\n        $val-&gt;nickname=str_replace(mb_substr($val-&gt;nickname,1,1),\'*\',$val-&gt;nickname); //中文名 先截取需要替换的位置字符，再查找字符替换\r\n    }\r\n}</pre>', 'php substr_replace 替换手机号码中的指定位置，mb_substr替换姓名（中文）中的指定位置', '', 'publish', 'open', 'open', '', 'php-substr_replace-%e6%9b%bf%e6%8d%a2%e6%89%8b%e6%9c%ba%e5%8f%b7%e7%a0%81%e4%b8%ad%e7%9a%84%e6%8c%87%e5%ae%9a%e4%bd%8d%e7%bd%ae%ef%bc%8cmb_substr%e6%9b%bf%e6%8d%a2%e5%a7%93%e5%90%8d%ef%bc%88%e4%b8%ad', '', '', '2017-06-30 11:20:19', '2017-06-30 03:20:19', '', '0', 'http://luchanglong.com.cn/?p=352', '0', 'post', '', '0');
