INSERT INTO `wp_posts` VALUES ('500', '1', '2018-03-27 14:37:02', '2018-03-27 06:37:02', '<img class=\"alignnone size-full wp-image-501\" src=\"http://luchanglong.com.cn/wp-content/uploads/2018/03/QQ截图20180327143542.png\" alt=\"\" width=\"715\" height=\"137\" />\r\n\r\n&nbsp;\r\n<pre class=\"lang:php decode:true \">//压缩文件\r\n$zipper=new Zipper();\r\n$zipper-&gt;make($dir.\'.zip\')-&gt;add($dir);\r\n$zipper-&gt;close();\r\nreturn response()-&gt;download(public_path($dir.\'.zip\'));</pre>\r\n&nbsp;', 'laravel5使用 chumper/zipper 打包压缩文件夹并下载', '', 'publish', 'open', 'open', '', 'laravel5%e4%bd%bf%e7%94%a8-chumperzipper-%e6%89%93%e5%8c%85%e5%8e%8b%e7%bc%a9%e6%96%87%e4%bb%b6%e5%a4%b9%e5%b9%b6%e4%b8%8b%e8%bd%bd', '', '', '2018-03-27 15:22:37', '2018-03-27 07:22:37', '', '0', 'http://luchanglong.com.cn/?p=500', '0', 'post', '', '0');
