INSERT INTO `wp_posts` VALUES ('502', '1', '2018-03-27 14:37:02', '2018-03-27 06:37:02', '<img class=\"alignnone size-full wp-image-501\" src=\"http://luchanglong.com.cn/wp-content/uploads/2018/03/QQ截图20180327143542.png\" alt=\"\" width=\"715\" height=\"137\" />\r\n\r\n&nbsp;\r\n<pre class=\"\">//压缩文件\r\n$zipper=new Zipper();\r\n$zipper-&gt;make($dir.\'.zip\')-&gt;add($dir);\r\n$zipper-&gt;close();\r\nreturn response()-&gt;download(public_path($dir.\'.zip\'));\r\n</pre>', 'laravel5使用 chumper/zipper 打包压缩文件夹并下载', '', 'inherit', 'closed', 'closed', '', '500-revision-v1', '', '', '2018-03-27 14:37:02', '2018-03-27 06:37:02', '', '500', 'http://luchanglong.com.cn/?p=502', '0', 'revision', '', '0');
INSERT INTO `wp_posts` VALUES ('503', '1', '2018-03-27 15:22:37', '2018-03-27 07:22:37', '<img class=\"alignnone size-full wp-image-501\" src=\"http://luchanglong.com.cn/wp-content/uploads/2018/03/QQ截图20180327143542.png\" alt=\"\" width=\"715\" height=\"137\" />\r\n\r\n&nbsp;\r\n<pre class=\"lang:php decode:true \">//压缩文件\r\n$zipper=new Zipper();\r\n$zipper-&gt;make($dir.\'.zip\')-&gt;add($dir);\r\n$zipper-&gt;close();\r\nreturn response()-&gt;download(public_path($dir.\'.zip\'));</pre>\r\n&nbsp;', 'laravel5使用 chumper/zipper 打包压缩文件夹并下载', '', 'inherit', 'closed', 'closed', '', '500-revision-v1', '', '', '2018-03-27 15:22:37', '2018-03-27 07:22:37', '', '500', 'http://luchanglong.com.cn/?p=503', '0', 'revision', '', '0');
