INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (2, 1, '2016-06-07 22:49:02', '2016-11-24 05:48:02', 'adminLTE 2.3.7 汉化中文版  已将所有js、css做本地化处理  <a href=\"http://adminlte.la998.com/\">http://adminlte.la998.com/</a>      <a href=\"http://adminlte.la998.com/adminlte2.3.7.zip\">点击下载</a>\r\n\r\n&nbsp;', 'adminLTE', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2016-12-22 16:27:00', '2016-12-22 08:27:00', '', 0, 'http://luchanglong.com.cn/?page_id=2', 0, 'page', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (4, 1, '2016-10-09 13:50:40', '2016-11-24 05:50:40', '', '1460398', '', 'inherit', 'open', 'closed', '', '1460398', '', '', '2016-11-24 13:50:40', '2016-11-24 05:50:40', '', 0, 'http://luchanglong.com.cn/wp-content/uploads/2016/11/1460398.jpg', 0, 'attachment', 'image/jpeg', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (6, 1, '2016-10-13 13:59:18', '2016-10-13 05:59:18', '安装lnmp1.3完成后使用SELECT @@sql_MODE 查询sql_mode得出的结果为空，因为程序需要\r\n把sql_mode 设置为sql_mode = \'NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION\'，\r\n因此在sql中执行\r\n<pre class=\"lang:mysql decode:true \">SET sql_mode=`NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION`;\r\nSET global sql_mode = \'NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION\';</pre>\r\n&nbsp;\r\n\r\n再执行\r\nSELECT @@sql_MODE;\r\n\r\n得出结果为NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION ，大喜，以为成功了，遂重启sql，再试一下SELECT @@sql_MODE ，得出的结果为空，悲，不生效！！！！\r\n不服\r\n在  /etc/my.cnf  最后添加一行\r\nsql_mode=\"NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION\"\r\n\r\n重启sql，SELECT @@sql_MODE 出来的结果还是空，不生效！！\r\n\r\n后来将sql_mode=\"NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION\" 放到my.cnf [mysqld]中,才解决了问题！！！\r\n\r\n记录下来，让以后不再踩坑！', '关于设置mysql_mode的一些问题', '', 'publish', 'open', 'open', '', '%e5%85%b3%e4%ba%8e%e8%ae%be%e7%bd%aemysql_mode%e7%9a%84%e4%b8%80%e4%ba%9b%e9%97%ae%e9%a2%98', '', '', '2017-01-17 14:37:55', '2017-01-17 06:37:55', '', 0, 'http://luchanglong.com.cn/?p=6', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (7, 1, '2016-10-15 20:59:18', '2016-11-24 05:59:18', '安装lnmp1.3完成后使用SELECT @@sql_MODE 查询sql_mode得出的结果为空，因为程序需要\r\n把sql_mode 设置为sql_mode = \'NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION\'，\r\n因此在sql中执行\r\nSET sql_mode=`NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION`;\r\nSET global sql_mode = \'NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION\';\r\n\r\n再执行\r\nSELECT @@sql_MODE;\r\n\r\n\r\n得出结果为NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION ，大喜，以为成功了，遂重启sql，再试一下SELECT @@sql_MODE ，得出的结果为空，悲，不生效！！！！\r\n不服\r\n在  /etc/my.cnf  最后添加一行\r\nsql_mode=\"NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION\"\r\n\r\n重启sql，SELECT @@sql_MODE 出来的结果还是空，不生效！！\r\n\r\n后来将sql_mode=\"NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION\" 放到my.cnf [mysqld]中,才解决了问题！！！\r\n\r\n记录下来，让以后不再踩坑！', '关于设置mysql_mode的一些问题', '', 'inherit', 'closed', 'closed', '', '6-revision-v1', '', '', '2016-11-24 13:59:18', '2016-11-24 05:59:18', '', 6, 'http://luchanglong.com.cn/?p=7', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (8, 1, '2016-10-17 14:01:02', '2016-11-24 06:01:02', 'Vi打开~/.bashrc，在末尾添加如下内容：\r\n\r\nexport PATH=/usr/local/php/bin:$PATH\r\nexport PATH=/usr/local/php/sbin:$PATH\r\n保存后，终端输入命令：\r\n\r\nsource ~/.bashrc\r\n此时即可通过php --version查看php版本，看到如下内容：\r\n\r\nPHP 5.5.10 (cli) (built: Apr 26 2014 09:46:14)\r\nCopyright (c) 1997-2014 The PHP Group\r\nZend Engine v2.5.0, Copyright (c) 1998-2014 Zend Technologies', 'php 编译安装后设置环境变量可以使用php -v -m等命令', '', 'publish', 'open', 'open', '', 'php-%e7%bc%96%e8%af%91%e5%ae%89%e8%a3%85%e5%90%8e%e8%ae%be%e7%bd%ae%e7%8e%af%e5%a2%83%e5%8f%98%e9%87%8f%e5%8f%af%e4%bb%a5%e4%bd%bf%e7%94%a8php-v-m%e7%ad%89%e5%91%bd%e4%bb%a4', '', '', '2016-11-24 14:02:12', '2016-11-24 06:02:12', '', 0, 'http://luchanglong.com.cn/?p=8', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (9, 1, '2016-10-18 11:01:02', '2016-11-24 06:01:02', 'Vi打开~/.bashrc，在末尾添加如下内容：\r\n\r\nexport PATH=/usr/local/php/bin:$PATH\r\nexport PATH=/usr/local/php/sbin:$PATH\r\n保存后，终端输入命令：\r\n\r\nsource ~/.bashrc\r\n此时即可通过php --version查看php版本，看到如下内容：\r\n\r\nPHP 5.5.10 (cli) (built: Apr 26 2014 09:46:14)\r\nCopyright (c) 1997-2014 The PHP Group\r\nZend Engine v2.5.0, Copyright (c) 1998-2014 Zend Technologies', 'php 编译安装后设置环境变量可以使用php -v -m等命令', '', 'inherit', 'closed', 'closed', '', '8-revision-v1', '', '', '2016-11-24 14:01:02', '2016-11-24 06:01:02', '', 8, 'http://luchanglong.com.cn/?p=9', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (10, 1, '2016-10-19 15:03:55', '2016-11-24 06:03:48', '<div>LNMP状态管理命令：</div>\r\n<div></div>\r\n<div>LNMP 1.2状态管理: lnmp {start|stop|reload|restart|kill|status}</div>\r\n<div>LNMP 1.2各个程序状态管理: lnmp {nginx|mysql|mariadb|php-fpm|pureftpd} {start|stop|reload|restart|kill|status}</div>\r\n<div>LNMP 1.1状态管理： /root/lnmp {start|stop|reload|restart|kill|status}</div>\r\n<div>Nginx状态管理：/etc/init.d/nginx {start|stop|reload|restart}</div>\r\n<div>MySQL状态管理：/etc/init.d/mysql {start|stop|restart|reload|force-reload|status}</div>\r\n<div>Memcached状态管理：/etc/init.d/memcached {start|stop|restart}</div>\r\n<div>PHP-FPM状态管理：/etc/init.d/php-fpm {start|stop|quit|restart|reload|logrotate}</div>\r\n<div>PureFTPd状态管理： /etc/init.d/pureftpd {start|stop|restart|kill|status}</div>\r\n<div>ProFTPd状态管理： /etc/init.d/proftpd {start|stop|restart|reload}</div>\r\n<div></div>\r\n<div>如重启LNMP，输入命令：/root/lnmp restart 即可，单独重启mysql：/etc/init.d/mysql restart</div>\r\n<div></div>\r\n<div>\r\n<h3><strong>##添加网站(虚拟主机)##</strong></h3>\r\n执行：<strong>lnmp vhost add</strong>\r\n<div>删除lamp创建虚拟机关联文件夹的命令</div>\r\n<div>[root@iZ94orvma5bZ www]# chattr -i thinkphp5/.user.ini</div>\r\n<div>[root@iZ94orvma5bZ www]# rm -rf thinkphp5/</div>\r\n</div>', 'lnmp的一些常用命令', '', 'publish', 'open', 'open', '', 'lnmp%e7%9a%84%e4%b8%80%e4%ba%9b%e5%b8%b8%e7%94%a8%e5%91%bd%e4%bb%a4', '', '', '2016-11-24 14:03:48', '2016-11-24 06:03:48', '', 0, 'http://luchanglong.com.cn/?p=10', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (11, 1, '2016-10-20 23:03:48', '2016-11-24 06:03:48', '<div>LNMP状态管理命令：</div>\r\n<div></div>\r\n<div>LNMP 1.2状态管理: lnmp {start|stop|reload|restart|kill|status}</div>\r\n<div>LNMP 1.2各个程序状态管理: lnmp {nginx|mysql|mariadb|php-fpm|pureftpd} {start|stop|reload|restart|kill|status}</div>\r\n<div>LNMP 1.1状态管理： /root/lnmp {start|stop|reload|restart|kill|status}</div>\r\n<div>Nginx状态管理：/etc/init.d/nginx {start|stop|reload|restart}</div>\r\n<div>MySQL状态管理：/etc/init.d/mysql {start|stop|restart|reload|force-reload|status}</div>\r\n<div>Memcached状态管理：/etc/init.d/memcached {start|stop|restart}</div>\r\n<div>PHP-FPM状态管理：/etc/init.d/php-fpm {start|stop|quit|restart|reload|logrotate}</div>\r\n<div>PureFTPd状态管理： /etc/init.d/pureftpd {start|stop|restart|kill|status}</div>\r\n<div>ProFTPd状态管理： /etc/init.d/proftpd {start|stop|restart|reload}</div>\r\n<div></div>\r\n<div>如重启LNMP，输入命令：/root/lnmp restart 即可，单独重启mysql：/etc/init.d/mysql restart</div>\r\n<div></div>\r\n<div>\r\n<h3><strong>##添加网站(虚拟主机)##</strong></h3>\r\n执行：<strong>lnmp vhost add</strong>\r\n<div>删除lamp创建虚拟机关联文件夹的命令</div>\r\n<div>[root@iZ94orvma5bZ www]# chattr -i thinkphp5/.user.ini</div>\r\n<div>[root@iZ94orvma5bZ www]# rm -rf thinkphp5/</div>\r\n</div>', 'lnmp的一些常用命令', '', 'inherit', 'closed', 'closed', '', '10-revision-v1', '', '', '2016-11-24 14:03:48', '2016-11-24 06:03:48', '', 10, 'http://luchanglong.com.cn/?p=11', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (12, 1, '2016-10-21 14:07:42', '2016-11-24 06:07:42', 'PSR 是 PHP Standard Recommendations 的简写，由 <a href=\"https://github.com/php-fig\" target=\"_blank\">PHP FIG</a> 组织制定的 PHP 规范，是 PHP 开发的实践标准。\r\n\r\n<a href=\"https://github.com/php-fig\" target=\"_blank\">PHP FIG</a>，FIG 是 Framework Interoperability Group（框架可互用性小组）的缩写，由几位开源框架的开发者成立于 2009 年，从那开始也选取了很多其他成员进来（包括但不限于 <a href=\"http://laravel.com/\" target=\"_blank\">Laravel</a>, <a href=\"https://www.joomla.org/\" target=\"_blank\">Joomla</a>, <a href=\"https://www.drupal.org/\" target=\"_blank\">Drupal</a>, <a href=\"https://getcomposer.org/\" target=\"_blank\">Composer</a>, <a href=\"https://phalconphp.com/en/\" target=\"_blank\">Phalcon</a>, <a href=\"http://www.slimframework.com/\" target=\"_blank\">Slim</a>, <a href=\"http://symfony.com/\" target=\"_blank\">Symfony</a>, <a href=\"http://framework.zend.com/\" target=\"_blank\">Zend Framework</a> 等），虽然不是「官方」组织，但也代表了大部分的 PHP 社区。\r\n\r\n项目的目的在于：通过框架作者或者框架的代表之间讨论，以最低程度的限制，制定一个协作标准，各个框架遵循统一的编码规范，避免各家自行发展的风格阻碍了 PHP 的发展，解决这个程序设计师由来已久的困扰。\r\n\r\n目前已表决通过了 6 套标准，已经得到大部分 PHP 框架的支持和认可。\r\n\r\n本项目的主要面向对象是所有参与的各个成员（也就是各自框架的社区），这里是完整的 <a href=\"http://www.php-fig.org/members/\" target=\"_blank\">成员列表</a>，当然，同时也欢迎其它 PHP 社区采用本规范。\r\n\r\n此中文翻译由 <a href=\"https://github.com/summerblue\" target=\"_blank\">@Summer</a> 维护，主要针对「已通过」的 PSR 进行翻译，排版遵照 <a href=\"https://github.com/sparanoid/chinese-copywriting-guidelines\" target=\"_blank\">中文文案排版指北</a>，更多讨论请前往 <a href=\"https://phphub.org/\" target=\"_blank\">PHPHub</a>。\r\n<h2 id=\"toc_1\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_1\">按表决情况索引</a></h2>\r\n<h3 id=\"toc_2\" class=\"table_title\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_2\">已通过</a></h3>\r\n<div class=\"responsive-table\">\r\n<table>\r\n<thead>\r\n<tr>\r\n<th align=\"center\">序号</th>\r\n<th>标题</th>\r\n<th>撰稿者</th>\r\n<th>协调者</th>\r\n<th>发起人</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td align=\"center\">1</td>\r\n<td><a href=\"https://phphub.org/topics/2078\">基础编码规范</a></td>\r\n<td>Paul M. Jones</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">2</td>\r\n<td><a href=\"https://phphub.org/topics/2079\">编码风格规范</a></td>\r\n<td>Paul M. Jones</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">3</td>\r\n<td><a href=\"https://phphub.org/topics/2080\">日志接口规范</a></td>\r\n<td>Jordi Boggiano</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">4</td>\r\n<td><a href=\"https://phphub.org/topics/2081\">自动加载规范</a></td>\r\n<td>Paul M. Jones</td>\r\n<td>Phil Sturgeon</td>\r\n<td>Larry Garfield</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">6</td>\r\n<td><a href=\"https://phphub.org/topics/2082\">缓存接口规范</a></td>\r\n<td>Larry Garfield</td>\r\n<td>Paul Dragoonis</td>\r\n<td>Robert Hafner</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">7</td>\r\n<td><a href=\"https://github.com/summerblue/psr.phphub.org/blob/master/psrs/%E3%80%8CPSR%20%E8%A7%84%E8%8C%83%E3%80%8DPSR-7%20HTTP%20%E6%B6%88%E6%81%AF%E6%8E%A5%E5%8F%A3%E8%A7%84%E8%8C%83.md\">HTTP 消息接口规范</a></td>\r\n<td>Matthew Weier O\'Phinney</td>\r\n<td>Beau Simensen</td>\r\n<td>Paul M. Jones</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<h3 id=\"toc_3\" class=\"table_title\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_3\">审查中</a></h3>\r\n<div class=\"responsive-table\">\r\n<table>\r\n<thead>\r\n<tr>\r\n<th align=\"center\">序号</th>\r\n<th>标题</th>\r\n<th>撰稿者</th>\r\n<th>协调者</th>\r\n<th>发起人</th>\r\n</tr>\r\n</thead>\r\n</table>\r\n</div>\r\n<h3 id=\"toc_4\" class=\"table_title\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_4\">起草中</a></h3>\r\n<div class=\"responsive-table\">\r\n<table>\r\n<thead>\r\n<tr>\r\n<th align=\"center\">序号</th>\r\n<th>标题</th>\r\n<th>撰稿者</th>\r\n<th>协调者</th>\r\n<th>发起人</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td align=\"center\">5</td>\r\n<td><a href=\"https://github.com/phpDocumentor/fig-standards/tree/master/proposed\">PHPDoc 标准</a></td>\r\n<td>Mike van Riel</td>\r\n<td>Vacant</td>\r\n<td>Vacant</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">8</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/psr-8-hug/psr-8-hug.md\">Huggable 接口</a></td>\r\n<td>Larry Garfield</td>\r\n<td>Vacant</td>\r\n<td>Paul M. Jones</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">9</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/security-disclosure-publication.md\">项目安全问题公示</a></td>\r\n<td>Lukas Kahwe Smith</td>\r\n<td>Korvin Szanto</td>\r\n<td>Larry Garfield</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">10</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/security-reporting-process.md\">项目安全上报方法</a></td>\r\n<td>Lukas Kahwe Smith</td>\r\n<td>Larry Garfield</td>\r\n<td>Korvin Szanto</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">11</td>\r\n<td><a href=\"https://github.com/container-interop/fig-standards/blob/master/proposed/container.md\">服务容器接口</a></td>\r\n<td>Matthieu Napoli, David Négrier</td>\r\n<td>Paul M. Jones</td>\r\n<td>Vacant</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">12</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/extended-coding-style-guide.md\">全量编码风格规范</a></td>\r\n<td>Korvin Szanto</td>\r\n<td>Alexander Makarov</td>\r\n<td>Robert Deutz</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">13</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/links.md\">超媒体链接</a></td>\r\n<td>Larry Garfield</td>\r\n<td>Matthew Weier O\'Phinney</td>\r\n<td>Evert Pot</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<h3 id=\"toc_5\" class=\"table_title\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_5\">已废弃</a></h3>\r\n<div class=\"responsive-table\">\r\n<table>\r\n<thead>\r\n<tr>\r\n<th align=\"center\">序号</th>\r\n<th>标题</th>\r\n<th>撰稿者</th>\r\n<th>协调者</th>\r\n<th>发起人</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td align=\"center\">0</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/accepted/PSR-0.md\">自动加载规范</a></td>\r\n<td>Matthew Weier O\'Phinney</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<h2 id=\"toc_6\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_6\">按照数字索引</a></h2>\r\n<div class=\"responsive-table\">\r\n<table>\r\n<thead>\r\n<tr>\r\n<th>状态</th>\r\n<th align=\"center\">序号</th>\r\n<th>标题</th>\r\n<th>撰稿者</th>\r\n<th>协调者</th>\r\n<th>发起人</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td>X</td>\r\n<td align=\"center\">0</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/accepted/PSR-0.md\">自动加载规范</a></td>\r\n<td>Matthew Weier O\'Phinney</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td>A</td>\r\n<td align=\"center\">1</td>\r\n<td><a href=\"https://phphub.org/topics/2078\">基础编码规范</a></td>\r\n<td>Paul M. Jones</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td>A</td>\r\n<td align=\"center\">2</td>\r\n<td><a href=\"https://phphub.org/topics/2079\">编码风格规范</a></td>\r\n<td>Paul M. Jones</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td>A</td>\r\n<td align=\"center\">3</td>\r\n<td><a href=\"https://phphub.org/topics/2080\">日志接口规范</a></td>\r\n<td>Jordi Boggiano</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td>A</td>\r\n<td align=\"center\">4</td>\r\n<td><a href=\"https://phphub.org/topics/2081\">自动加载规范</a></td>\r\n<td>Paul M. Jones</td>\r\n<td>Phil Sturgeon</td>\r\n<td>Larry Garfield</td>\r\n</tr>\r\n<tr>\r\n<td>D</td>\r\n<td align=\"center\">5</td>\r\n<td><a href=\"https://github.com/phpDocumentor/fig-standards/tree/master/proposed\">PHPDoc 标准</a></td>\r\n<td>Mike van Riel</td>\r\n<td>Vacant</td>\r\n<td>Vacant</td>\r\n</tr>\r\n<tr>\r\n<td>A</td>\r\n<td align=\"center\">6</td>\r\n<td><a href=\"https://phphub.org/topics/2082\">缓存接口规范</a></td>\r\n<td>Larry Garfield</td>\r\n<td>Paul Dragoonis</td>\r\n<td>Robert Hafner</td>\r\n</tr>\r\n<tr>\r\n<td>A</td>\r\n<td align=\"center\">7</td>\r\n<td><a href=\"https://github.com/summerblue/psr.phphub.org/blob/master/psrs/%E3%80%8CPSR%20%E8%A7%84%E8%8C%83%E3%80%8DPSR-7%20HTTP%20%E6%B6%88%E6%81%AF%E6%8E%A5%E5%8F%A3%E8%A7%84%E8%8C%83.md\">HTTP 消息接口规范</a></td>\r\n<td>Matthew Weier O\'Phinney</td>\r\n<td>Beau Simensen</td>\r\n<td>Paul M. Jones</td>\r\n</tr>\r\n<tr>\r\n<td>D</td>\r\n<td align=\"center\">8</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/psr-8-hug/psr-8-hug.md\">Huggable 接口</a></td>\r\n<td>Larry Garfield</td>\r\n<td>Vacant</td>\r\n<td>Paul M. Jones</td>\r\n</tr>\r\n<tr>\r\n<td>D</td>\r\n<td align=\"center\">9</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/security-disclosure-publication.md\">项目安全问题公示</a></td>\r\n<td>Lukas Kahwe Smith</td>\r\n<td>Korvin Szanto</td>\r\n<td>Larry Garfield</td>\r\n</tr>\r\n<tr>\r\n<td>D</td>\r\n<td align=\"center\">10</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/security-reporting-process.md\">项目安全上报方法</a></td>\r\n<td>Lukas Kahwe Smith</td>\r\n<td>Larry Garfield</td>\r\n<td>Korvin Szanto</td>\r\n</tr>\r\n<tr>\r\n<td>D</td>\r\n<td align=\"center\">11</td>\r\n<td><a href=\"https://github.com/container-interop/fig-standards/blob/master/proposed/container.md\">服务容器接口</a></td>\r\n<td>Matthieu Napoli, David Négrier</td>\r\n<td>Paul M. Jones</td>\r\n<td>Vacant</td>\r\n</tr>\r\n<tr>\r\n<td>D</td>\r\n<td align=\"center\">12</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/extended-coding-style-guide.md\">全量编码风格规范</a></td>\r\n<td>Korvin Szanto</td>\r\n<td>Alexander Makarov</td>\r\n<td>Robert Deutz</td>\r\n</tr>\r\n<tr>\r\n<td>D</td>\r\n<td align=\"center\">13</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/links.md\">超媒体链接</a></td>\r\n<td>Larry Garfield</td>\r\n<td>Matthew Weier O\'Phinney</td>\r\n<td>Evert Pot</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<p class=\"hint\"><strong>说明:</strong> A (Accepted) = 已通过 | D (Draft) = 起草中 | R (Review) = 审查中 | X (Deprecated) = 已废弃</p>', '转载：PHP标准规范 PHP-FIG', '', 'publish', 'open', 'open', '', '%e8%bd%ac%e8%bd%bdphp%e6%a0%87%e5%87%86%e8%a7%84%e8%8c%83-php-fig', '', '', '2016-11-24 14:07:50', '2016-11-24 06:07:50', '', 0, 'http://luchanglong.com.cn/?p=12', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (13, 1, '2016-10-22 20:00:03', '2016-11-24 06:07:42', 'PSR 是 PHP Standard Recommendations 的简写，由 <a href=\"https://github.com/php-fig\" target=\"_blank\">PHP FIG</a> 组织制定的 PHP 规范，是 PHP 开发的实践标准。\r\n\r\n<a href=\"https://github.com/php-fig\" target=\"_blank\">PHP FIG</a>，FIG 是 Framework Interoperability Group（框架可互用性小组）的缩写，由几位开源框架的开发者成立于 2009 年，从那开始也选取了很多其他成员进来（包括但不限于 <a href=\"http://laravel.com/\" target=\"_blank\">Laravel</a>, <a href=\"https://www.joomla.org/\" target=\"_blank\">Joomla</a>, <a href=\"https://www.drupal.org/\" target=\"_blank\">Drupal</a>, <a href=\"https://getcomposer.org/\" target=\"_blank\">Composer</a>, <a href=\"https://phalconphp.com/en/\" target=\"_blank\">Phalcon</a>, <a href=\"http://www.slimframework.com/\" target=\"_blank\">Slim</a>, <a href=\"http://symfony.com/\" target=\"_blank\">Symfony</a>, <a href=\"http://framework.zend.com/\" target=\"_blank\">Zend Framework</a> 等），虽然不是「官方」组织，但也代表了大部分的 PHP 社区。\r\n\r\n项目的目的在于：通过框架作者或者框架的代表之间讨论，以最低程度的限制，制定一个协作标准，各个框架遵循统一的编码规范，避免各家自行发展的风格阻碍了 PHP 的发展，解决这个程序设计师由来已久的困扰。\r\n\r\n目前已表决通过了 6 套标准，已经得到大部分 PHP 框架的支持和认可。\r\n\r\n本项目的主要面向对象是所有参与的各个成员（也就是各自框架的社区），这里是完整的 <a href=\"http://www.php-fig.org/members/\" target=\"_blank\">成员列表</a>，当然，同时也欢迎其它 PHP 社区采用本规范。\r\n\r\n此中文翻译由 <a href=\"https://github.com/summerblue\" target=\"_blank\">@Summer</a> 维护，主要针对「已通过」的 PSR 进行翻译，排版遵照 <a href=\"https://github.com/sparanoid/chinese-copywriting-guidelines\" target=\"_blank\">中文文案排版指北</a>，更多讨论请前往 <a href=\"https://phphub.org/\" target=\"_blank\">PHPHub</a>。\r\n<h2 id=\"toc_1\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_1\">按表决情况索引</a></h2>\r\n<h3 id=\"toc_2\" class=\"table_title\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_2\">已通过</a></h3>\r\n<div class=\"responsive-table\">\r\n<table>\r\n<thead>\r\n<tr>\r\n<th align=\"center\">序号</th>\r\n<th>标题</th>\r\n<th>撰稿者</th>\r\n<th>协调者</th>\r\n<th>发起人</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td align=\"center\">1</td>\r\n<td><a href=\"https://phphub.org/topics/2078\">基础编码规范</a></td>\r\n<td>Paul M. Jones</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">2</td>\r\n<td><a href=\"https://phphub.org/topics/2079\">编码风格规范</a></td>\r\n<td>Paul M. Jones</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">3</td>\r\n<td><a href=\"https://phphub.org/topics/2080\">日志接口规范</a></td>\r\n<td>Jordi Boggiano</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">4</td>\r\n<td><a href=\"https://phphub.org/topics/2081\">自动加载规范</a></td>\r\n<td>Paul M. Jones</td>\r\n<td>Phil Sturgeon</td>\r\n<td>Larry Garfield</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">6</td>\r\n<td><a href=\"https://phphub.org/topics/2082\">缓存接口规范</a></td>\r\n<td>Larry Garfield</td>\r\n<td>Paul Dragoonis</td>\r\n<td>Robert Hafner</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">7</td>\r\n<td><a href=\"https://github.com/summerblue/psr.phphub.org/blob/master/psrs/%E3%80%8CPSR%20%E8%A7%84%E8%8C%83%E3%80%8DPSR-7%20HTTP%20%E6%B6%88%E6%81%AF%E6%8E%A5%E5%8F%A3%E8%A7%84%E8%8C%83.md\">HTTP 消息接口规范</a></td>\r\n<td>Matthew Weier O\'Phinney</td>\r\n<td>Beau Simensen</td>\r\n<td>Paul M. Jones</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<h3 id=\"toc_3\" class=\"table_title\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_3\">审查中</a></h3>\r\n<div class=\"responsive-table\">\r\n<table>\r\n<thead>\r\n<tr>\r\n<th align=\"center\">序号</th>\r\n<th>标题</th>\r\n<th>撰稿者</th>\r\n<th>协调者</th>\r\n<th>发起人</th>\r\n</tr>\r\n</thead>\r\n</table>\r\n</div>\r\n<h3 id=\"toc_4\" class=\"table_title\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_4\">起草中</a></h3>\r\n<div class=\"responsive-table\">\r\n<table>\r\n<thead>\r\n<tr>\r\n<th align=\"center\">序号</th>\r\n<th>标题</th>\r\n<th>撰稿者</th>\r\n<th>协调者</th>\r\n<th>发起人</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td align=\"center\">5</td>\r\n<td><a href=\"https://github.com/phpDocumentor/fig-standards/tree/master/proposed\">PHPDoc 标准</a></td>\r\n<td>Mike van Riel</td>\r\n<td>Vacant</td>\r\n<td>Vacant</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">8</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/psr-8-hug/psr-8-hug.md\">Huggable 接口</a></td>\r\n<td>Larry Garfield</td>\r\n<td>Vacant</td>\r\n<td>Paul M. Jones</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">9</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/security-disclosure-publication.md\">项目安全问题公示</a></td>\r\n<td>Lukas Kahwe Smith</td>\r\n<td>Korvin Szanto</td>\r\n<td>Larry Garfield</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">10</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/security-reporting-process.md\">项目安全上报方法</a></td>\r\n<td>Lukas Kahwe Smith</td>\r\n<td>Larry Garfield</td>\r\n<td>Korvin Szanto</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">11</td>\r\n<td><a href=\"https://github.com/container-interop/fig-standards/blob/master/proposed/container.md\">服务容器接口</a></td>\r\n<td>Matthieu Napoli, David Négrier</td>\r\n<td>Paul M. Jones</td>\r\n<td>Vacant</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">12</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/extended-coding-style-guide.md\">全量编码风格规范</a></td>\r\n<td>Korvin Szanto</td>\r\n<td>Alexander Makarov</td>\r\n<td>Robert Deutz</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">13</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/links.md\">超媒体链接</a></td>\r\n<td>Larry Garfield</td>\r\n<td>Matthew Weier O\'Phinney</td>\r\n<td>Evert Pot</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<h3 id=\"toc_5\" class=\"table_title\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_5\">已废弃</a></h3>\r\n<div class=\"responsive-table\">\r\n<table>\r\n<thead>\r\n<tr>\r\n<th align=\"center\">序号</th>\r\n<th>标题</th>\r\n<th>撰稿者</th>\r\n<th>协调者</th>\r\n<th>发起人</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td align=\"center\">0</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/accepted/PSR-0.md\">自动加载规范</a></td>\r\n<td>Matthew Weier O\'Phinney</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<h2 id=\"toc_6\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_6\">按照数字索引</a></h2>\r\n<div class=\"responsive-table\">\r\n<table>\r\n<thead>\r\n<tr>\r\n<th>状态</th>\r\n<th align=\"center\">序号</th>\r\n<th>标题</th>\r\n<th>撰稿者</th>\r\n<th>协调者</th>\r\n<th>发起人</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td>X</td>\r\n<td align=\"center\">0</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/accepted/PSR-0.md\">自动加载规范</a></td>\r\n<td>Matthew Weier O\'Phinney</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td>A</td>\r\n<td align=\"center\">1</td>\r\n<td><a href=\"https://phphub.org/topics/2078\">基础编码规范</a></td>\r\n<td>Paul M. Jones</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td>A</td>\r\n<td align=\"center\">2</td>\r\n<td><a href=\"https://phphub.org/topics/2079\">编码风格规范</a></td>\r\n<td>Paul M. Jones</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td>A</td>\r\n<td align=\"center\">3</td>\r\n<td><a href=\"https://phphub.org/topics/2080\">日志接口规范</a></td>\r\n<td>Jordi Boggiano</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td>A</td>\r\n<td align=\"center\">4</td>\r\n<td><a href=\"https://phphub.org/topics/2081\">自动加载规范</a></td>\r\n<td>Paul M. Jones</td>\r\n<td>Phil Sturgeon</td>\r\n<td>Larry Garfield</td>\r\n</tr>\r\n<tr>\r\n<td>D</td>\r\n<td align=\"center\">5</td>\r\n<td><a href=\"https://github.com/phpDocumentor/fig-standards/tree/master/proposed\">PHPDoc 标准</a></td>\r\n<td>Mike van Riel</td>\r\n<td>Vacant</td>\r\n<td>Vacant</td>\r\n</tr>\r\n<tr>\r\n<td>A</td>\r\n<td align=\"center\">6</td>\r\n<td><a href=\"https://phphub.org/topics/2082\">缓存接口规范</a></td>\r\n<td>Larry Garfield</td>\r\n<td>Paul Dragoonis</td>\r\n<td>Robert Hafner</td>\r\n</tr>\r\n<tr>\r\n<td>A</td>\r\n<td align=\"center\">7</td>\r\n<td><a href=\"https://github.com/summerblue/psr.phphub.org/blob/master/psrs/%E3%80%8CPSR%20%E8%A7%84%E8%8C%83%E3%80%8DPSR-7%20HTTP%20%E6%B6%88%E6%81%AF%E6%8E%A5%E5%8F%A3%E8%A7%84%E8%8C%83.md\">HTTP 消息接口规范</a></td>\r\n<td>Matthew Weier O\'Phinney</td>\r\n<td>Beau Simensen</td>\r\n<td>Paul M. Jones</td>\r\n</tr>\r\n<tr>\r\n<td>D</td>\r\n<td align=\"center\">8</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/psr-8-hug/psr-8-hug.md\">Huggable 接口</a></td>\r\n<td>Larry Garfield</td>\r\n<td>Vacant</td>\r\n<td>Paul M. Jones</td>\r\n</tr>\r\n<tr>\r\n<td>D</td>\r\n<td align=\"center\">9</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/security-disclosure-publication.md\">项目安全问题公示</a></td>\r\n<td>Lukas Kahwe Smith</td>\r\n<td>Korvin Szanto</td>\r\n<td>Larry Garfield</td>\r\n</tr>\r\n<tr>\r\n<td>D</td>\r\n<td align=\"center\">10</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/security-reporting-process.md\">项目安全上报方法</a></td>\r\n<td>Lukas Kahwe Smith</td>\r\n<td>Larry Garfield</td>\r\n<td>Korvin Szanto</td>\r\n</tr>\r\n<tr>\r\n<td>D</td>\r\n<td align=\"center\">11</td>\r\n<td><a href=\"https://github.com/container-interop/fig-standards/blob/master/proposed/container.md\">服务容器接口</a></td>\r\n<td>Matthieu Napoli, David Négrier</td>\r\n<td>Paul M. Jones</td>\r\n<td>Vacant</td>\r\n</tr>\r\n<tr>\r\n<td>D</td>\r\n<td align=\"center\">12</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/extended-coding-style-guide.md\">全量编码风格规范</a></td>\r\n<td>Korvin Szanto</td>\r\n<td>Alexander Makarov</td>\r\n<td>Robert Deutz</td>\r\n</tr>\r\n<tr>\r\n<td>D</td>\r\n<td align=\"center\">13</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/links.md\">超媒体链接</a></td>\r\n<td>Larry Garfield</td>\r\n<td>Matthew Weier O\'Phinney</td>\r\n<td>Evert Pot</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<p class=\"hint\"><strong>说明:</strong> A (Accepted) = 已通过 | D (Draft) = 起草中 | R (Review) = 审查中 | X (Deprecated) = 已废弃</p>', '转载PHP标准规范 PHP-FIG', '', 'inherit', 'closed', 'closed', '', '12-revision-v1', '', '', '2016-11-24 14:07:42', '2016-11-24 06:07:42', '', 12, 'http://luchanglong.com.cn/?p=13', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (14, 1, '2016-10-26 21:07:50', '2016-11-24 06:07:50', 'PSR 是 PHP Standard Recommendations 的简写，由 <a href=\"https://github.com/php-fig\" target=\"_blank\">PHP FIG</a> 组织制定的 PHP 规范，是 PHP 开发的实践标准。\r\n\r\n<a href=\"https://github.com/php-fig\" target=\"_blank\">PHP FIG</a>，FIG 是 Framework Interoperability Group（框架可互用性小组）的缩写，由几位开源框架的开发者成立于 2009 年，从那开始也选取了很多其他成员进来（包括但不限于 <a href=\"http://laravel.com/\" target=\"_blank\">Laravel</a>, <a href=\"https://www.joomla.org/\" target=\"_blank\">Joomla</a>, <a href=\"https://www.drupal.org/\" target=\"_blank\">Drupal</a>, <a href=\"https://getcomposer.org/\" target=\"_blank\">Composer</a>, <a href=\"https://phalconphp.com/en/\" target=\"_blank\">Phalcon</a>, <a href=\"http://www.slimframework.com/\" target=\"_blank\">Slim</a>, <a href=\"http://symfony.com/\" target=\"_blank\">Symfony</a>, <a href=\"http://framework.zend.com/\" target=\"_blank\">Zend Framework</a> 等），虽然不是「官方」组织，但也代表了大部分的 PHP 社区。\r\n\r\n项目的目的在于：通过框架作者或者框架的代表之间讨论，以最低程度的限制，制定一个协作标准，各个框架遵循统一的编码规范，避免各家自行发展的风格阻碍了 PHP 的发展，解决这个程序设计师由来已久的困扰。\r\n\r\n目前已表决通过了 6 套标准，已经得到大部分 PHP 框架的支持和认可。\r\n\r\n本项目的主要面向对象是所有参与的各个成员（也就是各自框架的社区），这里是完整的 <a href=\"http://www.php-fig.org/members/\" target=\"_blank\">成员列表</a>，当然，同时也欢迎其它 PHP 社区采用本规范。\r\n\r\n此中文翻译由 <a href=\"https://github.com/summerblue\" target=\"_blank\">@Summer</a> 维护，主要针对「已通过」的 PSR 进行翻译，排版遵照 <a href=\"https://github.com/sparanoid/chinese-copywriting-guidelines\" target=\"_blank\">中文文案排版指北</a>，更多讨论请前往 <a href=\"https://phphub.org/\" target=\"_blank\">PHPHub</a>。\r\n<h2 id=\"toc_1\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_1\">按表决情况索引</a></h2>\r\n<h3 id=\"toc_2\" class=\"table_title\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_2\">已通过</a></h3>\r\n<div class=\"responsive-table\">\r\n<table>\r\n<thead>\r\n<tr>\r\n<th align=\"center\">序号</th>\r\n<th>标题</th>\r\n<th>撰稿者</th>\r\n<th>协调者</th>\r\n<th>发起人</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td align=\"center\">1</td>\r\n<td><a href=\"https://phphub.org/topics/2078\">基础编码规范</a></td>\r\n<td>Paul M. Jones</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">2</td>\r\n<td><a href=\"https://phphub.org/topics/2079\">编码风格规范</a></td>\r\n<td>Paul M. Jones</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">3</td>\r\n<td><a href=\"https://phphub.org/topics/2080\">日志接口规范</a></td>\r\n<td>Jordi Boggiano</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">4</td>\r\n<td><a href=\"https://phphub.org/topics/2081\">自动加载规范</a></td>\r\n<td>Paul M. Jones</td>\r\n<td>Phil Sturgeon</td>\r\n<td>Larry Garfield</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">6</td>\r\n<td><a href=\"https://phphub.org/topics/2082\">缓存接口规范</a></td>\r\n<td>Larry Garfield</td>\r\n<td>Paul Dragoonis</td>\r\n<td>Robert Hafner</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">7</td>\r\n<td><a href=\"https://github.com/summerblue/psr.phphub.org/blob/master/psrs/%E3%80%8CPSR%20%E8%A7%84%E8%8C%83%E3%80%8DPSR-7%20HTTP%20%E6%B6%88%E6%81%AF%E6%8E%A5%E5%8F%A3%E8%A7%84%E8%8C%83.md\">HTTP 消息接口规范</a></td>\r\n<td>Matthew Weier O\'Phinney</td>\r\n<td>Beau Simensen</td>\r\n<td>Paul M. Jones</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<h3 id=\"toc_3\" class=\"table_title\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_3\">审查中</a></h3>\r\n<div class=\"responsive-table\">\r\n<table>\r\n<thead>\r\n<tr>\r\n<th align=\"center\">序号</th>\r\n<th>标题</th>\r\n<th>撰稿者</th>\r\n<th>协调者</th>\r\n<th>发起人</th>\r\n</tr>\r\n</thead>\r\n</table>\r\n</div>\r\n<h3 id=\"toc_4\" class=\"table_title\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_4\">起草中</a></h3>\r\n<div class=\"responsive-table\">\r\n<table>\r\n<thead>\r\n<tr>\r\n<th align=\"center\">序号</th>\r\n<th>标题</th>\r\n<th>撰稿者</th>\r\n<th>协调者</th>\r\n<th>发起人</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td align=\"center\">5</td>\r\n<td><a href=\"https://github.com/phpDocumentor/fig-standards/tree/master/proposed\">PHPDoc 标准</a></td>\r\n<td>Mike van Riel</td>\r\n<td>Vacant</td>\r\n<td>Vacant</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">8</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/psr-8-hug/psr-8-hug.md\">Huggable 接口</a></td>\r\n<td>Larry Garfield</td>\r\n<td>Vacant</td>\r\n<td>Paul M. Jones</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">9</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/security-disclosure-publication.md\">项目安全问题公示</a></td>\r\n<td>Lukas Kahwe Smith</td>\r\n<td>Korvin Szanto</td>\r\n<td>Larry Garfield</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">10</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/security-reporting-process.md\">项目安全上报方法</a></td>\r\n<td>Lukas Kahwe Smith</td>\r\n<td>Larry Garfield</td>\r\n<td>Korvin Szanto</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">11</td>\r\n<td><a href=\"https://github.com/container-interop/fig-standards/blob/master/proposed/container.md\">服务容器接口</a></td>\r\n<td>Matthieu Napoli, David Négrier</td>\r\n<td>Paul M. Jones</td>\r\n<td>Vacant</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">12</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/extended-coding-style-guide.md\">全量编码风格规范</a></td>\r\n<td>Korvin Szanto</td>\r\n<td>Alexander Makarov</td>\r\n<td>Robert Deutz</td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">13</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/proposed/links.md\">超媒体链接</a></td>\r\n<td>Larry Garfield</td>\r\n<td>Matthew Weier O\'Phinney</td>\r\n<td>Evert Pot</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<h3 id=\"toc_5\" class=\"table_title\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_5\">已废弃</a></h3>\r\n<div class=\"responsive-table\">\r\n<table>\r\n<thead>\r\n<tr>\r\n<th align=\"center\">序号</th>\r\n<th>标题</th>\r\n<th>撰稿者</th>\r\n<th>协调者</th>\r\n<th>发起人</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td align=\"center\">0</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/accepted/PSR-0.md\">自动加载规范</a></td>\r\n<td>Matthew Weier O\'Phinney</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<h2 id=\"toc_6\"><a href=\"https://psr.phphub.org/?from=timeline&amp;isappinstalled=0#toc_6\">按照数字索引</a></h2>\r\n<div class=\"responsive-table\">\r\n<table>\r\n<thead>\r\n<tr>\r\n<th>状态</th>\r\n<th align=\"center\">序号</th>\r\n<th>标题</th>\r\n<th>撰稿者</th>\r\n<th>协调者</th>\r\n<th>发起人</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td>X</td>\r\n<td align=\"center\">0</td>\r\n<td><a href=\"https://github.com/php-fig/fig-standards/blob/master/accepted/PSR-0.md\">自动加载规范</a></td>\r\n<td>Matthew Weier O\'Phinney</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td>A</td>\r\n<td align=\"center\">1</td>\r\n<td><a href=\"https://phphub.org/topics/2078\">基础编码规范</a></td>\r\n<td>Paul M. Jones</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td>A</td>\r\n<td align=\"center\">2</td>\r\n<td><a href=\"https://phphub.org/topics/2079\">编码风格规范</a></td>\r\n<td>Paul M. Jones</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td>A</td>\r\n<td align=\"center\">3</td>\r\n<td><a href=\"https://phphub.org/topics/2080\">日志接口规范</a></td>\r\n<td>Jordi Boggiano</td>\r\n<td><em>N/A</em></td>\r\n<td><em>N/A</em></td>\r\n</tr>\r\n<tr>\r\n<td>A</td>\r\n<td align=\"center\">4</td>\r\n<td><a href=\"https://phphub.org/