INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (99, 1, '2016-12-20 23:30:19', '2016-12-20 15:30:19', '[root@iZ94orvma5bZ ~]# cat /etc/redhat-release\nCentOS Linux release 7.2.1511 (Core)\n\n[root@iZ94orvma5bZ ~]# cat /etc/os-release\nNAME=\"CentOS Linux\"\nVERSION=\"7 (Core)\"\nID=\"centos\"\nID_LIKE=\"rhel fedora\"\nVERSION_ID=\"7\"\nPRETTY_NAME=\"CentOS Linux 7 (Core)\"\nANSI_COLOR=\"0;31\"\nCPE_NAME=\"cpe:/o:centos:centos:7\"\nHOME_URL=\"https://www.centos.org/\"\nBUG_REPORT_URL=\"https://bugs.centos.org/\"\n\nCENTOS_MANTISBT_PROJECT=\"CentOS-7\"\nCENTOS_MANTISBT_PROJECT_VERSION=\"7\"\nREDHAT_SUPPORT_PRODUCT=\"centos\"\nREDHAT_SUPPORT_PRODUCT_VERSION=\"7\"', 'centos 7 查看系统版本信息', '', 'inherit', 'closed', 'closed', '', '61-autosave-v1', '', '', '2016-12-20 23:30:19', '2016-12-20 15:30:19', '', 61, 'http://luchanglong.com.cn/?p=99', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (102, 1, '2016-12-20 23:37:55', '2016-12-20 15:37:55', '用户操作\r\nw 列出在线用户\r\ncut -d : -f 1 /etc/passwd 列出所有用户\r\ncat /etc/passwd | grep -v /sbin/nologin | cut -d : -f 1 列出可登陆用户\r\nuserdel ftpuser 删除用户\r\ncut -d : -f 1 /etc/group 列出用户组\r\ngroupdel peter 删除用户组', 'centos 管理用户、用户组命令', '', 'inherit', 'closed', 'closed', '', '95-revision-v1', '', '', '2016-12-20 23:37:55', '2016-12-20 15:37:55', '', 95, 'http://luchanglong.com.cn/?p=102', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (103, 1, '2017-01-10 10:58:51', '2017-01-10 02:58:51', '<div class=\"line number1 index0 alt2\">\r\n<pre class=\"lang:php decode:true \">function bubbleSort($numbers) {\r\n    $cnt = count($numbers);\r\n    for ($i = 0; $i &lt; $cnt; $i++) {\r\n        for ($j = 0; $j &lt; $cnt - $i - 1; $j++) {\r\n            if ($numbers[$j] &gt; $numbers[$j + 1]) {\r\n                $temp = $numbers[$j];\r\n                $numbers[$j] = $numbers[$j + 1];\r\n                $numbers[$j + 1] = $temp;\r\n            }\r\n        }\r\n    }\r\n \r\n    return $numbers;\r\n}\r\n \r\n$num = array(20, 40, 60, 80, 30, 70, 90, 10, 50, 0);\r\nvar_dump(bubbleSort($num));\r\n \r\n//输出结果如下：\r\n//array(10) {\r\n//  [0]=&gt;\r\n//  int(0)\r\n//  [1]=&gt;\r\n//  int(10)\r\n//  [2]=&gt;\r\n//  int(20)\r\n//  [3]=&gt;\r\n//  int(30)\r\n//  [4]=&gt;\r\n//  int(40)\r\n//  [5]=&gt;\r\n//  int(50)\r\n//  [6]=&gt;\r\n//  int(60)\r\n//  [7]=&gt;\r\n//  int(70)\r\n//  [8]=&gt;\r\n//  int(80)\r\n//  [9]=&gt;\r\n//  int(90)\r\n//}</pre>\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n</div>', 'php 冒泡排序', '', 'inherit', 'closed', 'closed', '', '53-autosave-v1', '', '', '2017-01-10 10:58:51', '2017-01-10 02:58:51', '', 53, 'http://luchanglong.com.cn/?p=103', 0, 'revision', '', 0);
