INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (155, 1, '2017-01-05 15:37:13', '2017-01-05 07:37:13', '安装\r\n\r\nyum install subversion\r\n\r\n<img class=\"alignnone size-medium wp-image-150\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/01/1-300x80.jpg\" alt=\"\" width=\"300\" height=\"80\" />\r\n\r\n查看版本\r\n\r\nsvnserve --version\r\n\r\n<img class=\"alignnone size-medium wp-image-153\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/01/2-300x108.jpg\" alt=\"\" width=\"300\" height=\"108\" />\r\n\r\n创建版本库目录\r\n\r\nmkdir -p /home/svn/response\r\n\r\n创建版本库\r\n\r\nsvnadmin create /home/svn/response\r\n\r\n目录下生成一下文件\r\n\r\n<img class=\"alignnone size-medium wp-image-154\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/01/3-300x97.jpg\" alt=\"\" width=\"300\" height=\"97\" />\r\n\r\n&nbsp;\r\n\r\n&nbsp;', 'centos 7 安装配置 svn 服务', '', 'inherit', 'closed', 'closed', '', '149-revision-v1', '', '', '2017-01-05 15:37:13', '2017-01-05 07:37:13', '', 149, 'http://luchanglong.com.cn/?p=155', 0, 'revision', '', 0);
