INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (242, 1, '2017-01-17 13:51:01', '2017-01-17 05:51:01', '前面写过 服务端的搭建配置 <a href=\"http://luchanglong.com.cn/?p=149\">http://luchanglong.com.cn/?p=149</a>\r\n\r\n现在我们来看一下客户端应该怎么做！\r\n\r\n1.安装\r\n\r\nyum install -y subversion\r\n\r\n安装成功可以查看版本\r\n<pre class=\"lang:default decode:true\">[root@iZwz94arwsjf9juf1lfjrgZ ~]# svn --version\r\nsvn, version 1.7.14 (r1542130)\r\n   compiled Nov 20 2015, 19:25:09\r\n</pre>\r\n现在已经可以使用svn了，下面我们来checkout一下\r\n\r\n将远程目录checkout到当前shop目录下：\r\n\r\nsvn checkout svn://120.24.xx.xx/shop   shop\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;', 'centos7 安装配置和使用 svn  客户端', '', 'inherit', 'closed', 'closed', '', '239-revision-v1', '', '', '2017-01-17 13:51:01', '2017-01-17 05:51:01', '', 239, 'http://luchanglong.com.cn/?p=242', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (244, 1, '2017-01-18 14:55:21', '2017-01-18 06:55:21', '对于mysql数据库管理来说，一般我们使用的是navicat，当然了其他就不说了，现在我们来介绍一个新的工具：SQLyog（https://www.webyog.com/）\r\n\r\n我们使用navicat导出的sql文件总是很大，而使用SQLyog导出的sql文件只有navicat导出的文件的1/3到1/2而已，所以SQLyog首先就适合导出作为安装的sql语句（当然我们也不会使用navicat导出的 文件来作为安装文件使用）。\r\n\r\n我们在修改测试版的时候数据库结构总是在零零碎碎的变动，当期们想要将生产环境升级的时候，问题来了，要怎么记住每一次改动呢？\r\n\r\n我要介绍的就是SQLyog中的一个数据升级工具，能完全解决这个问题。\r\n\r\n打开SQLyog，选择 高级工具-&gt;架构同步工具\r\n\r\n<img class=\"alignnone size-full wp-image-246\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/01/11-1.png\" alt=\"\" width=\"725\" height=\"250\" />\r\n\r\n左边选择最新版本数据库结构，右边选择旧的数据库结构，点击比较，就能生成升级数据库用语句，方便吧\r\n\r\n<img class=\"alignnone size-medium wp-image-249\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/01/22-900x558.jpg\" alt=\"\" width=\"900\" height=\"558\" />\r\n\r\n&nbsp;\r\n\r\n&nbsp;', 'mysql 数据库升级工具使用', '', 'publish', 'open', 'open', '', 'mysql-%e6%95%b0%e6%8d%ae%e5%ba%93%e5%8d%87%e7%ba%a7%e5%b7%a5%e5%85%b7%e4%bd%bf%e7%94%a8', '', '', '2017-01-18 14:58:31', '2017-01-18 06:58:31', '', 0, 'http://luchanglong.com.cn/?p=244', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (245, 1, '2017-01-18 14:53:48', '2017-01-18 06:53:48', '', '11', '', 'inherit', 'open', 'closed', '', '11-2', '', '', '2017-01-18 14:53:48', '2017-01-18 06:53:48', '', 244, 'http://luchanglong.com.cn/wp-content/uploads/2017/01/11.png', 0, 'attachment', 'image/png', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (246, 1, '2017-01-18 14:54:54', '2017-01-18 06:54:54', '', '11', '', 'inherit', 'open', 'closed', '', '11-3', '', '', '2017-01-18 14:54:54', '2017-01-18 06:54:54', '', 244, 'http://luchanglong.com.cn/wp-content/uploads/2017/01/11-1.png', 0, 'attachment', 'image/png', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (247, 1, '2017-01-18 14:55:21', '2017-01-18 06:55:21', '对于mysql数据库管理来说，一般我们使用的是navicat，当然了其他就不说了，现在我们来介绍一个新的工具：SQLyog（https://www.webyog.com/）\r\n\r\n我们使用navicat导出的sql文件总是很大，而使用SQLyog导出的sql文件只有navicat导出的文件的1/3到1/2而已，所以SQLyog首先就适合导出作为安装的sql语句（当然我们也不会使用navicat导出的 文件来作为安装文件使用）。\r\n\r\n我们在修改测试版的时候数据库结构总是在零零碎碎的变动，当期们想要将生产环境升级的时候，问题来了，要怎么记住每一次改动呢？\r\n\r\n我要介绍的就是SQLyog中的一个数据升级工具，能完全解决这个问题。\r\n\r\n打开SQLyog，选择 高级工具-&gt;架构同步工具\r\n\r\n<img class=\"alignnone size-full wp-image-246\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/01/11-1.png\" alt=\"\" width=\"725\" height=\"250\" />\r\n\r\n&nbsp;\r\n\r\n&nbsp;', 'mysql 数据库升级工具使用', '', 'inherit', 'closed', 'closed', '', '244-revision-v1', '', '', '2017-01-18 14:55:21', '2017-01-18 06:55:21', '', 244, 'http://luchanglong.com.cn/?p=247', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (248, 1, '2017-01-18 14:57:25', '2017-01-18 06:57:25', '对于mysql数据库管理来说，一般我们使用的是navicat，当然了其他就不说了，现在我们来介绍一个新的工具：SQLyog（https://www.webyog.com/）\n\n我们使用navicat导出的sql文件总是很大，而使用SQLyog导出的sql文件只有navicat导出的文件的1/3到1/2而已，所以SQLyog首先就适合导出作为安装的sql语句（当然我们也不会使用navicat导出的 文件来作为安装文件使用）。\n\n我们在修改测试版的时候数据库结构总是在零零碎碎的变动，当期们想要将生产环境升级的时候，问题来了，要怎么记住每一次改动呢？\n\n我要介绍的就是SQLyog中的一个数据升级工具，能完全解决这个问题。\n\n打开SQLyog，选择 高级工具-&gt;架构同步工具\n\n<img class=\"alignnone size-full wp-image-246\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/01/11-1.png\" alt=\"\" width=\"725\" height=\"250\" />\n\n左边选择最新版本数据库结构，右边选择旧的数据库结构，点击比较，就能生成升级数据库用语句，方便吧\n\n&nbsp;\n\n&nbsp;\n\n&nbsp;', 'mysql 数据库升级工具使用', '', 'inherit', 'closed', 'closed', '', '244-autosave-v1', '', '', '2017-01-18 14:57:25', '2017-01-18 06:57:25', '', 244, 'http://luchanglong.com.cn/?p=248', 0, 'revision', '', 0);
