INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (263, 1, '2017-02-03 14:09:45', '2017-02-03 06:09:45', '在windows server 2008之后默认图片都只显示图标，想要在图片上显示缩略图和显示预览需要重新设置\r\n\r\n打开一个后图片的文件夹，选择 查看-&gt;选项-&gt;查看 ,取消 “始终显示图标，从不显示缩略图”的勾选，确定。之后将查看方式设置为中等图标（大图标），就可以查看缩略图和预览图片了啦。\r\n\r\n<img class=\"alignnone size-medium wp-image-261\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/02/1-1-900x576.png\" alt=\"\" width=\"900\" height=\"576\" />\r\n\r\n<img class=\"alignnone size-medium wp-image-262\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/02/2-1-900x552.png\" alt=\"\" width=\"900\" height=\"552\" />\r\n\r\n&nbsp;', 'windows server 2016 设置查看图片预览和显示缩略图', '', 'inherit', 'closed', 'closed', '', '260-revision-v1', '', '', '2017-02-03 14:09:45', '2017-02-03 06:09:45', '', 260, 'http://luchanglong.com.cn/?p=263', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (264, 1, '2017-02-03 14:20:11', '2017-02-03 06:20:11', 'windows10 和windows server2012/2016 打开我的电脑后默认打开的是快速访问，这可能会泄露我们的隐私和使用习惯，下面我们来关闭它。\r\n\r\n打开我的电脑,选择查看，在常规选项中，将“打开文件资源管理器是打开”设置为“此电脑”，取消勾选“隐私”中的那两项，确定，设置完成，以后打开我的电脑就会不会默认打开快速访问了。\r\n\r\n<img class=\"alignnone size-medium wp-image-265\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/02/3-1-900x629.png\" alt=\"\" width=\"900\" height=\"629\" />', 'windows server 2016 设置关闭快速访问和设置打开我的电脑 默认打开此电脑', '', 'publish', 'open', 'open', '', 'windows-server-2016-%e8%ae%be%e7%bd%ae%e5%85%b3%e9%97%ad%e5%bf%ab%e9%80%9f%e8%ae%bf%e9%97%ae%e5%92%8c%e8%ae%be%e7%bd%ae%e6%89%93%e5%bc%80%e6%88%91%e7%9a%84%e7%94%b5%e8%84%91-%e9%bb%98%e8%ae%a4', '', '', '2017-02-03 14:20:46', '2017-02-03 06:20:46', '', 0, 'http://luchanglong.com.cn/?p=264', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (265, 1, '2017-02-03 14:19:57', '2017-02-03 06:19:57', '', '3', '', 'inherit', 'open', 'closed', '', '3-6', '', '', '2017-02-03 14:19:57', '2017-02-03 06:19:57', '', 264, 'http://luchanglong.com.cn/wp-content/uploads/2017/02/3-1.png', 0, 'attachment', 'image/png', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (266, 1, '2017-02-03 14:20:11', '2017-02-03 06:20:11', 'windows10 和windows server2012/2016 打开我的电脑后默认打开的是快速访问，这可能会泄露我们的隐私和使用习惯，下面我们来关闭它。\r\n\r\n打开我的电脑,选择查看，在常规选项中，将“打开文件资源管理器是打开”设置为“此电脑”，取消勾选“隐私”中的那两项，确定，设置完成，以后打开我的电脑就会不会默认打开快速访问了。\r\n\r\n<img class=\"alignnone size-medium wp-image-265\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/02/3-1-900x629.png\" alt=\"\" width=\"900\" height=\"629\" />', 'windows server 2016 设置关闭快速访问和设置打开我的电脑 默认打开此电脑', '', 'inherit', 'closed', 'closed', '', '264-revision-v1', '', '', '2017-02-03 14:20:11', '2017-02-03 06:20:11', '', 264, 'http://luchanglong.com.cn/?p=266', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (267, 1, '2017-02-03 14:22:00', '2017-02-03 06:22:00', 'windows server 2016 如何预览图片？\r\n\r\n在windows server 2008之后默认图片都只显示图标，想要在图片上显示缩略图和显示预览需要重新设置\r\n\r\n打开一个后图片的文件夹，选择 查看-&gt;选项-&gt;查看 ,取消 “始终显示图标，从不显示缩略图”的勾选，确定。之后将查看方式设置为中等图标（大图标），就可以查看缩略图和预览图片了啦。\r\n\r\n<img class=\"alignnone size-medium wp-image-261\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/02/1-1-900x576.png\" alt=\"\" width=\"900\" height=\"576\" />\r\n\r\n<img class=\"alignnone size-medium wp-image-262\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/02/2-1-900x552.png\" alt=\"\" width=\"900\" height=\"552\" />\r\n\r\n&nbsp;', 'windows server 2016 设置查看图片预览和显示缩略图', '', 'inherit', 'closed', 'closed', '', '260-revision-v1', '', '', '2017-02-03 14:22:00', '2017-02-03 06:22:00', '', 260, 'http://luchanglong.com.cn/?p=267', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (268, 1, '2017-02-03 17:22:10', '2017-02-03 09:22:10', '错误\r\n\r\nroot@iZ94orvma5bZ mshop]# git push origin master\r\nTo git@github.com:DanceHill/mshop.git\r\n! [rejected] master -&gt; master (non-fast-forward)\r\nerror: failed to push some refs to \'git@github.com:DanceHill/mshop.git\'\r\nhint: Updates were rejected because the tip of your current branch is behind\r\nhint: its remote counterpart. Merge the remote changes (e.g. \'git pull\')\r\nhint: before pushing again.\r\nhint: See the \'Note about fast-forwards\' in \'git push --help\' for details.\r\n\r\n&nbsp;\r\n\r\n这个是因为本地版本低于服务器版本，比如你在git网页上修改了文件，而又没有 pull，然后直接修改本地文件，你提交的时候就会报这个错误\r\n\r\n解决：\r\n<pre class=\"lang:sh decode:true  \">[root@iZ94orvma5bZ mshop]# git push -u origin master -f\r\nCounting objects: 98, done.\r\nCompressing objects: 100% (57/57), done.\r\nWriting objects: 100% (60/60), 57.39 KiB | 0 bytes/s, done.\r\nTotal 60 (delta 36), reused 0 (delta 0)\r\nremote: Resolving deltas: 100% (36/36), completed with 31 local objects.\r\nTo git@github.asdfddff/mvshop.git\r\n+ bb60a15...291193d master -&gt; master (forced update)\r\nBranch master set up to track remote branch master from origin.</pre>\r\n&nbsp;', '使用git 错误解决hint: Updates were rejected because the tip of your current branch is behind', '', 'inherit', 'closed', 'closed', '', '147-revision-v1', '', '', '2017-02-03 17:22:10', '2017-02-03 09:22:10', '', 147, 'http://luchanglong.com.cn/?p=268', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (269, 1, '2017-02-03 17:29:28', '2017-02-03 09:29:28', '使用centos7搭建了svn服务器后，在windows使用客户端，检出后使用编辑项目，发现新增文件被自动忽略，提交时勾选“显示无版本控制的文件”也不显示。\r\n\r\n这时直接在根目录选择属性，编辑global-ignores 文件，删除行\"*\",问题解决\r\n\r\n<img class=\"alignnone size-full wp-image-270\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/02/1-2.png\" alt=\"\" width=\"656\" height=\"663\" />\r\n\r\n<img class=\"alignnone size-full wp-image-271\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/02/2-2.png\" alt=\"\" width=\"716\" height=\"466\" />\r\n\r\n&nbsp;\r\n\r\n&nbsp;', 'svn 新增文件被忽略解决办法', '', 'publish', 'open', 'open', '', 'svn-%e6%96%b0%e5%a2%9e%e6%96%87%e4%bb%b6%e8%a2%ab%e5%bf%bd%e7%95%a5%e8%a7%a3%e5%86%b3%e5%8a%9e%e6%b3%95', '', '', '2017-02-03 17:29:28', '2017-02-03 09:29:28', '', 0, 'http://luchanglong.com.cn/?p=269', 0, 'post', '', 9);
