INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (327, 1, '2017-05-11 11:12:56', '2017-05-11 03:12:56', 'laravel5默认目录结构如下\r\n\r\n<img class=\"alignnone size-full wp-image-317\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/1.png\" alt=\"\" width=\"398\" height=\"816\" />\r\n\r\n现在我们需要在app下使用创建文件夹modules下面放置各种模块 admin、api、front等等，就需要用到<span class=\"author\"><a class=\"url fn\" href=\"https://github.com/caffeinated\" rel=\"author\">caffeinated</a></span><span class=\"path-divider\">/</span><strong><a href=\"https://github.com/caffeinated/modules\" data-pjax=\"#js-repo-pjax-container\">modules</a> </strong>\r\n\r\n在https://github.com/caffeinated/modules/wiki 中有介绍详细的安装方法\r\n\r\n1.通过composer安装&lt;至于如何使用composer请自行搜索&gt;\r\n\r\nlaravel5.3以上可以直接在laravel根目录使用安装\r\n<pre><code>composer require caffeinated/modules</code></pre>\r\n由于笔者使用laravel5.1，所以\r\n\r\n在lavravel的composer.json中添加\r\n<pre class=\"\">\"caffeinated/modules\": \"3.0.*\"</pre>\r\n然后执行composer update ，安装成功\r\n\r\n<img class=\"alignnone size-full wp-image-318\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/2.png\" alt=\"\" width=\"625\" height=\"686\" />\r\n\r\n在/vendor下看到caffeinated文件夹\r\n\r\n<img class=\"alignnone size-full wp-image-320\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/4.png\" alt=\"\" width=\"487\" height=\"262\" />\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n2.配置modules到项目中\r\n\r\n配置Service Provider\r\n\r\n在config/app.php的providers中添加\r\n<pre><span class=\"pl-s1\"><span class=\"pl-c1\">Caffeinated\\Modules\\</span><span class=\"pl-c1\">ModulesServiceProvider</span><span class=\"pl-k\">::</span><span class=\"pl-c1\">class</span>,</span></pre>\r\n配置Facade在config/app.php的Facade中添加\r\n<pre><span class=\"pl-s1\"><span class=\"pl-s\"><span class=\"pl-pds\">\'</span>Module<span class=\"pl-pds\">\'</span></span> <span class=\"pl-k\">=&gt;</span> <span class=\"pl-c1\">Caffeinated\\Modules\\Facades\\</span><span class=\"pl-c1\">Module</span><span class=\"pl-k\">::</span><span class=\"pl-c1\">class</span>,</span></pre>\r\n&nbsp;\r\n\r\n3.使用laravel 的artisan来生成modules，生成成功后可以在app目录多了一个文件夹modules，并且下面有admin模块，有自己的controller和route\r\n\r\n<img class=\"alignnone size-full wp-image-323\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/5.png\" alt=\"\" width=\"667\" height=\"870\" /> <img class=\"alignnone size-medium wp-image-324\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/6.png\" alt=\"\" width=\"623\" height=\"156\" />\r\n\r\n<img class=\"alignnone size-full wp-image-326\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/7.png\" alt=\"\" width=\"461\" height=\"678\" />\r\n\r\n下面我们\r\n\r\n&nbsp;\r\n\r\n&nbsp;', 'laravel模块化开发', '', 'inherit', 'closed', 'closed', '', '316-revision-v1', '', '', '2017-05-11 11:12:56', '2017-05-11 03:12:56', '', 316, 'http://luchanglong.com.cn/?p=327', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (328, 1, '2017-05-11 11:14:00', '2017-05-11 03:14:00', 'laravel5默认目录结构如下\r\n\r\n<img class=\"alignnone size-full wp-image-317\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/1.png\" alt=\"\" width=\"398\" height=\"816\" />\r\n\r\n现在我们需要在app下使用创建文件夹modules下面放置各种模块 admin、api、front等等，就需要用到<span class=\"author\"><a class=\"url fn\" href=\"https://github.com/caffeinated\" rel=\"author\">caffeinated</a></span><span class=\"path-divider\">/</span><strong><a href=\"https://github.com/caffeinated/modules\" data-pjax=\"#js-repo-pjax-container\">modules</a> </strong>\r\n\r\n在https://github.com/caffeinated/modules/wiki 中有介绍详细的安装方法\r\n\r\n1.通过composer安装&lt;至于如何使用composer请自行搜索&gt;\r\n\r\nlaravel5.3以上可以直接在laravel根目录使用安装\r\n<pre><code>composer require caffeinated/modules</code></pre>\r\n由于笔者使用laravel5.1，所以\r\n\r\n在lavravel的composer.json中添加\r\n<pre class=\"\">\"caffeinated/modules\": \"3.0.*\"</pre>\r\n然后执行composer update ，安装成功\r\n\r\n<img class=\"alignnone size-full wp-image-318\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/2.png\" alt=\"\" width=\"625\" height=\"686\" />\r\n\r\n在/vendor下看到caffeinated文件夹\r\n\r\n<img class=\"alignnone size-full wp-image-320\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/4.png\" alt=\"\" width=\"487\" height=\"262\" />\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n2.配置modules到项目中\r\n\r\n配置Service Provider\r\n\r\n在config/app.php的providers中添加\r\n<pre><span class=\"pl-s1\"><span class=\"pl-c1\">Caffeinated\\Modules\\</span><span class=\"pl-c1\">ModulesServiceProvider</span><span class=\"pl-k\">::</span><span class=\"pl-c1\">class</span>,</span></pre>\r\n配置Facade在config/app.php的Facade中添加\r\n<pre><span class=\"pl-s1\"><span class=\"pl-s\"><span class=\"pl-pds\">\'</span>Module<span class=\"pl-pds\">\'</span></span> <span class=\"pl-k\">=&gt;</span> <span class=\"pl-c1\">Caffeinated\\Modules\\Facades\\</span><span class=\"pl-c1\">Module</span><span class=\"pl-k\">::</span><span class=\"pl-c1\">class</span>,</span></pre>\r\n&nbsp;\r\n\r\n3.使用laravel 的artisan来生成modules，生成成功后可以在app目录多了一个文件夹modules，并且下面有admin模块，有自己的controller和route\r\n\r\n<img class=\"alignnone size-full wp-image-323\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/5.png\" alt=\"\" width=\"667\" height=\"870\" />\r\n\r\n<img class=\"alignnone size-medium wp-image-324\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/6.png\" alt=\"\" width=\"623\" height=\"156\" />\r\n\r\n&nbsp;\r\n\r\n下面我们\r\n\r\n&nbsp;\r\n\r\n&nbsp;', 'laravel模块化开发', '', 'inherit', 'closed', 'closed', '', '316-revision-v1', '', '', '2017-05-11 11:14:00', '2017-05-11 03:14:00', '', 316, 'http://luchanglong.com.cn/?p=328', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (329, 1, '2018-03-07 11:30:24', '2018-03-07 03:30:24', 'laravel5默认目录结构如下\n\n<img class=\"alignnone size-full wp-image-317\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/1.png\" alt=\"\" width=\"398\" height=\"816\" />\n\n现在我们需要在app下使用创建文件夹modules下面放置各种模块 admin、api、front等等，就需要用到<span class=\"author\"><a class=\"url fn\" href=\"https://github.com/caffeinated\" rel=\"author\">caffeinated</a></span><span class=\"path-divider\">/</span><strong><a href=\"https://github.com/caffeinated/modules\" data-pjax=\"#js-repo-pjax-container\">modules</a> </strong>\n\n在https://github.com/caffeinated/modules/wiki 中有介绍详细的安装方法\n\n1.通过composer安装&lt;至于如何使用composer请自行搜索&gt;\n\nlaravel5.3以上可以直接在laravel根目录使用安装\n<pre><code>composer require caffeinated/modules</code></pre>\n由于笔者使用laravel5.1，所以\n\n在lavravel的composer.json中添加\n<pre class=\"\">\"caffeinated/modules\": \"3.0.*\"</pre>\n然后执行composer update ，安装成功\n\n<img class=\"alignnone size-full wp-image-318\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/2.png\" alt=\"\" width=\"625\" height=\"686\" />\n\n在/vendor下看到caffeinated文件夹\n\n<img class=\"alignnone size-full wp-image-320\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/4.png\" alt=\"\" width=\"487\" height=\"262\" />\n\n&nbsp;\n\n&nbsp;\n\n2.配置modules到项目中\n\n配置Service Provider\n\n在config/app.php的providers中添加\n\nCaffeinated\\Modules\\ModulesServiceProvider::class\n\n配置Facade在config/app.php的Facade中添加\n\n\'Module\' =&gt; Caffeinated\\Modules\\Facades\\Module::class\n\n&nbsp;\n\n&nbsp;\n\n3.使用laravel 的artisan来生成modules，生成成功后可以在app目录多了一个文件夹modules，并且下面有admin模块，有自己的controller和route\n\nphp artisan make:module Admin\n\n(ps:)\n\n<img class=\"alignnone size-full wp-image-323\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/5.png\" alt=\"\" width=\"667\" height=\"870\" />\n\n<img class=\"alignnone size-medium wp-image-324\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/6.png\" alt=\"\" width=\"623\" height=\"156\" />\n\n<img class=\"alignnone size-full wp-image-331\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/8.png\" alt=\"\" width=\"461\" height=\"678\" />\n\n下面列出一些modules可执行的命令\n<h2>Generators</h2>\n<ul>\n 	<li><code>make:module</code></li>\n 	<li><code>make:module:controller</code></li>\n 	<li><code>make:module:migration</code></li>\n 	<li><code>make:module:request</code></li>\n 	<li><code>make:module:test</code></li>\n</ul>\n<h2><a id=\"user-content-module-specific\" class=\"anchor\" href=\"https://github.com/caffeinated/modules/wiki/Artisan-Commands#module-specific\"></a>Module Specific</h2>\n<ul>\n 	<li><code>module:disable</code></li>\n 	<li><code>module:enable</code></li>\n 	<li><code>module:list</code></li>\n 	<li><code>module:migrate</code></li>\n 	<li><code>module:migrate:refresh</code></li>\n 	<li><code>module:migrate:reset</code></li>\n 	<li><code>module:migrate:rollback</code></li>\n 	<li><code>module:seed</code></li>\n</ul>\n&nbsp;\n\n&nbsp;\n\n友情提示：\n\nlaravel5.1不要配7.2...', 'laravel5使用caffeinated/modules模块化开发', '', 'inherit', 'closed', 'closed', '', '316-autosave-v1', '', '', '2018-03-07 11:30:24', '2018-03-07 03:30:24', '', 316, 'http://luchanglong.com.cn/?p=329', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (330, 1, '2017-05-11 11:15:13', '2017-05-11 03:15:13', 'laravel5默认目录结构如下\r\n\r\n<img class=\"alignnone size-full wp-image-317\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/1.png\" alt=\"\" width=\"398\" height=\"816\" />\r\n\r\n现在我们需要在app下使用创建文件夹modules下面放置各种模块 admin、api、front等等，就需要用到<span class=\"author\"><a class=\"url fn\" href=\"https://github.com/caffeinated\" rel=\"author\">caffeinated</a></span><span class=\"path-divider\">/</span><strong><a href=\"https://github.com/caffeinated/modules\" data-pjax=\"#js-repo-pjax-container\">modules</a> </strong>\r\n\r\n在https://github.com/caffeinated/modules/wiki 中有介绍详细的安装方法\r\n\r\n1.通过composer安装&lt;至于如何使用composer请自行搜索&gt;\r\n\r\nlaravel5.3以上可以直接在laravel根目录使用安装\r\n<pre><code>composer require caffeinated/modules</code></pre>\r\n由于笔者使用laravel5.1，所以\r\n\r\n在lavravel的composer.json中添加\r\n<pre class=\"\">\"caffeinated/modules\": \"3.0.*\"</pre>\r\n然后执行composer update ，安装成功\r\n\r\n<img class=\"alignnone size-full wp-image-318\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/2.png\" alt=\"\" width=\"625\" height=\"686\" />\r\n\r\n在/vendor下看到caffeinated文件夹\r\n\r\n<img class=\"alignnone size-full wp-image-320\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/4.png\" alt=\"\" width=\"487\" height=\"262\" />\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n2.配置modules到项目中\r\n\r\n配置Service Provider\r\n\r\n在config/app.php的providers中添加\r\n<pre><span class=\"pl-s1\"><span class=\"pl-c1\">Caffeinated\\Modules\\</span><span class=\"pl-c1\">ModulesServiceProvider</span><span class=\"pl-k\">::</span><span class=\"pl-c1\">class</span>,</span></pre>\r\n配置Facade在config/app.php的Facade中添加\r\n<pre><span class=\"pl-s1\"><span class=\"pl-s\"><span class=\"pl-pds\">\'</span>Module<span class=\"pl-pds\">\'</span></span> <span class=\"pl-k\">=&gt;</span> <span class=\"pl-c1\">Caffeinated\\Modules\\Facades\\</span><span class=\"pl-c1\">Module</span><span class=\"pl-k\">::</span><span class=\"pl-c1\">class</span>,</span></pre>\r\n&nbsp;\r\n\r\n3.使用laravel 的artisan来生成modules，生成成功后可以在app目录多了一个文件夹modules，并且下面有admin模块，有自己的controller和route\r\n\r\n<img class=\"alignnone size-full wp-image-323\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/5.png\" alt=\"\" width=\"667\" height=\"870\" />\r\n\r\n<img class=\"alignnone size-medium wp-image-324\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/6.png\" alt=\"\" width=\"623\" height=\"156\" />\r\n\r\n<img class=\"alignnone size-full wp-image-326\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/05/7.png\" alt=\"\" width=\"461\" height=\"678\" />\r\n\r\n下面列出一些modules可执行的命令\r\n<h2>Generators</h2>\r\n<ul>\r\n 	<li><a href=\"https://github.com/caffeinated/modules/wiki/Artisan-Commands#makemodule-slug\"><code>make:module</code></a></li>\r\n 	<li><a href=\"https://github.com/caffeinated/modules/wiki/Artisan-Commands#makemodulecontroller-slug-name\"><code>make:module:controller</code></a></li>\r\n 	<li><a href=\"https://github.com/caffeinated/modules/wiki/Artisan-Commands#makemodulemigration-slug-table\"><code>make:module:migration</code></a></li>\r\n 	<li><a href=\"https://github.com/caffeinated/modules/wiki/Artisan-Commands#makemodulerequest-slug-name\"><code>make:module:request</code></a></li>\r\n 	<li><a href=\"https://github.com/caffeinated/modules/wiki/Artisan-Commands#makemoduletest-slug-name\"><code>make:module:test</code></a></li>\r\n</ul>\r\n<h2><a id=\"user-content-module-specific\" class=\"anchor\" href=\"https://github.com/caffeinated/modules/wiki/Artisan-Commands#module-specific\"></a>Module Specific</h2>\r\n<ul>\r\n 	<li><a href=\"https://github.com/caffeinated/modules/wiki/Artisan-Commands#moduledisable-module\"><code>module:disable</code></a></li>\r\n 	<li><a href=\"https://github.com/caffeinated/modules/wiki/Artisan-Commands#moduleenable-module\"><code>module:enable</code></a></li>\r\n 	<li><a href=\"https://github.com/caffeinated/modules/wiki/Artisan-Commands#modulelist\"><code>module:list</code></a></li>\r\n 	<li><a href=\"https://github.com/caffeinated/modules/wiki/Artisan-Commands#modulemigrate-module\"><code>module:migrate</code></a></li>\r\n 	<li><a href=\"https://github.com/caffeinated/modules/wiki/Artisan-Commands#modulemigraterefresh-module\"><code>module:migrate:refresh</code></a></li>\r\n 	<li><a href=\"https://github.com/caffeinated/modules/wiki/Artisan-Commands#modulemigratereset-module\"><code>module:migrate:reset</code></a></li>\r\n 	<li><a href=\"https://github.com/caffeinated/modules/wiki/Artisan-Commands#modulemigraterollback-module\"><code>module:migrate:rollback</code></a></li>\r\n 	<li><a href=\"https://github.com/caffeinated/modules/wiki/Artisan-Commands#moduleseed-module\"><code>module:seed</code></a></li>\r\n</ul>\r\n&nbsp;', 'laravel模块化开发', '', 'inherit', 'closed', 'closed', '', '316-revision-v1', '', '', '2017-05-11 11:15:13', '2017-05-11 03:15:13', '', 316, 'http://luchanglong.com.cn/?p=330', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (331, 1, '2017-05-11 11:16:31', '2017-05-11 03:16:31', '', '8', '', 'inherit', 'open', 'closed', '', '8-2', '', '', '2017-05-11 11:16:31', '2017-05-11 03:16:31', '', 316, 'http://luchanglong.com.cn/wp-content/uploads/2017/05/8.png', 0, 'attachment', 'image/png', 0);
