INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (378, 1, '2017-08-16 13:17:21', '2017-08-16 05:17:21', '以下仿照linux权限算法，对内容发布平台进行设置\r\n<pre class=\"lang:php decode:true\"> /**\r\n     * 推送平台\r\n     *\r\n     * 值     显示\r\n     * 0      全部\r\n     * 1      web平台\r\n     * 2      android平台\r\n     * 4      iOS平台\r\n     *\r\n     * 3      web+android\r\n     * 5      web+iOS\r\n     * 6      android+iOS\r\n     * 7      web+android+iOS\r\n     *\r\n     * 仿unix权限规则，平台码按等比数列形式增加，如 1,2,4,8,16,32....\r\n     * 两种平台的状态码相加等于的值就表示这两种平台都显示,多个平台也一样\r\n     *\r\n     * @return array([],[]...)\r\n     * */\r\n    static function platForm()\r\n    {\r\n        return array(\r\n            // 推送平台码 、文字\r\n            [\'code\' =&gt; \'0\', \'text\' =&gt; \'全部\'],\r\n            [\'code\' =&gt; \'1\', \'text\' =&gt; \'网站\'],\r\n            [\'code\' =&gt; \'2\', \'text\' =&gt; \'安卓\'],\r\n            [\'code\' =&gt; \'4\', \'text\' =&gt; \'苹果\']\r\n        );\r\n    }\r\n\r\n    /**\r\n     * 获取当前任务推送的平台\r\n     * 为后台编辑提供\r\n     * 状态码,根据保存的platfrom的值获取任务推送的平台\r\n     * 如:传入7，返回 array(1,2,4),表示这个任务在1,2,4 平台开启推送\r\n     * @code int 所有平台码：如0,1,2,3,4,5,6,7\r\n     * @return array(1,2,4...)\r\n     */\r\n    static function getTaskPlatForm($code = 0)\r\n    {\r\n        $plat = [];\r\n        if ($code &gt; 0) {\r\n            $platFrom = array_reverse(self::platForm());//从大到小开始取推送的平台\r\n            foreach ($platFrom as $k =&gt; $v) {\r\n                if ($v[\'code\'] == 0) break;\r\n                if ($code&amp;$v[\'code\'])$plat[] = (int)$v[\'code\']; //使用按位与运算\r\n            }\r\n        } else {\r\n            $plat = [0];\r\n        }\r\n        return $plat;\r\n    }\r\n\r\n    /**\r\n     * 获取传入状态内的所有任务对应的platfrom的值\r\n     * 为前台筛选提供\r\n     * 如:传入2 返回 array(0,2,3,6,7),表示数据库中platfrom 为0,2,3,7,6的都允许显示\r\n     * @param $code int 唯一平台码：如0,1,2,4\r\n     * @return array(2,3,7...)\r\n     * */\r\n    static function inTaskPlatForm($code = 0)\r\n    {\r\n        $plat = [];\r\n        if ($code&gt;0) {\r\n            $platFrom = self::platForm();\r\n            $plat_code = [];\r\n            foreach ($platFrom as $v) {\r\n                $plat_code[] = $v[\'code\'];\r\n            }\r\n            $sum = array_sum($plat_code);\r\n            $plat = [];\r\n            for ($i = $sum; $i &gt;= 0; $i--) {\r\n                if ($i &amp; $code) $plat[] = $i;\r\n            }\r\n        }\r\n        $plat[]=0;\r\n        return $plat;\r\n    }</pre>\r\n&nbsp;', '仿linux权限 设置内容发布平台', '', 'publish', 'open', 'open', '', '%e4%bb%bflinux%e6%9d%83%e9%99%90-%e8%ae%be%e7%bd%aeapi%e5%8f%91%e5%b8%83%e5%b9%b3%e5%8f%b0', '', '', '2017-08-16 13:18:52', '2017-08-16 05:18:52', '', 0, 'http://luchanglong.com.cn/?p=378', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (379, 1, '2017-08-16 13:17:21', '2017-08-16 05:17:21', '以下仿照linux权限算法，对内容发布平台进行设置\r\n\r\n&nbsp;\r\n<pre class=\"lang:php decode:true\"> /**\r\n     * 推送平台\r\n     *\r\n     * 值     显示\r\n     * 0      全部\r\n     * 1      web平台\r\n     * 2      android平台\r\n     * 4      iOS平台\r\n     *\r\n     * 3      web+android\r\n     * 5      web+iOS\r\n     * 6      android+iOS\r\n     * 7      web+android+iOS\r\n     *\r\n     * 仿unix权限规则，平台码按等比数列形式增加，如 1,2,4,8,16,32....\r\n     * 两种平台的状态码相加等于的值就表示这两种平台都显示,多个平台也一样\r\n     *\r\n     * @return array([],[]...)\r\n     * */\r\n    static function platForm()\r\n    {\r\n        return array(\r\n            // 推送平台码 、文字\r\n            [\'code\' =&gt; \'0\', \'text\' =&gt; \'全部\'],\r\n            [\'code\' =&gt; \'1\', \'text\' =&gt; \'网站\'],\r\n            [\'code\' =&gt; \'2\', \'text\' =&gt; \'安卓\'],\r\n            [\'code\' =&gt; \'4\', \'text\' =&gt; \'苹果\']\r\n        );\r\n    }\r\n\r\n    /**\r\n     * 获取当前任务推送的平台\r\n     * 为后台编辑提供\r\n     * 状态码,根据保存的platfrom的值获取任务推送的平台\r\n     * 如:传入7，返回 array(1,2,4),表示这个任务在1,2,4 平台开启推送\r\n     * @code int 所有平台码：如0,1,2,3,4,5,6,7\r\n     * @return array(1,2,4...)\r\n     */\r\n    static function getTaskPlatForm($code = 0)\r\n    {\r\n        $plat = [];\r\n        if ($code &gt; 0) {\r\n            $platFrom = array_reverse(self::platForm());//从大到小开始取推送的平台\r\n            foreach ($platFrom as $k =&gt; $v) {\r\n                if ($v[\'code\'] == 0) break;\r\n                if ($code&amp;$v[\'code\'])$plat[] = (int)$v[\'code\']; //使用按位与运算\r\n            }\r\n        } else {\r\n            $plat = [0];\r\n        }\r\n        return $plat;\r\n    }\r\n\r\n    /**\r\n     * 获取传入状态内的所有任务对应的platfrom的值\r\n     * 为前台筛选提供\r\n     * 如:传入2 返回 array(0,2,3,6,7),表示数据库中platfrom 为0,2,3,7,6的都允许显示\r\n     * @param $code int 唯一平台码：如0,1,2,4\r\n     * @return array(2,3,7...)\r\n     * */\r\n    static function inTaskPlatForm($code = 0)\r\n    {\r\n        $plat = [];\r\n        if ($code&gt;0) {\r\n            $platFrom = self::platForm();\r\n            $plat_code = [];\r\n            foreach ($platFrom as $v) {\r\n                $plat_code[] = $v[\'code\'];\r\n            }\r\n            $sum = array_sum($plat_code);\r\n            $plat = [];\r\n            for ($i = $sum; $i &gt;= 0; $i--) {\r\n                if ($i &amp; $code) $plat[] = $i;\r\n            }\r\n        }\r\n        $plat[]=0;\r\n        return $plat;\r\n    }</pre>\r\n&nbsp;', '仿linux权限 设置api发布平台', '', 'inherit', 'closed', 'closed', '', '378-revision-v1', '', '', '2017-08-16 13:17:21', '2017-08-16 05:17:21', '', 378, 'http://luchanglong.com.cn/?p=379', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (380, 1, '2017-08-16 13:18:05', '2017-08-16 05:18:05', '以下仿照linux权限算法，对内容发布平台进行设置\r\n\r\n&nbsp;\r\n<pre class=\"lang:php decode:true\"> /**\r\n     * 推送平台\r\n     *\r\n     * 值     显示\r\n     * 0      全部\r\n     * 1      web平台\r\n     * 2      android平台\r\n     * 4      iOS平台\r\n     *\r\n     * 3      web+android\r\n     * 5      web+iOS\r\n     * 6      android+iOS\r\n     * 7      web+android+iOS\r\n     *\r\n     * 仿unix权限规则，平台码按等比数列形式增加，如 1,2,4,8,16,32....\r\n     * 两种平台的状态码相加等于的值就表示这两种平台都显示,多个平台也一样\r\n     *\r\n     * @return array([],[]...)\r\n     * */\r\n    static function platForm()\r\n    {\r\n        return array(\r\n            // 推送平台码 、文字\r\n            [\'code\' =&gt; \'0\', \'text\' =&gt; \'全部\'],\r\n            [\'code\' =&gt; \'1\', \'text\' =&gt; \'网站\'],\r\n            [\'code\' =&gt; \'2\', \'text\' =&gt; \'安卓\'],\r\n            [\'code\' =&gt; \'4\', \'text\' =&gt; \'苹果\']\r\n        );\r\n    }\r\n\r\n    /**\r\n     * 获取当前任务推送的平台\r\n     * 为后台编辑提供\r\n     * 状态码,根据保存的platfrom的值获取任务推送的平台\r\n     * 如:传入7，返回 array(1,2,4),表示这个任务在1,2,4 平台开启推送\r\n     * @code int 所有平台码：如0,1,2,3,4,5,6,7\r\n     * @return array(1,2,4...)\r\n     */\r\n    static function getTaskPlatForm($code = 0)\r\n    {\r\n        $plat = [];\r\n        if ($code &gt; 0) {\r\n            $platFrom = array_reverse(self::platForm());//从大到小开始取推送的平台\r\n            foreach ($platFrom as $k =&gt; $v) {\r\n                if ($v[\'code\'] == 0) break;\r\n                if ($code&amp;$v[\'code\'])$plat[] = (int)$v[\'code\']; //使用按位与运算\r\n            }\r\n        } else {\r\n            $plat = [0];\r\n        }\r\n        return $plat;\r\n    }\r\n\r\n    /**\r\n     * 获取传入状态内的所有任务对应的platfrom的值\r\n     * 为前台筛选提供\r\n     * 如:传入2 返回 array(0,2,3,6,7),表示数据库中platfrom 为0,2,3,7,6的都允许显示\r\n     * @param $code int 唯一平台码：如0,1,2,4\r\n     * @return array(2,3,7...)\r\n     * */\r\n    static function inTaskPlatForm($code = 0)\r\n    {\r\n        $plat = [];\r\n        if ($code&gt;0) {\r\n            $platFrom = self::platForm();\r\n            $plat_code = [];\r\n            foreach ($platFrom as $v) {\r\n                $plat_code[] = $v[\'code\'];\r\n            }\r\n            $sum = array_sum($plat_code);\r\n            $plat = [];\r\n            for ($i = $sum; $i &gt;= 0; $i--) {\r\n                if ($i &amp; $code) $plat[] = $i;\r\n            }\r\n        }\r\n        $plat[]=0;\r\n        return $plat;\r\n    }</pre>\r\n&nbsp;', '仿linux权限 设置内容发布平台', '', 'inherit', 'closed', 'closed', '', '378-revision-v1', '', '', '2017-08-16 13:18:05', '2017-08-16 05:18:05', '', 378, 'http://luchanglong.com.cn/?p=380', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (381, 1, '2017-08-16 13:18:52', '2017-08-16 05:18:52', '以下仿照linux权限算法，对内容发布平台进行设置\r\n<pre class=\"lang:php decode:true\"> /**\r\n     * 推送平台\r\n     *\r\n     * 值     显示\r\n     * 0      全部\r\n     * 1      web平台\r\n     * 2      android平台\r\n     * 4      iOS平台\r\n     *\r\n     * 3      web+android\r\n     * 5      web+iOS\r\n     * 6      android+iOS\r\n     * 7      web+android+iOS\r\n     *\r\n     * 仿unix权限规则，平台码按等比数列形式增加，如 1,2,4,8,16,32....\r\n     * 两种平台的状态码相加等于的值就表示这两种平台都显示,多个平台也一样\r\n     *\r\n     * @return array([],[]...)\r\n     * */\r\n    static function platForm()\r\n    {\r\n        return array(\r\n            // 推送平台码 、文字\r\n            [\'code\' =&gt; \'0\', \'text\' =&gt; \'全部\'],\r\n            [\'code\' =&gt; \'1\', \'text\' =&gt; \'网站\'],\r\n            [\'code\' =&gt; \'2\', \'text\' =&gt; \'安卓\'],\r\n            [\'code\' =&gt; \'4\', \'text\' =&gt; \'苹果\']\r\n        );\r\n    }\r\n\r\n    /**\r\n     * 获取当前任务推送的平台\r\n     * 为后台编辑提供\r\n     * 状态码,根据保存的platfrom的值获取任务推送的平台\r\n     * 如:传入7，返回 array(1,2,4),表示这个任务在1,2,4 平台开启推送\r\n     * @code int 所有平台码：如0,1,2,3,4,5,6,7\r\n     * @return array(1,2,4...)\r\n     */\r\n    static function getTaskPlatForm($code = 0)\r\n    {\r\n        $plat = [];\r\n        if ($code &gt; 0) {\r\n            $platFrom = array_reverse(self::platForm());//从大到小开始取推送的平台\r\n            foreach ($platFrom as $k =&gt; $v) {\r\n                if ($v[\'code\'] == 0) break;\r\n                if ($code&amp;$v[\'code\'])$plat[] = (int)$v[\'code\']; //使用按位与运算\r\n            }\r\n        } else {\r\n            $plat = [0];\r\n        }\r\n        return $plat;\r\n    }\r\n\r\n    /**\r\n     * 获取传入状态内的所有任务对应的platfrom的值\r\n     * 为前台筛选提供\r\n     * 如:传入2 返回 array(0,2,3,6,7),表示数据库中platfrom 为0,2,3,7,6的都允许显示\r\n     * @param $code int 唯一平台码：如0,1,2,4\r\n     * @return array(2,3,7...)\r\n     * */\r\n    static function inTaskPlatForm($code = 0)\r\n    {\r\n        $plat = [];\r\n        if ($code&gt;0) {\r\n            $platFrom = self::platForm();\r\n            $plat_code = [];\r\n            foreach ($platFrom as $v) {\r\n                $plat_code[] = $v[\'code\'];\r\n            }\r\n            $sum = array_sum($plat_code);\r\n            $plat = [];\r\n            for ($i = $sum; $i &gt;= 0; $i--) {\r\n                if ($i &amp; $code) $plat[] = $i;\r\n            }\r\n        }\r\n        $plat[]=0;\r\n        return $plat;\r\n    }</pre>\r\n&nbsp;', '仿linux权限 设置内容发布平台', '', 'inherit', 'closed', 'closed', '', '378-revision-v1', '', '', '2017-08-16 13:18:52', '2017-08-16 05:18:52', '', 378, 'http://luchanglong.com.cn/?p=381', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (386, 1, '2017-09-02 01:21:03', '2017-09-01 17:21:03', '<pre class=\"lang:php decode:true \">&lt;?php\r\n/**\r\n * php 正则表达式\r\n * 在php中想使用正则表达式，请先确认已经安装pcre扩展，可以输出phpinfo查看\r\n * author： mrluchanglong@163.com\r\n * 201709\r\n * */\r\n\r\n/**\r\n * 一个正则表达式通常使用如下格式：\r\n * &lt;delimiter&gt;&lt;pattern&gt;&lt;delimiter&gt;[&lt;modifiers&gt;]\r\n * &lt;定界符&gt;&lt;匹配式&gt;&lt;定界符&gt;[修饰符（可选）]\r\n * 修饰符是可选的。界定符把正则式与修饰符分离开来。PCRE将正则表达式的第一个字符作为定界符。\r\n * 所以你应该使用一个不会出现在正则式本身中的字符。\r\n * 或者，你可以使用一个在表达式中存在的字符，但是你必须使用\\对它进行转义.\r\n * 传统来说，字符/被用来当作定界符，但是你也可以选择其他常用的定界符，例如|或者@。\r\n * 从个人角度来说，大多数情况下，我们会选择@，除非我们需要匹配一个Email地址或者类似的包含@的正则式，那种情况下我们将用/。\r\n *\r\n * php函数preg_match()被用来匹配正则表达式。\r\n * 传递到函数的第一个参数式正则式。\r\n * 第二个参数是需要跟正则式匹配的字符串而且它也叫做标题。函数返回TRUE（正则式匹配）或者FALSE（正则式不匹配）。\r\n * 你还可以传递第三个参数————一个变量名。匹配的文本将通过引用存放到这个名字命名的数组中。如果不需要使用匹配的文本而只是想知道是否存在一个匹配，你可以不使用第三个参数并留空。\r\n */\r\n\r\n//简而言之，格式如下，其中$matches是可选的：\r\n$result=preg_match($pattern,$subject,$matches);\r\n\r\n\r\n\r\n\r\n?&gt;</pre>\r\n&nbsp;', '在php中使用正则表达式', '', 'publish', 'open', 'open', '', '%e5%9c%a8php%e4%b8%ad%e4%bd%bf%e7%94%a8%e6%ad%a3%e5%88%99%e8%a1%a8%e8%be%be%e5%bc%8f', '', '', '2017-09-02 01:21:03', '2017-09-01 17:21:03', '', 0, 'http://luchanglong.com.cn/?p=386', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (387, 1, '2017-09-02 01:21:03', '2017-09-01 17:21:03', '<pre class=\"lang:php decode:true \">&lt;?php\r\n/**\r\n * php 正则表达式\r\n * 在php中想使用正则表达式，请先确认已经安装pcre扩展，可以输出phpinfo查看\r\n * author： mrluchanglong@163.com\r\n * 201709\r\n * */\r\n\r\n/**\r\n * 一个正则表达式通常使用如下格式：\r\n * &lt;delimiter&gt;&lt;pattern&gt;&lt;delimiter&gt;[&lt;modifiers&gt;]\r\n * &lt;定界符&gt;&lt;匹配式&gt;&lt;定界符&gt;[修饰符（可选）]\r\n * 修饰符是可选的。界定符把正则式与修饰符分离开来。PCRE将正则表达式的第一个字符作为定界符。\r\n * 所以你应该使用一个不会出现在正则式本身中的字符。\r\n * 或者，你可以使用一个在表达式中存在的字符，但是你必须使用\\对它进行转义.\r\n * 传统来说，字符/被用来当作定界符，但是你也可以选择其他常用的定界符，例如|或者@。\r\n * 从个人角度来说，大多数情况下，我们会选择@，除非我们需要匹配一个Email地址或者类似的包含@的正则式，那种情况下我们将用/。\r\n *\r\n * php函数preg_match()被用来匹配正则表达式。\r\n * 传递到函数的第一个参数式正则式。\r\n * 第二个参数是需要跟正则式匹配的字符串而且它也叫做标题。函数返回TRUE（正则式匹配）或者FALSE（正则式不匹配）。\r\n * 你还可以传递第三个参数————一个变量名。匹配的文本将通过引用存放到这个名字命名的数组中。如果不需要使用匹配的文本而只是想知道是否存在一个匹配，你可以不使用第三个参数并留空。\r\n */\r\n\r\n//简而言之，格式如下，其中$matches是可选的：\r\n$result=preg_match($pattern,$subject,$matches);\r\n\r\n\r\n\r\n\r\n?&gt;</pre>\r\n&nbsp;', '在php中使用正则表达式', '', 'inherit', 'closed', 'closed', '', '386-revision-v1', '', '', '2017-09-02 01:21:03', '2017-09-01 17:21:03', '', 386, 'http://luchanglong.com.cn/?p=387', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (388, 1, '2017-09-06 11:53:39', '2017-09-06 03:53:39', '在mysql命令行或者客户端管理工具中执行：SHOW VARIABLES LIKE \"general_log%\";\r\n\r\n结果：\r\n\r\ngeneral_log OFF\r\ngeneral_log_file /var/lib/mysql/localhost.log\r\n\r\nOFF说明没有开启日志记录\r\n\r\n分别执行开启日志以及日志路径和日志文件名\r\n\r\nSET GLOBAL general_log_file = \'/var/lib/mysql/localhost.log\';\r\nSET GLOBAL general_log = \'ON\';\r\n\r\n还要注意\r\n\r\n这时执行的所有sql都会别记录下来，方便查看，但是如果重启mysql就会停止记录需要重新设置\r\n\r\nSHOW VARIABLES LIKE \"log_output%\";\r\n\r\n如果是NONE，需要设置\r\n\r\nSET GLOBAL log_output=\'TABLE,FILE\'', '开启mysql执行日志', '', 'publish', 'open', 'open', '', '%e5%bc%80%e5%90%afmysql%e6%89%a7%e8%a1%8c%e6%97%a5%e5%bf%97', '', '', '2017-09-06 11:53:59', '2017-09-06 03:53:59', '', 0, 'http://luchanglong.com.cn/?p=388', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (389, 1, '2017-09-06 11:53:39', '2017-09-06 03:53:39', '在mysql命令行或者客户端管理工具中执行：SHOW VARIABLES LIKE \"general_log%\";\r\n\r\n结果：\r\n\r\ngeneral_log OFF\r\ngeneral_log_file /var/lib/mysql/localhost.log\r\n\r\nOFF说明没有开启日志记录\r\n\r\n分别执行开启日志以及日志路径和日志文件名\r\n\r\nSET GLOBAL general_log_file = \'/var/lib/mysql/localhost.log\';\r\nSET GLOBAL general_log = \'ON\';\r\n\r\n还要注意\r\n\r\n这时执行的所有sql都会别记录下来，方便查看，但是如果重启mysql就会停止记录需要重新设置\r\n\r\nSHOW VARIABLES LIKE \"log_output%\";\r\n\r\n如果是NONE，需要设置\r\n\r\nSET GLOBAL log_output=\'TABLE,FILE\'', '开启mysql执行日志', '', 'inherit', 'closed', 'closed', '', '388-revision-v1', '', '', '2017-09-06 11:53:39', '2017-09-06 03:53:39', '', 388, 'http://luchanglong.com.cn/?p=389', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (390, 1, '2017-09-08 12:08:05', '2017-09-08 04:08:05', '安装\r\n\r\nsudo apt-get install apache2-utils\r\n\r\n测试：\r\n\r\nab -c 20 -n 1000 http://www.example.com/\r\n\r\n-c：并发数\r\n-n：总请求数\r\n\r\nab运行完成后会有比较详细的结果\r\n<code></code>\r\n<pre><code>Server Software:        web服务器软件及版本\r\nServer Hostname:        请求的地址\r\nServer Port:            请求的端口\r\n\r\nDocument Path:          请求的页面路径\r\nDocument Length:        页面大小\r\n\r\nConcurrency Level:      并发数\r\nTime taken for tests:   测试总共花费的时间\r\nComplete requests:      完成的请求数\r\nFailed requests:        失败的请求数\r\nWrite errors:           写入错误\r\nTotal transferred:      总共传输字节数，包含http的头信息等\r\nHTML transferred:       html字节数，实际的页面传递字节数\r\nRequests per second:    每秒处理的请求数，服务器的吞吐量（重要）\r\nTime per request:       平均数，用户平均请求等待时间\r\nTime per request:       服务器平均处理时间\r\nTransfer rate:          平均传输速率（每秒收到的速率）</code></pre>\r\n参数说明：\r\n\r\n-n即requests，用于指定压力测试总共的执行次数。\r\n\r\n-c即concurrency，用于指定<span id=\"3_nwp\">压力测试的并发数。</span>\r\n\r\n-t即timelimit，等待响应的最大时间(单位：秒)。\r\n\r\n-b即windowsize，TCP发送/接收的缓冲大小(单位：字节)。\r\n\r\n-p即postfile，发送POST请求时需要上传的文件，此外还必须设置<code>-T</code>参数。\r\n\r\n-u即putfile，发送PUT请求时需要上传的文件，此外还必须设置<code>-T</code>参数。\r\n\r\n-T即content-type，用于设置Content-Type请求头信息，例如：<code>application/x-www-form-urlencoded</code>，默认值为<code>text/plain</code>。\r\n\r\n-v即verbosity，指定打印帮助信息的冗余级别。\r\n\r\n-w以HTML表格形式打印结果。-i使用HEAD请求代替GET请求。\r\n\r\n-x插入字符串作为table标签的属性。-y插入字符串作为tr标签的属性。\r\n\r\n-z插入字符串作为td标签的属性。\r\n\r\n-C添加cookie信息，例如：\"Apache=1234\"(可以重复该参数选项以添加多个)。\r\n\r\n-H添加任意的请求头，例如：\"Accept-Encoding: gzip\"，请求头将会添加在现有的多个请求头之后(可以重复该参数选项以添加多个)。\r\n\r\n-A添加一个基本的网络认证信息，用户名和密码之间用英文冒号隔开。\r\n\r\n-P添加一个基本的代理认证信息，用户名和密码之间用英文冒号隔开。\r\n\r\n-X指定使用的<span id=\"2_nwp\">代理服务器和端口号，例如:\"126.10.10.3:88\"。</span>\r\n\r\n-V打印版本号并退出。\r\n\r\n-k使用HTTP的KeepAlive特性。\r\n\r\n-d不显示百分比。\r\n\r\n-S不显示预估和警告信息。\r\n\r\n-g输出结果信息到gnuplot格式的文件中。\r\n\r\n-e输出结果信息到CSV格式的文件中。\r\n\r\n-r指定接收到错误信息时不退出程序。\r\n\r\n-h显示用法信息，其实就是<code>ab -help</code>。', 'ubuntu使用ab测试', '', 'publish', 'open', 'open', '', 'ubuntu%e4%bd%bf%e7%94%a8ab%e6%b5%8b%e8%af%95', '', '', '2017-09-08 16:09:21', '2017-09-08 08:09:21', '', 0, 'http://luchanglong.com.cn/?p=390', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (391, 1, '2017-09-08 12:08:05', '2017-09-08 04:08:05', '安装\r\n\r\nsudo apt-get install apache2-utils\r\n\r\n测试：\r\n\r\nab -c 20 -n 1000 http://www.example.com/\r\n\r\n-c：并发数\r\n-n：总请求数\r\n\r\nab运行完成后会有比较详细的结果\r\n<code></code>\r\n<pre><code>Server Software:        web服务器软件及版本\r\nServer Hostname:        请求的地址\r\nServer Port:            请求的端口\r\n\r\nDocument Path:          请求的页面路径\r\nDocument Length:        页面大小\r\n\r\nConcurrency Level:      并发数\r\nTime taken for tests:   测试总共花费的时间\r\nComplete requests:      完成的请求数\r\nFailed requests:        失败的请求数\r\nWrite errors:           写入错误\r\nTotal transferred:      总共传输字节数，包含http的头信息等\r\nHTML transferred:       html字节数，实际的页面传递字节数\r\nRequests per second:    每秒处理的请求数，服务器的吞吐量（重要）\r\nTime per request:       平均数，用户平均请求等待时间\r\nTime per request:       服务器平均处理时间\r\nTransfer rate:          平均传输速率（每秒收到的速率）</code></pre>', 'ubuntu使用ab测试', '', 'inherit', 'closed', 'closed', '', '390-revision-v1', '', '', '2017-09-08 12:08:05', '2017-09-08 04:08:05', '', 390, 'http://luchanglong.com.cn/?p=391', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (392, 1, '2017-09-08 14:28:37', '2017-09-08 06:28:37', '<pre class=\"lang:mysql decode:true  \">#创建生成测试数据的存储过程\r\nDROP PROCEDURE IF EXISTS test;\r\nCREATE PROCEDURE test() \r\nBEGIN \r\nDECLARE i INT DEFAULT 0; \r\nSET autocommit = 0; \r\nWHILE i&lt;100000 DO \r\n\r\nINSERT INTO `test`.`user` (`uid`, `task_id`, `publisher_id`, `expired_at`, `update_at`) \r\nVALUES ( 450+i, \'125\', \'1\', \'2\',\'2017-09-08 14:42:23\', \'2017-09-08 13:42:30\');\r\n\r\nSET i = i+1; \r\nIF i%1000 = 0 THEN \r\nCOMMIT; \r\nEND IF; \r\nEND WHILE; \r\nEND;\r\n\r\n#执行存储过程生成测试数据\r\nCALL test();</pre>\r\n&nbsp;', 'mysql使用存储过程重复/批量 插入测试数据', '', 'publish', 'open', 'open', '', 'mysql%e4%bd%bf%e7%94%a8%e5%ad%98%e5%82%a8%e8%bf%87%e7%a8%8b%e9%87%8d%e5%a4%8d%e6%89%b9%e9%87%8f-%e6%8f%92%e5%85%a5%e6%b5%8b%e8%af%95%e6%95%b0%e6%8d%ae', '', '', '2017-09-08 14:28:37', '2017-09-08 06:28:37', '', 0, 'http://luchanglong.com.cn/?p=392', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (393, 1, '2017-09-08 14:28:37', '2017-09-08 06:28:37', '<pre class=\"lang:mysql decode:true  \">#创建生成测试数据的存储过程\r\nDROP PROCEDURE IF EXISTS test;\r\nCREATE PROCEDURE test() \r\nBEGIN \r\nDECLARE i INT DEFAULT 0; \r\nSET autocommit = 0; \r\nWHILE i&lt;100000 DO \r\n\r\nINSERT INTO `test`.`user` (`uid`, `task_id`, `publisher_id`, `expired_at`, `update_at`) \r\nVALUES ( 450+i, \'125\', \'1\', \'2\',\'2017-09-08 14:42:23\', \'2017-09-08 13:42:30\');\r\n\r\nSET i = i+1; \r\nIF i%1000 = 0 THEN \r\nCOMMIT; \r\nEND IF; \r\nEND WHILE; \r\nEND;\r\n\r\n#执行存储过程生成测试数据\r\nCALL test();</pre>\r\n&nbsp;', 'mysql使用存储过程重复/批量 插入测试数据', '', 'inherit', 'closed', 'closed', '', '392-revision-v1', '', '', '2017-09-08 14:28:37', '2017-09-08 06:28:37', '', 392, 'http://luchanglong.com.cn/?p=393', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (394, 1, '2017-09-08 16:08:36', '2017-09-08 08:08:36', '安装\n\nsudo apt-get install apache2-utils\n\n测试：\n\nab -c 20 -n 1000 http://www.example.com/\n\n-c：并发数\n-n：总请求数\n\nab运行完成后会有比较详细的结果\n<code></code>\n<pre><code>Server Software:        web服务器软件及版本\nServer Hostname:        请求的地址\nServer Port:            请求的端口\n\nDocument Path:          请求的页面路径\nDocument Length:        页面大小\n\nConcurrency Level:      并发数\nTime taken for tests:   测试总共花费的时间\nComplete requests:      完成的请求数\nFailed requests:        失败的请求数\nWrite errors:           写入错误\nTotal transferred:      总共传输字节数，包含http的头信息等\nHTML transferred:       html字节数，实际的页面传递字节数\nRequests per second:    每秒处理的请求数，服务器的吞吐量（重要）\nTime per request:       平均数，用户平均请求等待时间\nTime per request:       服务器平均处理时间\nTransfer rate:          平均传输速率（每秒收到的速率）</code></pre>\n参数说明：\n\n-n即requests，用于指定压力测试总共的执行次数。\n\n-c即concurrency，用于指定<span id=\"3_nwp\">压力测试的并发数。</span>\n\n-t即timelimit，等待响应的最大时间(单位：秒)。\n\n-b即windowsize，TCP发送/接收的缓冲大小(单位：字节)。\n\n-p即postfile，发送POST请求时需要上传的文件，此外还必须设置<code>-T</code>参数。\n\n-u即putfile，发送PUT请求时需要上传的文件，此外还必须设置<code>-T</code>参数。\n\n-T即content-type，用于设置Content-Type请求头信息，例如：<code>application/x-www-form-urlencoded</code>，默认值为<code>text/plain</code>。\n\n-v即verbosity，指定打印帮助信息的冗余级别。\n\n-w以HTML表格形式打印结果。-i使用HEAD请求代替GET请求。\n\n-x插入字符串作为table标签的属性。-y插入字符串作为tr标签的属性。\n\n-z插入字符串作为td标签的属性。\n\n-C添加cookie信息，例如：\"Apache=1234\"(可以重复该参数选项以添加多个)。\n\n-H添加任意的请求头，例如：\"Accept-Encoding: gzip\"，请求头将会添加在现有的多个请求头之后(可以重复该参数选项以添加多个)。\n\n-A添加一个基本的网络认证信息，用户名和密码之间用英文冒号隔开。\n\n-P添加一个基本的代理认证信息，用户名和密码之间用英文冒号隔开。-X指定使用的<span id=\"2_nwp\"><a id=\"2_nwl\" href=\"http://cpro.baidu.com/cpro/ui/uijs.php?adclass=0&amp;app_id=0&amp;c=news&amp;cf=46&amp;ch=0&amp;di=128&amp;fv=19&amp;is_app=0&amp;jk=1d4988a9148e80f1&amp;k=%B4%FA%C0%ED%B7%FE%CE%F1%C6%F7&amp;k0=%B4%FA%C0%ED%B7%FE%CE%F1%C6%F7&amp;kdi0=0&amp;luki=1&amp;mcpm=0&amp;n=10&amp;p=baidu&amp;q=97086099_cpr&amp;rb=0&amp;rs=1&amp;seller_id=1&amp;sid=f1808e14a988491d&amp;ssp2=1&amp;stid=0&amp;t=tpclicked3_hc&amp;td=1691595&amp;tu=u1691595&amp;u=http%3A%2F%2Fwww%2E365mini%2Ecom%2Fpage%2Fapache%2Dbenchmark%2Ehtm&amp;urlid=0\" target=\"_blank\" rel=\"noopener noreferrer\">代理服务器</a>和端口号，例如:\"126.10.10.3:88\"。</span>-V打印版本号并退出。-k使用HTTP的KeepAlive特性。-k使用HTTP的KeepAlive特性。-d不显示百分比。-S不显示预估和警告信息。-g输出结果信息到gnuplot格式的文件中。-e输出结果信息到CSV格式的文件中。-r指定接收到错误信息时不退出程序。-h显示用法信息，其实就是<code>ab -help</code>。', 'ubuntu使用ab测试', '', 'inherit', 'closed', 'closed', '', '390-autosave-v1', '', '', '2017-09-08 16:08:36', '2017-09-08 08:08:36', '', 390, 'http://luchanglong.com.cn/?p=394', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (395, 1, '2017-09-08 16:09:21', '2017-09-08 08:09:21', '安装\r\n\r\nsudo apt-get install apache2-utils\r\n\r\n测试：\r\n\r\nab -c 20 -n 1000 http://www.example.com/\r\n\r\n-c：并发数\r\n-n：总请求数\r\n\r\nab运行完成后会有比较详细的结果\r\n<code></code>\r\n<pre><code>Server Software:        web服务器软件及版本\r\nServer Hostname:        请求的地址\r\nServer Port:            请求的端口\r\n\r\nDocument Path:          请求的页面路径\r\nDocument Length:        页面大小\r\n\r\nConcurrency Level:      并发数\r\nTime taken for tests:   测试总共花费的时间\r\nComplete requests:      完成的请求数\r\nFailed requests:        失败的请求数\r\nWrite errors:           写入错误\r\nTotal transferred:      总共传输字节数，包含http的头信息等\r\nHTML transferred:       html字节数，实际的页面传递字节数\r\nRequests per second:    每秒处理的请求数，服务器的吞吐量（重要）\r\nTime per request:       平均数，用户平均请求等待时间\r\nTime per request:       服务器平均处理时间\r\nTransfer rate:          平均传输速率（每秒收到的速率）</code></pre>\r\n参数说明：\r\n\r\n-n即requests，用于指定压力测试总共的执行次数。\r\n\r\n-c即concurrency，用于指定<span id=\"3_nwp\">压力测试的并发数。</span>\r\n\r\n-t即timelimit，等待响应的最大时间(单位：秒)。\r\n\r\n-b即windowsize，TCP发送/接收的缓冲大小(单位：字节)。\r\n\r\n-p即postfile，发送POST请求时需要上传的文件，此外还必须设置<code>-T</code>参数。\r\n\r\n-u即putfile，发送PUT请求时需要上传的文件，此外还必须设置<code>-T</code>参数。\r\n\r\n-T即content-type，用于设置Content-Type请求头信息，例如：<code>application/x-www-form-urlencoded</code>，默认值为<code>text/plain</code>。\r\n\r\n-v即verbosity，指定打印帮助信息的冗余级别。\r\n\r\n-w以HTML表格形式打印结果。-i使用HEAD请求代替GET请求。\r\n\r\n-x插入字符串作为table标签的属性。-y插入字符串作为tr标签的属性。\r\n\r\n-z插入字符串作为td标签的属性。\r\n\r\n-C添加cookie信息，例如：\"Apache=1234\"(可以重复该参数选项以添加多个)。\r\n\r\n-H添加任意的请求头，例如：\"Accept-Encoding: gzip\"，请求头将会添加在现有的多个请求头之后(可以重复该参数选项以添加多个)。\r\n\r\n-A添加一个基本的网络认证信息，用户名和密码之间用英文冒号隔开。\r\n\r\n-P添加一个基本的代理认证信息，用户名和密码之间用英文冒号隔开。\r\n\r\n-X指定使用的<span id=\"2_nwp\">代理服务器和端口号，例如:\"126.10.10.3:88\"。</span>\r\n\r\n-V打印版本号并退出。\r\n\r\n-k使用HTTP的KeepAlive特性。\r\n\r\n-d不显示百分比。\r\n\r\n-S不显示预估和警告信息。\r\n\r\n-g输出结果信息到gnuplot格式的文件中。\r\n\r\n-e输出结果信息到CSV格式的文件中。\r\n\r\n-r指定接收到错误信息时不退出程序。\r\n\r\n-h显示用法信息，其实就是<code>ab -help</code>。', 'ubuntu使用ab测试', '', 'inherit', 'closed', 'closed', '', '390-revision-v1', '', '', '2017-09-08 16:09:21', '2017-09-08 08:09:21', '', 390, 'http://luchanglong.com.cn/?p=395', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (397, 1, '2017-09-10 00:44:32', '2017-09-09 16:44:32', '压力测试：\r\n\r\nphp的压力测试一般有两种方法法\r\n\r\n1.ab工具 。ApacheBench的缩写。它是绑定到apache中的，也可以单独安装。\r\n\r\n$ab -n 1000 -c 10  http://localhost/index.php\r\n\r\n2.Siege。可以一个测试多个地址。\r\n\r\n$ siege -i -t 10S -f urls.txt\r\n\r\n&nbsp;\r\n\r\n性能分析：\r\n\r\n1.ZendStudio 的Profiler来进行性能分析\r\n\r\n2.使用APD（Advanced PHP Debugger）进行性能分析。一般来说用的就是这个。\r\n\r\n3.使用Xdebug进行性能分析。这个比较偏向于代码调试。当然如果配置了Ccachegrind还是很好用的。\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n.使用APC（Advanced PHP Cache）。opcache 可以大大的加快你php的运行速度。\r\n\r\n&nbsp;', 'php压力测试和性能分析工具介绍', '', 'publish', 'open', 'open', '', 'php%e5%8e%8b%e5%8a%9b%e6%b5%8b%e8%af%95%e5%92%8c%e6%80%a7%e8%83%bd%e5%88%86%e6%9e%90', '', '', '2017-09-10 00:57:18', '2017-09-09 16:57:19', '', 0, 'http://luchanglong.com.cn/?p=397', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (398, 1, '2017-09-10 00:44:32', '2017-09-09 16:44:32', '压力测试：\r\n\r\nphp的压力测试一般有两种方法法\r\n\r\n1.ab工具 。ApacheBench的缩写。它是绑定到apache中的，也可以单独安装。\r\n\r\n$ab -n 1000 -c 10  http://localhost/index.php\r\n\r\n2.Siege。可以一个测试多个地址。\r\n\r\n$ siege -i -t 10S -f urls.txt\r\n\r\n&nbsp;\r\n\r\n性能分析：\r\n\r\n1.ZendStudio 的Profiler来进行性能分析\r\n\r\n2.使用APD（Advanced PHP Debugger）进行性能分析。一般来说用的就是这个。\r\n\r\n3.使用Xdebug进行性能分析。这个比较偏向于代码调试。当然如果配置了Ccachegrind还是很好用的。\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n.使用APC（Advanced PHP Cache）。opcache 可以大大的加快你php的运行速度。\r\n\r\n&nbsp;', 'php压力测试和性能分析', '', 'inherit', 'closed', 'closed', '', '397-revision-v1', '', '', '2017-09-10 00:44:32', '2017-09-09 16:44:32', '', 397, 'http://luchanglong.com.cn/?p=398', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (399, 1, '2017-09-10 00:57:18', '2017-09-09 16:57:19', '压力测试：\r\n\r\nphp的压力测试一般有两种方法法\r\n\r\n1.ab工具 。ApacheBench的缩写。它是绑定到apache中的，也可以单独安装。\r\n\r\n$ab -n 1000 -c 10  http://localhost/index.php\r\n\r\n2.Siege。可以一个测试多个地址。\r\n\r\n$ siege -i -t 10S -f urls.txt\r\n\r\n&nbsp;\r\n\r\n性能分析：\r\n\r\n1.ZendStudio 的Profiler来进行性能分析\r\n\r\n2.使用APD（Advanced PHP Debugger）进行性能分析。一般来说用的就是这个。\r\n\r\n3.使用Xdebug进行性能分析。这个比较偏向于代码调试。当然如果配置了Ccachegrind还是很好用的。\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n.使用APC（Advanced PHP Cache）。opcache 可以大大的加快你php的运行速度。\r\n\r\n&nbsp;', 'php压力测试和性能分析工具介绍', '', 'inherit', 'closed', 'closed', '', '397-revision-v1', '', '', '2017-09-10 00:57:18', '2017-09-09 16:57:19', '', 397, 'http://luchanglong.com.cn/?p=399', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (400, 1, '2017-09-10 02:38:15', '2017-09-09 18:38:15', '在类UNIX的系统中，PHP（除了CLI模式）默认在/usr/local/lib路径寻找php.ini文件。为了让它更加“shell式”，CLI模式默认寻找/etc/php-cli.ini，用做代替。这可以让你的web服务器和CLI/shell脚本保持单独的php.ini文件，而无需再你每次运行一个php的脚本时设置-c选项。\r\n\r\n不同的Unix/linux系统发布版绑定PHP时，经常使用它们自己默认的php.ini位置；你可以通过你的PHP执行文件加上get_cfg_var(\"cfg_file_path\")来找到该文件。', 'php.ini文件位置', '', 'publish', 'open', 'open', '', 'php-ini%e6%96%87%e4%bb%b6%e4%bd%8d%e7%bd%ae', '', '', '2017-09-10 02:38:15', '2017-09-09 18:38:15', '', 0, 'http://luchanglong.com.cn/?p=400', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (401, 1, '2017-09-10 02:38:15', '2017-09-09 18:38:15', '在类UNIX的系统中，PHP（除了CLI模式）默认在/usr/local/lib路径寻找php.ini文件。为了让它更加“shell式”，CLI模式默认寻找/etc/php-cli.ini，用做代替。这可以让你的web服务器和CLI/shell脚本保持单独的php.ini文件，而无需再你每次运行一个php的脚本时设置-c选项。\r\n\r\n不同的Unix/linux系统发布版绑定PHP时，经常使用它们自己默认的php.ini位置；你可以通过你的PHP执行文件加上get_cfg_var(\"cfg_file_path\")来找到该文件。', 'php.ini文件位置', '', 'inherit', 'closed', 'closed', '', '400-revision-v1', '', '', '2017-09-10 02:38:15', '2017-09-09 18:38:15', '', 400, 'http://luchanglong.com.cn/?p=401', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (403, 1, '2017-09-10 23:01:11', '2017-09-10 15:01:11', 'printf(\"%3d %6.1f\",a,b);\r\n\r\n第一个%表示对应的第一个参数a\r\n\r\n第二个%表示对应的第二个参数b\r\n\r\n3d表示第一个参数保留三个位置的数字\r\n\r\n6.1f 表示保留6个位置的浮点书，并且保留1位小数\r\n\r\n对应说明：\r\n\r\n%d 按照十进制整型数打印\r\n\r\n%6d 按照十进制整型数打印，至少6个字符宽度\r\n\r\n%f 按照浮点数打印\r\n\r\n%6f 按照浮点数打印，至少6个字符宽度\r\n\r\n%.2f按照浮点数打印，小数点后有两位小数\r\n\r\n%6.2f 按照浮点数打印，至少6个字符宽度，小数点后有两位小数\r\n\r\n&nbsp;\r\n\r\n%O 表示八进制\r\n\r\n%X 表示十六进制\r\n\r\n%C 表示字符\r\n\r\n%s 表示字符串\r\n\r\n%% 表示百分号（%）本身\r\n\r\n&nbsp;\r\n\r\n&nbsp;', 'c中标准输入输出库中函数printf说明', '', 'publish', 'open', 'open', '', 'c%e4%b8%ad%e6%a0%87%e5%87%86%e8%be%93%e5%85%a5%e8%be%93%e5%87%ba%e5%ba%93%e4%b8%ad%e5%87%bd%e6%95%b0printf%e8%af%b4%e6%98%8e', '', '', '2017-09-10 23:01:11', '2017-09-10 15:01:11', '', 0, 'http://luchanglong.com.cn/?p=403', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (404, 1, '2017-09-10 23:01:11', '2017-09-10 15:01:11', 'printf(\"%3d %6.1f\",a,b);\r\n\r\n第一个%表示对应的第一个参数a\r\n\r\n第二个%表示对应的第二个参数b\r\n\r\n3d表示第一个参数保留三个位置的数字\r\n\r\n6.1f 表示保留6个位置的浮点书，并且保留1位小数\r\n\r\n对应说明：\r\n\r\n%d 按照十进制整型数打印\r\n\r\n%6d 按照十进制整型数打印，至少6个字符宽度\r\n\r\n%f 按照浮点数打印\r\n\r\n%6f 按照浮点数打印，至少6个字符宽度\r\n\r\n%.2f按照浮点数打印，小数点后有两位小数\r\n\r\n%6.2f 按照浮点数打印，至少6个字符宽度，小数点后有两位小数\r\n\r\n&nbsp;\r\n\r\n%O 表示八进制\r\n\r\n%X 表示十六进制\r\n\r\n%C 表示字符\r\n\r\n%s 表示字符串\r\n\r\n%% 表示百分号（%）本身\r\n\r\n&nbsp;\r\n\r\n&nbsp;', 'c中标准输入输出库中函数printf说明', '', 'inherit', 'closed', 'closed', '', '403-revision-v1', '', '', '2017-09-10 23:01:11', '2017-09-10 15:01:11', '', 403, 'http://luchanglong.com.cn/?p=404', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (405, 1, '2017-09-14 14:13:41', '2017-09-14 06:13:41', 'echo sprintf(”%05d”,1);\r\n先说%05d的意思，用一个5位数的数字格式化后边的参数，如果不足5位就补零', 'php 补位，位数不足补0', '', 'publish', 'open', 'open', '', 'php-%e8%a1%a5%e4%bd%8d%ef%bc%8c%e4%bd%8d%e6%95%b0%e4%b8%8d%e8%b6%b3%e8%a1%a50', '', '', '2017-09-14 14:13:41', '2017-09-14 06:13:41', '', 0, 'http://luchanglong.com.cn/?p=405', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (406, 1, '2017-09-14 14:13:41', '2017-09-14 06:13:41', 'echo sprintf(”%05d”,1);\r\n先说%05d的意思，用一个5位数的数字格式化后边的参数，如果不足5位就补零', 'php 补位，位数不足补0', '', 'inherit', 'closed', 'closed', '', '405-revision-v1', '', '', '2017-09-14 14:13:41', '2017-09-14 06:13:41', '', 405, 'http://luchanglong.com.cn/?p=406', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (409, 1, '2017-09-14 19:25:34', '2017-09-14 11:25:34', '<pre class=\"lang:php decode:true \">/**\r\n     * 缓存所有商品分类\r\n     * @return mixed\r\n     */\r\n    static function findAllCache()\r\n    {\r\n        //开发期间不缓存,直接返回所有分类\r\n        return $goodsCate = self::select(\'*\')-&gt;orderBy(\'pid\', \'ASC\')-&gt;orderBy(\'sort\', \'ASC\')-&gt;get()-&gt;toArray();\r\n\r\n        /* if(Cache::has(\'goods_cate\')){\r\n             $goodsCate = Cache::get(\'goods_cate\');\r\n         }else{\r\n             $goodsCate = self::select(\'*\')-&gt;orderBy(\'pid\', \'ASC\')-&gt;orderBy(\'sort\', \'ASC\')-&gt;get()-&gt;toArray();\r\n             Cache::put(\'goods_cate\',$goodsCate,1*24);\r\n         }\r\n         return $goodsCate;*/\r\n    }\r\n\r\n    /**\r\n     * 所有分类=&gt;树形数组\r\n     * 将查询到的所有分类转换成树形返回\r\n     * @param array  $cate 所有分类，可以使用findAllCache() 查询\r\n     * @param int $pid 初始父级\r\n     * @return mixed\r\n     */\r\n    static function getTree(array $cate,$pid=0){\r\n        $result = array();\r\n        foreach($cate as $v){\r\n            if($v[\'pid\'] == $pid){\r\n                $v[\'children\'] =self::getTree($cate,$v[\'id\']);\r\n                $result[] = $v;\r\n            }\r\n        }\r\n        return $result;\r\n    }\r\n    /**\r\n     * 一维数组=&gt;树形带option数组\r\n     * 返回options递归信息\r\n     * @param array $data 全部分类\r\n     * @param int $pid\r\n     * @param int $level\r\n     * @return string\r\n     */\r\n    static function getTreeOption($data,$pid = 0,$level=0){\r\n        $result = \'\';\r\n        $j=0;\r\n        foreach($data as $v){\r\n            if($v[\'pid\'] == $pid){\r\n                $delimit=\'\';\r\n                if($level){\r\n                    for($i=1;$i&lt;=$level;$i++){\r\n                        $delimit .= \'&amp;nbsp;&amp;nbsp;\';\r\n                    }\r\n                } else {\r\n                    $delimit = \'\';\r\n                }\r\n                $result .= \'&lt;option value=\"\'.$v[\'id\'].\'\"&gt;\'.$delimit.$v[\'title\'].\'&lt;/option&gt;\';\r\n                $result .= self::getTreeOption($data,$v[\'id\'],$level+1);\r\n            }\r\n        }\r\n        return $result;\r\n    }\r\n    /**\r\n     * 所有分类=&gt;顶级分类\r\n     * 将查询到的所有分类转换成顶级一维html option返回\r\n     * @param array  $data 所有分类\r\n     * @param int $pid 初始父级\r\n     * @return mixed\r\n     */\r\n    static function getParentHtml($data,$pid = 0,$level=0,$delimit = \'\'){\r\n        $result = array();\r\n        foreach($data as $v){\r\n            if($v[\'pid\'] == $pid){\r\n                if($level){\r\n                    for($i=1;$i&lt;=$level;$i++){\r\n                        $delimit .= \'&amp;nbsp;&amp;nbsp;\';\r\n                    }\r\n                } else {\r\n                    $delimit = \'\';\r\n                }\r\n                $result[] = \'&lt;option value=\"\'.$v[\'id\'].\'\"&gt;\'.$delimit.$v[\'title\'].\'&lt;/option&gt;\';\r\n                $res = self::getTreeOption($data,$v[\'id\'],$level+1);\r\n                if(is_array($res)){\r\n                    foreach($res as $v2){\r\n                        $result[] = $v2;\r\n                    }\r\n                }\r\n            }\r\n        }\r\n        return $result;\r\n    }\r\n\r\n</pre>\r\n&nbsp;', 'PHP 分类无限级，二维数组转树形，二维数组转树形html输出，二维数组获取顶级分类', '', 'publish', 'open', 'open', '', 'php-%e5%88%86%e7%b1%bb%e6%97%a0%e9%99%90%e7%ba%a7%ef%bc%8c%e4%ba%8c%e7%bb%b4%e6%95%b0%e7%bb%84%e8%bd%ac%e6%a0%91%e5%bd%a2%ef%bc%8c%e4%ba%8c%e7%bb%b4%e6%95%b0%e7%bb%84%e8%bd%ac%e6%a0%91%e5%bd%a2html', '', '', '2017-09-14 19:25:34', '2017-09-14 11:25:34', '', 0, 'http://luchanglong.com.cn/?p=409', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (410, 1, '2017-09-14 19:25:34', '2017-09-14 11:25:34', '<pre class=\"lang:php decode:true \">/**\r\n     * 缓存所有商品分类\r\n     * @return mixed\r\n     */\r\n    static function findAllCache()\r\n    {\r\n        //开发期间不缓存,直接返回所有分类\r\n        return $goodsCate = self::select(\'*\')-&gt;orderBy(\'pid\', \'ASC\')-&gt;orderBy(\'sort\', \'ASC\')-&gt;get()-&gt;toArray();\r\n\r\n        /* if(Cache::has(\'goods_cate\')){\r\n             $goodsCate = Cache::get(\'goods_cate\');\r\n         }else{\r\n             $goodsCate = self::select(\'*\')-&gt;orderBy(\'pid\', \'ASC\')-&gt;orderBy(\'sort\', \'ASC\')-&gt;get()-&gt;toArray();\r\n             Cache::put(\'goods_cate\',$goodsCate,1*24);\r\n         }\r\n         return $goodsCate;*/\r\n    }\r\n\r\n    /**\r\n     * 所有分类=&gt;树形数组\r\n     * 将查询到的所有分类转换成树形返回\r\n     * @param array  $cate 所有分类，可以使用findAllCache() 查询\r\n     * @param int $pid 初始父级\r\n     * @return mixed\r\n     */\r\n    static function getTree(array $cate,$pid=0){\r\n        $result = array();\r\n        foreach($cate as $v){\r\n            if($v[\'pid\'] == $pid){\r\n                $v[\'children\'] =self::getTree($cate,$v[\'id\']);\r\n                $result[] = $v;\r\n            }\r\n        }\r\n        return $result;\r\n    }\r\n    /**\r\n     * 一维数组=&gt;树形带option数组\r\n     * 返回options递归信息\r\n     * @param array $data 全部分类\r\n     * @param int $pid\r\n     * @param int $level\r\n     * @return string\r\n     */\r\n    static function getTreeOption($data,$pid = 0,$level=0){\r\n        $result = \'\';\r\n        $j=0;\r\n        foreach($data as $v){\r\n            if($v[\'pid\'] == $pid){\r\n                $delimit=\'\';\r\n                if($level){\r\n                    for($i=1;$i&lt;=$level;$i++){\r\n                        $delimit .= \'&amp;nbsp;&amp;nbsp;\';\r\n                    }\r\n                } else {\r\n                    $delimit = \'\';\r\n                }\r\n                $result .= \'&lt;option value=\"\'.$v[\'id\'].\'\"&gt;\'.$delimit.$v[\'title\'].\'&lt;/option&gt;\';\r\n                $result .= self::getTreeOption($data,$v[\'id\'],$level+1);\r\n            }\r\n        }\r\n        return $result;\r\n    }\r\n    /**\r\n     * 所有分类=&gt;顶级分类\r\n     * 将查询到的所有分类转换成顶级一维html option返回\r\n     * @param array  $data 所有分类\r\n     * @param int $pid 初始父级\r\n     * @return mixed\r\n     */\r\n    static function getParentHtml($data,$pid = 0,$level=0,$delimit = \'\'){\r\n        $result = array();\r\n        foreach($data as $v){\r\n            if($v[\'pid\'] == $pid){\r\n                if($level){\r\n                    for($i=1;$i&lt;=$level;$i++){\r\n                        $delimit .= \'&amp;nbsp;&amp;nbsp;\';\r\n                    }\r\n                } else {\r\n                    $delimit = \'\';\r\n                }\r\n                $result[] = \'&lt;option value=\"\'.$v[\'id\'].\'\"&gt;\'.$delimit.$v[\'title\'].\'&lt;/option&gt;\';\r\n                $res = self::getTreeOption($data,$v[\'id\'],$level+1);\r\n                if(is_array($res)){\r\n                    foreach($res as $v2){\r\n                        $result[] = $v2;\r\n                    }\r\n                }\r\n            }\r\n        }\r\n        return $result;\r\n    }\r\n\r\n</pre>\r\n&nbsp;', 'PHP 分类无限级，二维数组转树形，二维数组转树形html输出，二维数组获取顶级分类', '', 'inherit', 'closed', 'closed', '', '409-revision-v1', '', '', '2017-09-14 19:25:34', '2017-09-14 11:25:34', '', 409, 'http://luchanglong.com.cn/?p=410', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (411, 1, '2017-09-15 17:34:15', '2017-09-15 09:34:15', 'ubuntu系统下：\r\n\r\ncrontab记录日志\r\n\r\n修改rsyslog\r\n\r\nsudo vim /etc/rsyslog.d/50-default.conf\r\n\r\ncron.*              /var/log/cron.log #将cron前面的注释符去掉\r\n\r\n重启rsyslog\r\n\r\nsudo  service rsyslog  restart\r\n\r\n查看crontab日志\r\n\r\nless  /var/log/cron.log\r\n\r\ncrontab问题定位\r\n\r\n查看日志\r\n\r\n/var/log/cron.log 和 /var/mail/$user', 'crontab记录日志和查看日志', '', 'publish', 'open', 'open', '', 'crontab%e8%ae%b0%e5%bd%95%e6%97%a5%e5%bf%97%e5%92%8c%e6%9f%a5%e7%9c%8b%e6%97%a5%e5%bf%97', '', '', '2017-09-15 17:34:15', '2017-09-15 09:34:15', '', 0, 'http://luchanglong.com.cn/?p=411', 0, 'post', '', 0);
