INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (423, 1, '2017-09-30 10:45:27', '2017-09-30 02:45:27', '<img class=\"alignnone size-medium wp-image-420\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/09/111-900x195.png\" alt=\"\" width=\"900\" height=\"195\" />\r\n\r\nubuntu\r\n\r\n使用php5\r\nsudo apt-get install php5-curl\r\n\r\n使用php7.0\r\nsudo apt-get install php7.0-curl\r\n\r\n使用 php7.1\r\n\r\nsudo apt-get install php7.1-curl', '使用composer 安装时出现错误 requires ext-curl * -> the requested PHP extension curl is missing from your system', '', 'inherit', 'closed', 'closed', '', '419-revision-v1', '', '', '2017-09-30 10:45:27', '2017-09-30 02:45:27', '', 419, 'http://luchanglong.com.cn/?p=423', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (424, 1, '2017-09-30 10:49:01', '2017-09-30 02:49:01', '<img class=\"alignnone size-medium wp-image-420\" src=\"http://luchanglong.com.cn/wp-content/uploads/2017/09/111-900x195.png\" alt=\"\" width=\"900\" height=\"195\" />\r\n\r\n使用composer 安装时出现错误 requires ext-curl * -&gt; the requested PHP extension curl is missing from your system\r\n\r\nubuntu\r\n\r\n使用php5\r\nsudo apt-get install php5-curl\r\n\r\n使用php7.0\r\nsudo apt-get install php7.0-curl\r\n\r\n使用 php7.1\r\n\r\nsudo apt-get install php7.1-curl', '使用composer 安装时出现错误 requires ext-curl * -> the requested PHP extension curl is missing from your system', '', 'inherit', 'closed', 'closed', '', '419-revision-v1', '', '', '2017-09-30 10:49:01', '2017-09-30 02:49:01', '', 419, 'http://luchanglong.com.cn/?p=424', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (435, 1, '2017-11-28 11:46:21', '2017-11-28 03:46:21', '<pre class=\"lang:default decode:true  \">server {\r\n\r\n        listen 80;\r\n        listen [::]:80;\r\n        server_name xxxx.com;\r\n\r\n        rewrite ^(.*)$  https://$host$1 permanent;\r\n\r\n}\r\nserver {\r\n\r\n        listen 443 ssl;\r\n\r\n        server_name   xxx.com la998.com;\r\n\r\n        #rewrite ^(.*)$  https://$host$1 permanent;\r\n\r\n        ssl on;\r\n        ssl_certificate   cert/213972949720863.pem;\r\n        ssl_certificate_key  cert/213972949720863.key;\r\n        ssl_session_timeout 5m;\r\n        ssl_ciphers ECDHE-RSA-AES128-GCM-SHA256:ECDHE:ECDH:AES:HIGH:!NULL:!aNULL:!MD5:!ADH:!RC4;\r\n        ssl_protocols TLSv1 TLSv1.1 TLSv1.2;\r\n        ssl_prefer_server_ciphers on;\r\n\r\n        root /www/www.xxxxx.com;\r\n\r\n        index index.php index.html;\r\n\r\n        location / {\r\n                try_files $uri $uri/ =404;\r\n        }\r\n        location ~ \\.php$ {\r\n                include snippets/fastcgi-php.conf;\r\n\r\n                # With php7.0-cgi alone:\r\n                #fastcgi_pass 127.0.0.1:9000;\r\n                # With php7.0-fpm:\r\n                fastcgi_pass unix:/run/php/php7.0-fpm.sock;\r\n                fastcgi_param SCRIPT_FILENAME $document_root$fastcgi_script_name;\r\n                include fastcgi_params;\r\n        }\r\n        location ~ /\\.ht {\r\n                deny all;\r\n        }\r\n}\r\n</pre>\r\n&nbsp;', 'nginx  http强制跳转https配置文件', '', 'publish', 'open', 'open', '', 'nginx-http%e5%bc%ba%e5%88%b6%e8%b7%b3%e8%bd%achttps%e9%85%8d%e7%bd%ae%e6%96%87%e4%bb%b6', '', '', '2017-11-28 11:47:48', '2017-11-28 03:47:48', '', 0, 'http://luchanglong.com.cn/?p=435', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (436, 1, '2017-11-28 11:46:21', '2017-11-28 03:46:21', '<pre class=\"lang:default decode:true \">server {\r\n\r\n        listen 80;\r\n        listen [::]:80;\r\n        server_name la998.com www.la998.com;\r\n\r\n        rewrite ^(.*)$  https://$host$1 permanent;\r\n\r\n}\r\nserver {\r\n\r\n        listen 443 ssl;\r\n\r\n        server_name 120.24.97.36  www.la998.com la998.com www.phprule.com phprule.com;\r\n\r\n        #rewrite ^(.*)$  https://$host$1 permanent;\r\n\r\n        ssl on;\r\n        ssl_certificate   cert/213972949720863.pem;\r\n        ssl_certificate_key  cert/213972949720863.key;\r\n        ssl_session_timeout 5m;\r\n        ssl_ciphers ECDHE-RSA-AES128-GCM-SHA256:ECDHE:ECDH:AES:HIGH:!NULL:!aNULL:!MD5:!ADH:!RC4;\r\n        ssl_protocols TLSv1 TLSv1.1 TLSv1.2;\r\n        ssl_prefer_server_ciphers on;\r\n\r\n        root /www/la998;\r\n\r\n        index index.php index.html;\r\n\r\n        location / {\r\n                try_files $uri $uri/ =404;\r\n        }\r\n        location ~ \\.php$ {\r\n                include snippets/fastcgi-php.conf;\r\n\r\n                # With php7.0-cgi alone:\r\n                #fastcgi_pass 127.0.0.1:9000;\r\n                # With php7.0-fpm:\r\n                fastcgi_pass unix:/run/php/php7.0-fpm.sock;\r\n                fastcgi_param SCRIPT_FILENAME $document_root$fastcgi_script_name;\r\n                include fastcgi_params;\r\n        }\r\n        location ~ /\\.ht {\r\n                deny all;\r\n        }\r\n}\r\n</pre>\r\n&nbsp;', 'nginx  http强制跳转https配置文件', '', 'inherit', 'closed', 'closed', '', '435-revision-v1', '', '', '2017-11-28 11:46:21', '2017-11-28 03:46:21', '', 435, 'http://luchanglong.com.cn/?p=436', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (437, 1, '2017-11-28 11:47:48', '2017-11-28 03:47:48', '<pre class=\"lang:default decode:true  \">server {\r\n\r\n        listen 80;\r\n        listen [::]:80;\r\n        server_name xxxx.com;\r\n\r\n        rewrite ^(.*)$  https://$host$1 permanent;\r\n\r\n}\r\nserver {\r\n\r\n        listen 443 ssl;\r\n\r\n        server_name   xxx.com la998.com;\r\n\r\n        #rewrite ^(.*)$  https://$host$1 permanent;\r\n\r\n        ssl on;\r\n        ssl_certificate   cert/213972949720863.pem;\r\n        ssl_certificate_key  cert/213972949720863.key;\r\n        ssl_session_timeout 5m;\r\n        ssl_ciphers ECDHE-RSA-AES128-GCM-SHA256:ECDHE:ECDH:AES:HIGH:!NULL:!aNULL:!MD5:!ADH:!RC4;\r\n        ssl_protocols TLSv1 TLSv1.1 TLSv1.2;\r\n        ssl_prefer_server_ciphers on;\r\n\r\n        root /www/www.xxxxx.com;\r\n\r\n        index index.php index.html;\r\n\r\n        location / {\r\n                try_files $uri $uri/ =404;\r\n        }\r\n        location ~ \\.php$ {\r\n                include snippets/fastcgi-php.conf;\r\n\r\n                # With php7.0-cgi alone:\r\n                #fastcgi_pass 127.0.0.1:9000;\r\n                # With php7.0-fpm:\r\n                fastcgi_pass unix:/run/php/php7.0-fpm.sock;\r\n                fastcgi_param SCRIPT_FILENAME $document_root$fastcgi_script_name;\r\n                include fastcgi_params;\r\n        }\r\n        location ~ /\\.ht {\r\n                deny all;\r\n        }\r\n}\r\n</pre>\r\n&nbsp;', 'nginx  http强制跳转https配置文件', '', 'inherit', 'closed', 'closed', '', '435-revision-v1', '', '', '2017-11-28 11:47:48', '2017-11-28 03:47:48', '', 435, 'http://luchanglong.com.cn/?p=437', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (439, 1, '2017-11-30 15:07:34', '2017-11-30 07:07:34', '<pre class=\"lang:default decode:true  \">DROP PROCEDURE IF EXISTS  repair_order_brokerage_pay;\r\nCREATE PROCEDURE repair_order_brokerage_pay()\r\n  BEGIN\r\n    DECLARE error INTEGER DEFAULT 0;\r\n    DECLARE CONTINUE HANDLER FOR SQLEXCEPTION SET error=1;\r\n\r\n    START TRANSACTION;\r\n\r\n    /*******order_id=122*******/\r\n    /*修改订单售后服务状态,修改后会在下次定时任务时自动发放*/\r\n    UPDATE `xm_orders` SET `is_service_over`=\'0\' WHERE (`id`=\'122\') LIMIT 1;\r\n    /*已经执行了销量增加，把增加的销量重新减去,新版本代码将会修改这一做法，用户不发奖励将不修改售后状态和销量*/\r\n    UPDATE `xm_goods` SET `sales_volume`=sales_volume-2 WHERE (`id`=\'17\') LIMIT 1;\r\n    UPDATE `xm_goods` SET `sales_volume`=sales_volume-1 WHERE (`id`=\'16\') LIMIT 1;\r\n    UPDATE `xm_goods` SET `sales_volume`=sales_volume-1 WHERE (`id`=\'14\') LIMIT 1;\r\n \r\n    IF error = 1 THEN\r\n      ROLLBACK;\r\n    ELSE\r\n      COMMIT;\r\n    END IF;\r\n    select error;\r\n  END;\r\n\r\n#执行\r\nCALL repair_order_brokerage_pay();</pre>\r\n&nbsp;', 'mysql 存储过程中使用事务', '', 'publish', 'open', 'open', '', 'mysql-%e5%ad%98%e8%b4%ae%e8%bf%87%e7%a8%8b%e4%b8%ad%e4%bd%bf%e7%94%a8%e4%ba%8b%e5%8a%a1', '', '', '2017-11-30 15:08:52', '2017-11-30 07:08:52', '', 0, 'http://luchanglong.com.cn/?p=439', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (440, 1, '2017-11-30 15:07:34', '2017-11-30 07:07:34', '<pre class=\"lang:default decode:true  \">\r\nDROP PROCEDURE IF EXISTS  repair_order_brokerage_pay;\r\nCREATE PROCEDURE repair_order_brokerage_pay()\r\n  BEGIN\r\n    DECLARE error INTEGER DEFAULT 0;\r\n    DECLARE CONTINUE HANDLER FOR SQLEXCEPTION SET error=1;\r\n\r\n    START TRANSACTION;\r\n\r\n    /*******order_id=122*******/\r\n    /*修改订单售后服务状态,修改后会在下次定时任务时自动发放*/\r\n    UPDATE `xm_orders` SET `is_service_over`=\'0\' WHERE (`id`=\'122\') LIMIT 1;\r\n    /*已经执行了销量增加，把增加的销量重新减去,新版本代码将会修改这一做法，用户不发奖励将不修改售后状态和销量*/\r\n    UPDATE `xm_goods` SET `sales_volume`=sales_volume-2 WHERE (`id`=\'17\') LIMIT 1;\r\n    UPDATE `xm_goods` SET `sales_volume`=sales_volume-1 WHERE (`id`=\'16\') LIMIT 1;\r\n    UPDATE `xm_goods` SET `sales_volume`=sales_volume-1 WHERE (`id`=\'14\') LIMIT 1;\r\n \r\n    IF error = 1 THEN\r\n      ROLLBACK;\r\n    ELSE\r\n      COMMIT;\r\n    END IF;\r\n    select error;\r\n  END;\r\n\r\n#执行\r\nCALL repair_order_brokerage_pay();</pre>\r\n&nbsp;', 'mysql 存贮过程中使用事务', '', 'inherit', 'closed', 'closed', '', '439-revision-v1', '', '', '2017-11-30 15:07:34', '2017-11-30 07:07:34', '', 439, 'http://luchanglong.com.cn/?p=440', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (441, 1, '2017-11-30 15:08:52', '2017-11-30 07:08:52', '<pre class=\"lang:default decode:true  \">DROP PROCEDURE IF EXISTS  repair_order_brokerage_pay;\r\nCREATE PROCEDURE repair_order_brokerage_pay()\r\n  BEGIN\r\n    DECLARE error INTEGER DEFAULT 0;\r\n    DECLARE CONTINUE HANDLER FOR SQLEXCEPTION SET error=1;\r\n\r\n    START TRANSACTION;\r\n\r\n    /*******order_id=122*******/\r\n    /*修改订单售后服务状态,修改后会在下次定时任务时自动发放*/\r\n    UPDATE `xm_orders` SET `is_service_over`=\'0\' WHERE (`id`=\'122\') LIMIT 1;\r\n    /*已经执行了销量增加，把增加的销量重新减去,新版本代码将会修改这一做法，用户不发奖励将不修改售后状态和销量*/\r\n    UPDATE `xm_goods` SET `sales_volume`=sales_volume-2 WHERE (`id`=\'17\') LIMIT 1;\r\n    UPDATE `xm_goods` SET `sales_volume`=sales_volume-1 WHERE (`id`=\'16\') LIMIT 1;\r\n    UPDATE `xm_goods` SET `sales_volume`=sales_volume-1 WHERE (`id`=\'14\') LIMIT 1;\r\n \r\n    IF error = 1 THEN\r\n      ROLLBACK;\r\n    ELSE\r\n      COMMIT;\r\n    END IF;\r\n    select error;\r\n  END;\r\n\r\n#执行\r\nCALL repair_order_brokerage_pay();</pre>\r\n&nbsp;', 'mysql 存储过程中使用事务', '', 'inherit', 'closed', 'closed', '', '439-revision-v1', '', '', '2017-11-30 15:08:52', '2017-11-30 07:08:52', '', 439, 'http://luchanglong.com.cn/?p=441', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (442, 1, '2017-12-01 11:34:54', '2017-12-01 03:34:54', '<strong>因为计算机只能处理数字，如果要处理文本，就必须先把文本转换为数字才能处理。最早的计算机在设计时采用8个比特（bit）作为一个字节（byte），所以，一个字节能表示的最大的整数就是255（二进制11111111=十进制255），如果要表示更大的整数，就必须用更多的字节。比如两个字节可以表示的最大整数是<code>65535</code>，4个字节可以表示的最大整数是<code>4294967295</code></strong>', '一个字节（byte）到底有多大？一个字节能表示多大的整数？', '', 'publish', 'open', 'open', '', '%e4%b8%80%e4%b8%aa%e5%ad%97%e8%8a%82%ef%bc%88byte%ef%bc%89%e5%88%b0%e5%ba%95%e6%9c%89%e5%a4%9a%e5%a4%a7%ef%bc%9f%e4%b8%80%e4%b8%aa%e5%ad%97%e8%8a%82%e8%83%bd%e8%a1%a8%e7%a4%ba%e5%a4%9a%e5%a4%a7', '', '', '2017-12-01 11:34:54', '2017-12-01 03:34:54', '', 0, 'http://luchanglong.com.cn/?p=442', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (443, 1, '2017-12-01 11:34:54', '2017-12-01 03:34:54', '<strong>因为计算机只能处理数字，如果要处理文本，就必须先把文本转换为数字才能处理。最早的计算机在设计时采用8个比特（bit）作为一个字节（byte），所以，一个字节能表示的最大的整数就是255（二进制11111111=十进制255），如果要表示更大的整数，就必须用更多的字节。比如两个字节可以表示的最大整数是<code>65535</code>，4个字节可以表示的最大整数是<code>4294967295</code></strong>', '一个字节（byte）到底有多大？一个字节能表示多大的整数？', '', 'inherit', 'closed', 'closed', '', '442-revision-v1', '', '', '2017-12-01 11:34:54', '2017-12-01 03:34:54', '', 442, 'http://luchanglong.com.cn/?p=443', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (445, 1, '2017-12-13 14:49:42', '2017-12-13 06:49:42', '1.whereis 文件名\r\n\r\n特点:快速,但是是模糊查找,例如 找 #whereis mysql 它会把mysql,mysql.ini,mysql.*所在的目录都找出来.我一般的查找都用这条命令.\r\n\r\n2.find / -name 文件名\r\n\r\n特点:准确,但速度慢,消耗资源大,例如我想找到php.ini的准确位置,就需要用\r\n\r\n#find / -name php.ini\r\n\r\n3.locate 文件名\r\n\r\n强力推荐的方法,最快,最好的方法.', 'linux 查找文件', '', 'publish', 'open', 'open', '', 'linux-%e6%9f%a5%e6%89%be%e6%96%87%e4%bb%b6', '', '', '2017-12-13 14:49:42', '2017-12-13 06:49:42', '', 0, 'http://luchanglong.com.cn/?p=445', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (446, 1, '2017-12-13 14:49:42', '2017-12-13 06:49:42', '1.whereis 文件名\r\n\r\n特点:快速,但是是模糊查找,例如 找 #whereis mysql 它会把mysql,mysql.ini,mysql.*所在的目录都找出来.我一般的查找都用这条命令.\r\n\r\n2.find / -name 文件名\r\n\r\n特点:准确,但速度慢,消耗资源大,例如我想找到php.ini的准确位置,就需要用\r\n\r\n#find / -name php.ini\r\n\r\n3.locate 文件名\r\n\r\n强力推荐的方法,最快,最好的方法.', 'linux 查找文件', '', 'inherit', 'closed', 'closed', '', '445-revision-v1', '', '', '2017-12-13 14:49:42', '2017-12-13 06:49:42', '', 445, 'http://luchanglong.com.cn/?p=446', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (447, 1, '2017-12-15 14:12:04', '2017-12-15 06:12:04', '<pre class=\"lang:php decode:true \">&lt;?php\r\n/**\r\n * RSA加密\r\n * env:(PHP 4 &gt;= 4.0.4, PHP 5, PHP 7)\r\n * 密钥对生成\r\n * 工具：OpenSSL\r\n * 生成私钥：genrsa -out rsa_private_key.pem 1024\r\n * 生成公钥：rsa -in rsa_private_key.pem -pubout -out rsa_public_key.pem\r\n * Author:luchanglong\r\n * Date:2017-12-14\r\n * **********************************************\r\n * * 私钥丢失将导致数据永久无法解密\r\n * *********************************************\r\n */\r\n\r\nheader(\"Content-type: text/html; charset=utf-8\");\r\n\r\nclass RsaTools\r\n{\r\n    //私钥文件路径\r\n    private $rsaPrivateKeyFilePath;\r\n\r\n    //私钥值\r\n    private $rsaPrivateKey;\r\n\r\n    //公钥文件路径\r\n    private $rsaPublicKeyFilePath;\r\n\r\n    //公钥值\r\n    private $rsaPublicKey;\r\n\r\n    function __construct()\r\n    {\r\n        $this-&gt;rsaPrivateKeyFilePath=dirname(__FILE__).DIRECTORY_SEPARATOR.\'key\'.DIRECTORY_SEPARATOR.\'rsa_private_key.pem\';\r\n        $this-&gt;rsaPublicKeyFilePath=dirname(__FILE__).DIRECTORY_SEPARATOR.\'key\'.DIRECTORY_SEPARATOR.\'rsa_public_key.pem\';\r\n    }\r\n\r\n    /**\r\n     *  rsa公钥加密\r\n     **/\r\n    public function rsaEncrypt($data) {\r\n        if($this-&gt;checkEmpty($this-&gt;rsaPublicKeyFilePath)){\r\n            //读取字符串\r\n            $pubKey= $this-&gt;rsaPublicKey;\r\n            $res = \"-----BEGIN PUBLIC KEY-----\\n\" .\r\n                wordwrap($pubKey, 64, \"\\n\", true) .\r\n                \"\\n-----END PUBLIC KEY-----\";\r\n        }else {\r\n            //读取公钥文件\r\n            $pubKey = file_get_contents($this-&gt;rsaPublicKeyFilePath);\r\n            //转换为openssl格式密钥\r\n            $res = openssl_get_publickey($pubKey);\r\n        }\r\n\r\n        ($res) or die(\'RSA公钥错误。请检查公钥文件格式是否正确\');\r\n        $data=trim($data);\r\n        openssl_public_encrypt($data,$encrypted,$pubKey);//公钥加密\r\n        $encrypted = base64_encode($encrypted);\r\n        return $encrypted;\r\n    }\r\n    /**\r\n     * rsa私钥解密\r\n     **/\r\n    public function rsaDecrypt($data) {\r\n\r\n        if($this-&gt;checkEmpty($this-&gt;rsaPrivateKeyFilePath)){\r\n            //读字符串\r\n            $priKey=$this-&gt;rsaPrivateKey;\r\n            $res = \"-----BEGIN RSA PRIVATE KEY-----\\n\" .\r\n                wordwrap($priKey, 64, \"\\n\", true) .\r\n                \"\\n-----END RSA PRIVATE KEY-----\";\r\n        }else {\r\n            $priKey = file_get_contents($this-&gt;rsaPrivateKeyFilePath);\r\n            $res = openssl_get_privatekey($priKey);\r\n        }\r\n        ($res) or die(\'您使用的私钥格式错误，请检查RSA私钥配置\');\r\n        $data=base64_decode($data);\r\n        openssl_private_decrypt($data, $dcyCont, $res);\r\n        return $dcyCont;\r\n    }\r\n\r\n    /**\r\n     * 校验$value是否非空\r\n     *  if not set ,return true;\r\n     *    if is null , return true;\r\n     **/\r\n    protected function checkEmpty($value) {\r\n        if (!isset($value))\r\n            return true;\r\n        if ($value === null)\r\n            return true;\r\n        if (trim($value) === \"\")\r\n            return true;\r\n\r\n        return false;\r\n    }\r\n\r\n}\r\n\r\n\r\n$r=new RsaTools();\r\n$str=\"0123456789?&gt;&lt;~!@#$%^&amp;*()_+qwertyuiopasdfghjklzxcvbnm\";\r\necho \'待加密：\'.$str.\"&lt;br&gt;\";\r\n$en=$r-&gt;rsaEncrypt($str);\r\necho \'加密后：\'.$en.\'&lt;br&gt;\';\r\n$de=$r-&gt;rsaDecrypt($en);\r\necho \'解密后：\'.$de;\r\n\r\n</pre>\r\n&nbsp;', 'php rsa加密解密完整实例', '', 'publish', 'open', 'open', '', 'php-rsa%e5%8a%a0%e5%af%86%e8%a7%a3%e5%af%86%e5%ae%8c%e6%95%b4%e5%ae%9e%e4%be%8b', '', '', '2017-12-15 14:13:51', '2017-12-15 06:13:51', '', 0, 'http://luchanglong.com.cn/?p=447', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (448, 1, '2017-12-15 14:12:00', '2017-12-15 06:12:00', '<pre class=\"\">&lt;?php\r\n/**\r\n * RSA加密\r\n * env:(PHP 4 &gt;= 4.0.4, PHP 5, PHP 7)\r\n * 密钥对生成\r\n * 工具：OpenSSL\r\n * 生成私钥：genrsa -out rsa_private_key.pem 1024\r\n * 生成公钥：rsa -in rsa_private_key.pem -pubout -out rsa_public_key.pem\r\n * Author:luchanglong\r\n * Date:2017-12-14\r\n * **********************************************\r\n * * 私钥丢失将导致数据永久无法解密\r\n * *********************************************\r\n */\r\n\r\nheader(\"Content-type: text/html; charset=utf-8\");\r\n\r\nclass RsaTools\r\n{\r\n    //私钥文件路径\r\n    private $rsaPrivateKeyFilePath;\r\n\r\n    //私钥值\r\n    private $rsaPrivateKey;\r\n\r\n    //公钥文件路径\r\n    private $rsaPublicKeyFilePath;\r\n\r\n    //公钥值\r\n    private $rsaPublicKey;\r\n\r\n    function __construct()\r\n    {\r\n        $this-&gt;rsaPrivateKeyFilePath=dirname(__FILE__).DIRECTORY_SEPARATOR.\'key\'.DIRECTORY_SEPARATOR.\'rsa_private_key.pem\';\r\n        $this-&gt;rsaPublicKeyFilePath=dirname(__FILE__).DIRECTORY_SEPARATOR.\'key\'.DIRECTORY_SEPARATOR.\'rsa_public_key.pem\';\r\n    }\r\n\r\n    /**\r\n     *  rsa公钥加密\r\n     **/\r\n    public function rsaEncrypt($data) {\r\n        if($this-&gt;checkEmpty($this-&gt;rsaPublicKeyFilePath)){\r\n            //读取字符串\r\n            $pubKey= $this-&gt;rsaPublicKey;\r\n            $res = \"-----BEGIN PUBLIC KEY-----\\n\" .\r\n                wordwrap($pubKey, 64, \"\\n\", true) .\r\n                \"\\n-----END PUBLIC KEY-----\";\r\n        }else {\r\n            //读取公钥文件\r\n            $pubKey = file_get_contents($this-&gt;rsaPublicKeyFilePath);\r\n            //转换为openssl格式密钥\r\n            $res = openssl_get_publickey($pubKey);\r\n        }\r\n\r\n        ($res) or die(\'RSA公钥错误。请检查公钥文件格式是否正确\');\r\n        $data=trim($data);\r\n        openssl_public_encrypt($data,$encrypted,$pubKey);//公钥加密\r\n        $encrypted = base64_encode($encrypted);\r\n        return $encrypted;\r\n    }\r\n    /**\r\n     * rsa私钥解密\r\n     **/\r\n    public function rsaDecrypt($data) {\r\n\r\n        if($this-&gt;checkEmpty($this-&gt;rsaPrivateKeyFilePath)){\r\n            //读字符串\r\n            $priKey=$this-&gt;rsaPrivateKey;\r\n            $res = \"-----BEGIN RSA PRIVATE KEY-----\\n\" .\r\n                wordwrap($priKey, 64, \"\\n\", true) .\r\n                \"\\n-----END RSA PRIVATE KEY-----\";\r\n        }else {\r\n            $priKey = file_get_contents($this-&gt;rsaPrivateKeyFilePath);\r\n            $res = openssl_get_privatekey($priKey);\r\n        }\r\n        ($res) or die(\'您使用的私钥格式错误，请检查RSA私钥配置\');\r\n        $data=base64_decode($data);\r\n        openssl_private_decrypt($data, $dcyCont, $res);\r\n        return $dcyCont;\r\n    }\r\n\r\n    /**\r\n     * 校验$value是否非空\r\n     *  if not set ,return true;\r\n     *    if is null , return true;\r\n     **/\r\n    protected function checkEmpty($value) {\r\n        if (!isset($value))\r\n            return true;\r\n        if ($value === null)\r\n            return true;\r\n        if (trim($value) === \"\")\r\n            return true;\r\n\r\n        return false;\r\n    }\r\n\r\n}\r\n\r\n\r\n$r=new RsaTools();\r\n$str=\"0123456789?&gt;&lt;~!@#$%^&amp;*()_+qwertyuiopasdfghjklzxcvbnm\";\r\necho \'待加密：\'.$str.\"&lt;br&gt;\";\r\n$en=$r-&gt;rsaEncrypt($str);\r\necho \'加密后：\'.$en.\'&lt;br&gt;\';\r\n$de=$r-&gt;rsaDecrypt($en);\r\necho \'解密后：\'.$de;\r\n\r\n</pre>\r\n&nbsp;', 'php rsa加密解密完整实例', '', 'inherit', 'closed', 'closed', '', '447-revision-v1', '', '', '2017-12-15 14:12:00', '2017-12-15 06:12:00', '', 447, 'http://luchanglong.com.cn/?p=448', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (449, 1, '2017-12-15 14:12:46', '2017-12-15 06:12:46', '<pre class=\"\">&lt;?php\r\n/**\r\n * RSA加密\r\n * env:(PHP 4 &gt;= 4.0.4, PHP 5, PHP 7)\r\n * 密钥对生成\r\n * 工具：OpenSSL\r\n * 生成私钥：genrsa -out rsa_private_key.pem 1024\r\n * 生成公钥：rsa -in rsa_private_key.pem -pubout -out rsa_public_key.pem\r\n * Author:Leaver\r\n * Date:2017-12-14\r\n * **********************************************\r\n * * 私钥丢失将导致数据永久无法解密\r\n * *********************************************\r\n */\r\n\r\nheader(\"Content-type: text/html; charset=utf-8\");\r\n\r\nclass RsaTools\r\n{\r\n    //私钥文件路径\r\n    private $rsaPrivateKeyFilePath;\r\n\r\n    //私钥值\r\n    private $rsaPrivateKey;\r\n\r\n    //公钥文件路径\r\n    private $rsaPublicKeyFilePath;\r\n\r\n    //公钥值\r\n    private $rsaPublicKey;\r\n\r\n    function __construct()\r\n    {\r\n        $this-&gt;rsaPrivateKeyFilePath=dirname(__FILE__).DIRECTORY_SEPARATOR.\'key\'.DIRECTORY_SEPARATOR.\'rsa_private_key.pem\';\r\n        $this-&gt;rsaPublicKeyFilePath=dirname(__FILE__).DIRECTORY_SEPARATOR.\'key\'.DIRECTORY_SEPARATOR.\'rsa_public_key.pem\';\r\n    }\r\n\r\n    /**\r\n     *  rsa公钥加密\r\n     **/\r\n    public function rsaEncrypt($data) {\r\n        if($this-&gt;checkEmpty($this-&gt;rsaPublicKeyFilePath)){\r\n            //读取字符串\r\n            $pubKey= $this-&gt;rsaPublicKey;\r\n            $res = \"-----BEGIN PUBLIC KEY-----\\n\" .\r\n                wordwrap($pubKey, 64, \"\\n\", true) .\r\n                \"\\n-----END PUBLIC KEY-----\";\r\n        }else {\r\n            //读取公钥文件\r\n            $pubKey = file_get_contents($this-&gt;rsaPublicKeyFilePath);\r\n            //转换为openssl格式密钥\r\n            $res = openssl_get_publickey($pubKey);\r\n        }\r\n\r\n        ($res) or die(\'RSA公钥错误。请检查公钥文件格式是否正确\');\r\n        $data=trim($data);\r\n        openssl_public_encrypt($data,$encrypted,$pubKey);//公钥加密\r\n        $encrypted = base64_encode($encrypted);\r\n        return $encrypted;\r\n    }\r\n    /**\r\n     * rsa私钥解密\r\n     **/\r\n    public function rsaDecrypt($data) {\r\n\r\n        if($this-&gt;checkEmpty($this-&gt;rsaPrivateKeyFilePath)){\r\n            //读字符串\r\n            $priKey=$this-&gt;rsaPrivateKey;\r\n            $res = \"-----BEGIN RSA PRIVATE KEY-----\\n\" .\r\n                wordwrap($priKey, 64, \"\\n\", true) .\r\n                \"\\n-----END RSA PRIVATE KEY-----\";\r\n        }else {\r\n            $priKey = file_get_contents($this-&gt;rsaPrivateKeyFilePath);\r\n            $res = openssl_get_privatekey($priKey);\r\n        }\r\n        ($res) or die(\'您使用的私钥格式错误，请检查RSA私钥配置\');\r\n        $data=base64_decode($data);\r\n        openssl_private_decrypt($data, $dcyCont, $res);\r\n        return $dcyCont;\r\n    }\r\n\r\n    /**\r\n     * 校验$value是否非空\r\n     *  if not set ,return true;\r\n     *    if is null , return true;\r\n     **/\r\n    protected function checkEmpty($value) {\r\n        if (!isset($value))\r\n            return true;\r\n        if ($value === null)\r\n            return true;\r\n        if (trim($value) === \"\")\r\n            return true;\r\n\r\n        return false;\r\n    }\r\n\r\n}\r\n\r\n\r\n$r=new RsaTools();\r\n$str=\"0123456789?&gt;&lt;~!@#$%^&amp;*()_+qwertyuiopasdfghjklzxcvbnm\";\r\necho \'待加密：\'.$str.\"&lt;br&gt;\";\r\n$en=$r-&gt;rsaEncrypt($str);\r\necho \'加密后：\'.$en.\'&lt;br&gt;\';\r\n$de=$r-&gt;rsaDecrypt($en);\r\necho \'解密后：\'.$de;\r\n\r\n</pre>\r\n&nbsp;', 'php rsa加密解密完整实例', '', 'inherit', 'closed', 'closed', '', '447-revision-v1', '', '', '2017-12-15 14:12:46', '2017-12-15 06:12:46', '', 447, 'http://luchanglong.com.cn/?p=449', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (450, 1, '2017-12-15 14:13:51', '2017-12-15 06:13:51', '<pre class=\"lang:php decode:true \">&lt;?php\r\n/**\r\n * RSA加密\r\n * env:(PHP 4 &gt;= 4.0.4, PHP 5, PHP 7)\r\n * 密钥对生成\r\n * 工具：OpenSSL\r\n * 生成私钥：genrsa -out rsa_private_key.pem 1024\r\n * 生成公钥：rsa -in rsa_private_key.pem -pubout -out rsa_public_key.pem\r\n * Author:luchanglong\r\n * Date:2017-12-14\r\n * **********************************************\r\n * * 私钥丢失将导致数据永久无法解密\r\n * *********************************************\r\n */\r\n\r\nheader(\"Content-type: text/html; charset=utf-8\");\r\n\r\nclass RsaTools\r\n{\r\n    //私钥文件路径\r\n    private $rsaPrivateKeyFilePath;\r\n\r\n    //私钥值\r\n    private $rsaPrivateKey;\r\n\r\n    //公钥文件路径\r\n    private $rsaPublicKeyFilePath;\r\n\r\n    //公钥值\r\n    private $rsaPublicKey;\r\n\r\n    function __construct()\r\n    {\r\n        $this-&gt;rsaPrivateKeyFilePath=dirname(__FILE__).DIRECTORY_SEPARATOR.\'key\'.DIRECTORY_SEPARATOR.\'rsa_private_key.pem\';\r\n        $this-&gt;rsaPublicKeyFilePath=dirname(__FILE__).DIRECTORY_SEPARATOR.\'key\'.DIRECTORY_SEPARATOR.\'rsa_public_key.pem\';\r\n    }\r\n\r\n    /**\r\n     *  rsa公钥加密\r\n     **/\r\n    public function rsaEncrypt($data) {\r\n        if($this-&gt;checkEmpty($this-&gt;rsaPublicKeyFilePath)){\r\n            //读取字符串\r\n            $pubKey= $this-&gt;rsaPublicKey;\r\n            $res = \"-----BEGIN PUBLIC KEY-----\\n\" .\r\n                wordwrap($pubKey, 64, \"\\n\", true) .\r\n                \"\\n-----END PUBLIC KEY-----\";\r\n        }else {\r\n            //读取公钥文件\r\n            $pubKey = file_get_contents($this-&gt;rsaPublicKeyFilePath);\r\n            //转换为openssl格式密钥\r\n            $res = openssl_get_publickey($pubKey);\r\n        }\r\n\r\n        ($res) or die(\'RSA公钥错误。请检查公钥文件格式是否正确\');\r\n        $data=trim($data);\r\n        openssl_public_encrypt($data,$encrypted,$pubKey);//公钥加密\r\n        $encrypted = base64_encode($encrypted);\r\n        return $encrypted;\r\n    }\r\n    /**\r\n     * rsa私钥解密\r\n     **/\r\n    public function rsaDecrypt($data) {\r\n\r\n        if($this-&gt;checkEmpty($this-&gt;rsaPrivateKeyFilePath)){\r\n            //读字符串\r\n            $priKey=$this-&gt;rsaPrivateKey;\r\n            $res = \"-----BEGIN RSA PRIVATE KEY-----\\n\" .\r\n                wordwrap($priKey, 64, \"\\n\", true) .\r\n                \"\\n-----END RSA PRIVATE KEY-----\";\r\n        }else {\r\n            $priKey = file_get_contents($this-&gt;rsaPrivateKeyFilePath);\r\n            $res = openssl_get_privatekey($priKey);\r\n        }\r\n        ($res) or die(\'您使用的私钥格式错误，请检查RSA私钥配置\');\r\n        $data=base64_decode($data);\r\n        openssl_private_decrypt($data, $dcyCont, $res);\r\n        return $dcyCont;\r\n    }\r\n\r\n    /**\r\n     * 校验$value是否非空\r\n     *  if not set ,return true;\r\n     *    if is null , return true;\r\n     **/\r\n    protected function checkEmpty($value) {\r\n        if (!isset($value))\r\n            return true;\r\n        if ($value === null)\r\n            return true;\r\n        if (trim($value) === \"\")\r\n            return true;\r\n\r\n        return false;\r\n    }\r\n\r\n}\r\n\r\n\r\n$r=new RsaTools();\r\n$str=\"0123456789?&gt;&lt;~!@#$%^&amp;*()_+qwertyuiopasdfghjklzxcvbnm\";\r\necho \'待加密：\'.$str.\"&lt;br&gt;\";\r\n$en=$r-&gt;rsaEncrypt($str);\r\necho \'加密后：\'.$en.\'&lt;br&gt;\';\r\n$de=$r-&gt;rsaDecrypt($en);\r\necho \'解密后：\'.$de;\r\n\r\n</pre>\r\n&nbsp;', 'php rsa加密解密完整实例', '', 'inherit', 'closed', 'closed', '', '447-revision-v1', '', '', '2017-12-15 14:13:51', '2017-12-15 06:13:51', '', 447, 'http://luchanglong.com.cn/?p=450', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (451, 1, '2017-12-19 15:56:13', '2017-12-19 07:56:13', 'Python有五个标准的数据类型：\r\n<ul>\r\n 	<li>Numbers（数字）</li>\r\n 	<li>String（字符串）</li>\r\n 	<li>List（列表）</li>\r\n 	<li>Tuple（元组）</li>\r\n 	<li>Dictionary（字典）</li>\r\n</ul>\r\n<strong>List</strong>\r\n\r\n列表是 Python 中使用最频繁的数据类型。\r\n\r\n列表可以完成大多数集合类的数据结构实现。它支持字符，数字，字符串甚至可以包含列表（即嵌套）。\r\n\r\n列表用 <span class=\"marked\">[ ]</span> 标识，是 python 最通用的复合数据类型。\r\n<pre class=\"lang:default decode:true\"> l = [\'A\', \'b\', \'C\']</pre>\r\ntuple\r\n\r\n元组是另一个数据类型，类似于List（列表）。\r\n\r\n元组用\"()\"标识。内部元素用逗号隔开。但是元组不能二次赋值，相当于只读列表。\r\n<pre class=\"lang:default decode:true \">t = (\'C\', \'D\', \'A\')</pre>\r\n&nbsp;\r\n\r\ndict\r\n\r\n字典(dictionary)是除列表以外python之中最灵活的内置数据结构类型。列表是有序的对象集合，字典是无序的对象集合。\r\n\r\n两者之间的区别在于：字典当中的元素是通过键来存取的，而不是通过偏移存取。\r\n\r\n字典用\"{ }\"标识。字典由索引(key)和它对应的值value组成。\r\n<pre class=\"lang:default decode:true \">d = {\'lisa\': 90, \'hanmei\': 70, \'harry\': 85}</pre>\r\n&nbsp;\r\n\r\n<strong>set</strong>\r\n\r\n<b>set()</b> 函数创建一个无序不重复元素集，可进行关系测试，删除重复数据，还可以计算交集、差集、并集等。\r\n<pre class=\"lang:default decode:true \">&gt;&gt;&gt;x = set(\'runoob\') \r\n&gt;&gt;&gt; y = set(\'google\') \r\n&gt;&gt;&gt; x, y (set([\'b\', \'r\', \'u\', \'o\', \'n\']), set([\'e\', \'o\', \'g\', \'l\'])) # 重复的被删除 \r\n&gt;&gt;&gt; x &amp; y # 交集 set([\'o\']) \r\n&gt;&gt;&gt; x | y # 并集 set([\'b\', \'e\', \'g\', \'l\', \'o\', \'n\', \'r\', \'u\']) \r\n&gt;&gt;&gt; x - y # 差集 set([\'r\', \'b\', \'u\', \'n\']) \r\n&gt;&gt;&gt;</pre>\r\n&nbsp;\r\n\r\n&nbsp;', 'python 标准数据类型', '', 'publish', 'open', 'open', '', 'python-%e6%a0%87%e5%87%86%e6%95%b0%e6%8d%ae%e7%b1%bb%e5%9e%8b', '', '', '2017-12-19 15:58:34', '2017-12-19 07:58:34', '', 0, 'http://luchanglong.com.cn/?p=451', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (452, 1, '2017-12-19 15:39:53', '2017-12-19 07:39:53', '<h3>list  是一种有序的集合，可以随时添加和删除其中的元素。</h3>\r\n&nbsp;', 'python 常用内置函数', '', 'inherit', 'closed', 'closed', '', '451-revision-v1', '', '', '2017-12-19 15:39:53', '2017-12-19 07:39:53', '', 451, 'http://luchanglong.com.cn/?p=452', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (453, 1, '2017-12-19 15:56:13', '2017-12-19 07:56:13', 'Python有五个标准的数据类型：\r\n<ul>\r\n 	<li>Numbers（数字）</li>\r\n 	<li>String（字符串）</li>\r\n 	<li>List（列表）</li>\r\n 	<li>Tuple（元组）</li>\r\n 	<li>Dictionary（字典）</li>\r\n</ul>\r\n<strong>List</strong>\r\n\r\n列表是 Python 中使用最频繁的数据类型。\r\n\r\n列表可以完成大多数集合类的数据结构实现。它支持字符，数字，字符串甚至可以包含列表（即嵌套）。\r\n\r\n列表用 <span class=\"marked\">[ ]</span> 标识，是 python 最通用的复合数据类型。\r\n<pre><code class=\"python\"> l = [<span class=\"string\">\'A\'</span>, <span class=\"string\">\'b\'</span>, <span class=\"string\">\'C\'</span>]</code></pre>\r\n<h3> tuple</h3>\r\n元组是另一个数据类型，类似于List（列表）。\r\n\r\n元组用\"()\"标识。内部元素用逗号隔开。但是元组不能二次赋值，相当于只读列表。\r\n<pre><code class=\"python\">t = (<span class=\"string\">\'C\'</span>, <span class=\"string\">\'D\'</span>, <span class=\"string\">\'A\'</span>)</code></pre>\r\n<h3>dict</h3>\r\n字典(dictionary)是除列表以外python之中最灵活的内置数据结构类型。列表是有序的对象集合，字典是无序的对象集合。\r\n\r\n两者之间的区别在于：字典当中的元素是通过键来存取的，而不是通过偏移存取。\r\n\r\n字典用\"{ }\"标识。字典由索引(key)和它对应的值value组成。\r\n<pre><code class=\"python\">d = {<span class=\"string\">\'lisa\'</span>: <span class=\"number\">90</span>, <span class=\"string\">\'hanmei\'</span>: <span class=\"number\">70</span>, <span class=\"string\">\'harry\'</span>: 8<span class=\"number\">5</span>}</code></pre>\r\n&nbsp;\r\n\r\n<strong>set</strong>\r\n\r\n<b>set()</b> 函数创建一个无序不重复元素集，可进行关系测试，删除重复数据，还可以计算交集、差集、并集等。\r\n<pre><span class=\"hl-code\">&gt;&gt;&gt;</span><span class=\"hl-identifier\">x</span><span class=\"hl-code\"> = </span><span class=\"hl-identifier\">set</span><span class=\"hl-brackets\">(</span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">runoob</span><span class=\"hl-quotes\">\'</span><span class=\"hl-brackets\">)</span><span class=\"hl-code\"> \r\n&gt;&gt;&gt; </span><span class=\"hl-identifier\">y</span><span class=\"hl-code\"> = </span><span class=\"hl-identifier\">set</span><span class=\"hl-brackets\">(</span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">google</span><span class=\"hl-quotes\">\'</span><span class=\"hl-brackets\">)</span><span class=\"hl-code\"> \r\n&gt;&gt;&gt; </span><span class=\"hl-identifier\">x</span><span class=\"hl-code\">, </span><span class=\"hl-identifier\">y</span> <span class=\"hl-brackets\">(</span><span class=\"hl-identifier\">set</span><span class=\"hl-brackets\">(</span><span class=\"hl-brackets\">[</span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">b</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">r</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">u</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">o</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">n</span><span class=\"hl-quotes\">\'</span><span class=\"hl-brackets\">]</span><span class=\"hl-brackets\">)</span><span class=\"hl-code\">, </span><span class=\"hl-identifier\">set</span><span class=\"hl-brackets\">(</span><span class=\"hl-brackets\">[</span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">e</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">o</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">g</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">l</span><span class=\"hl-quotes\">\'</span><span class=\"hl-brackets\">]</span><span class=\"hl-brackets\">)</span><span class=\"hl-brackets\">)</span> <span class=\"hl-comment\"># 重复的被删除</span><span class=\"hl-code\"> \r\n&gt;&gt;&gt; </span><span class=\"hl-identifier\">x</span><span class=\"hl-code\"> &amp; </span><span class=\"hl-identifier\">y</span> <span class=\"hl-comment\"># 交集</span> <span class=\"hl-identifier\">set</span><span class=\"hl-brackets\">(</span><span class=\"hl-brackets\">[</span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">o</span><span class=\"hl-quotes\">\'</span><span class=\"hl-brackets\">]</span><span class=\"hl-brackets\">)</span><span class=\"hl-code\"> \r\n&gt;&gt;&gt; </span><span class=\"hl-identifier\">x</span><span class=\"hl-code\"> | </span><span class=\"hl-identifier\">y</span> <span class=\"hl-comment\"># 并集</span> <span class=\"hl-identifier\">set</span><span class=\"hl-brackets\">(</span><span class=\"hl-brackets\">[</span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">b</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">e</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">g</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">l</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">o</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">n</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">r</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">u</span><span class=\"hl-quotes\">\'</span><span class=\"hl-brackets\">]</span><span class=\"hl-brackets\">)</span><span class=\"hl-code\"> \r\n&gt;&gt;&gt; </span><span class=\"hl-identifier\">x</span><span class=\"hl-code\"> - </span><span class=\"hl-identifier\">y</span> <span class=\"hl-comment\"># 差集</span> <span class=\"hl-identifier\">set</span><span class=\"hl-brackets\">(</span><span class=\"hl-brackets\">[</span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">r</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">b</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">u</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">n</span><span class=\"hl-quotes\">\'</span><span class=\"hl-brackets\">]</span><span class=\"hl-brackets\">)</span><span class=\"hl-code\"> \r\n&gt;&gt;&gt;</span></pre>\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;', 'python 标准数据类型', '', 'inherit', 'closed', 'closed', '', '451-revision-v1', '', '', '2017-12-19 15:56:13', '2017-12-19 07:56:13', '', 451, 'http://luchanglong.com.cn/?p=453', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (454, 1, '2017-12-19 15:58:33', '2017-12-19 07:58:33', 'Python有五个标准的数据类型：\n<ul>\n 	<li>Numbers（数字）</li>\n 	<li>String（字符串）</li>\n 	<li>List（列表）</li>\n 	<li>Tuple（元组）</li>\n 	<li>Dictionary（字典）</li>\n</ul>\n<strong>List</strong>\n\n列表是 Python 中使用最频繁的数据类型。\n\n列表可以完成大多数集合类的数据结构实现。它支持字符，数字，字符串甚至可以包含列表（即嵌套）。\n\n列表用 <span class=\"marked\">[ ]</span> 标识，是 python 最通用的复合数据类型。\n<pre class=\"lang:default decode:true\"> l = [\'A\', \'b\', \'C\']</pre>\ntuple\n\n元组是另一个数据类型，类似于List（列表）。\n\n元组用\"()\"标识。内部元素用逗号隔开。但是元组不能二次赋值，相当于只读列表。\n<pre class=\"lang:default decode:true \">t = (\'C\', \'D\', \'A\')</pre>\n&nbsp;\n\ndict\n\n字典(dictionary)是除列表以外python之中最灵活的内置数据结构类型。列表是有序的对象集合，字典是无序的对象集合。\n\n两者之间的区别在于：字典当中的元素是通过键来存取的，而不是通过偏移存取。\n\n字典用\"{ }\"标识。字典由索引(key)和它对应的值value组成。\n<pre class=\"lang:default decode:true \">d = {\'lisa\': 90, \'hanmei\': 70, \'harry\': 85}</pre>\n&nbsp;\n\n<strong>set</strong>\n\n<b>set()</b> 函数创建一个无序不重复元素集，可进行关系测试，删除重复数据，还可以计算交集、差集、并集等。\n<pre class=\"lang:default decode:true \">&gt;&gt;&gt;x = set(\'runoob\') \n&gt;&gt;&gt; y = set(\'google\') \n&gt;&gt;&gt; x, y (set([\'b\', \'r\', \'u\', \'o\', \'n\']), set([\'e\', \'o\', \'g\', \'l\'])) # 重复的被删除 \n&gt;&gt;&gt; x &amp; y # 交集 set([\'o\']) \n&gt;&gt;&gt; x | y # 并集 set([\'b\', \'e\', \'g\', \'l\', \'o\', \'n\', \'r\', \'u\']) \n&gt;&gt;&gt; x - y # 差集 set([\'r\', \'b\', \'u\', \'n\']) \n&gt;&gt;&gt;</pre>\n&nbsp;\n\n&nbsp;', 'python 标准数据类型', '', 'inherit', 'closed', 'closed', '', '451-autosave-v1', '', '', '2017-12-19 15:58:33', '2017-12-19 07:58:33', '', 451, 'http://luchanglong.com.cn/?p=454', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (455, 1, '2017-12-19 15:57:31', '2017-12-19 07:57:31', 'Python有五个标准的数据类型：\r\n<ul>\r\n 	<li>Numbers（数字）</li>\r\n 	<li>String（字符串）</li>\r\n 	<li>List（列表）</li>\r\n 	<li>Tuple（元组）</li>\r\n 	<li>Dictionary（字典）</li>\r\n</ul>\r\n<strong>List</strong>\r\n\r\n列表是 Python 中使用最频繁的数据类型。\r\n\r\n列表可以完成大多数集合类的数据结构实现。它支持字符，数字，字符串甚至可以包含列表（即嵌套）。\r\n\r\n列表用 <span class=\"marked\">[ ]</span> 标识，是 python 最通用的复合数据类型。\r\n<pre class=\"lang:default decode:true \"> l = [\'A\', \'b\', \'C\']</pre>\r\n&nbsp;\r\n<pre class=\"\"></pre>\r\n<h3>tuple</h3>\r\n\r\n元组是另一个数据类型，类似于List（列表）。\r\n\r\n元组用\"()\"标识。内部元素用逗号隔开。但是元组不能二次赋值，相当于只读列表。\r\n\r\n<pre><code class=\"python\">t = (<span class=\"string\">\'C\'</span>, <span class=\"string\">\'D\'</span>, <span class=\"string\">\'A\'</span>)</code></pre>\r\n<h3>dict</h3>\r\n字典(dictionary)是除列表以外python之中最灵活的内置数据结构类型。列表是有序的对象集合，字典是无序的对象集合。\r\n\r\n两者之间的区别在于：字典当中的元素是通过键来存取的，而不是通过偏移存取。\r\n\r\n字典用\"{ }\"标识。字典由索引(key)和它对应的值value组成。\r\n<pre><code class=\"python\">d = {<span class=\"string\">\'lisa\'</span>: <span class=\"number\">90</span>, <span class=\"string\">\'hanmei\'</span>: <span class=\"number\">70</span>, <span class=\"string\">\'harry\'</span>: 8<span class=\"number\">5</span>}</code></pre>\r\n&nbsp;\r\n\r\n<strong>set</strong>\r\n\r\n<b>set()</b> 函数创建一个无序不重复元素集，可进行关系测试，删除重复数据，还可以计算交集、差集、并集等。\r\n<pre><span class=\"hl-code\">&gt;&gt;&gt;</span><span class=\"hl-identifier\">x</span><span class=\"hl-code\"> = </span><span class=\"hl-identifier\">set</span><span class=\"hl-brackets\">(</span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">runoob</span><span class=\"hl-quotes\">\'</span><span class=\"hl-brackets\">)</span><span class=\"hl-code\"> \r\n&gt;&gt;&gt; </span><span class=\"hl-identifier\">y</span><span class=\"hl-code\"> = </span><span class=\"hl-identifier\">set</span><span class=\"hl-brackets\">(</span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">google</span><span class=\"hl-quotes\">\'</span><span class=\"hl-brackets\">)</span><span class=\"hl-code\"> \r\n&gt;&gt;&gt; </span><span class=\"hl-identifier\">x</span><span class=\"hl-code\">, </span><span class=\"hl-identifier\">y</span> <span class=\"hl-brackets\">(</span><span class=\"hl-identifier\">set</span><span class=\"hl-brackets\">(</span><span class=\"hl-brackets\">[</span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">b</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">r</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">u</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">o</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">n</span><span class=\"hl-quotes\">\'</span><span class=\"hl-brackets\">]</span><span class=\"hl-brackets\">)</span><span class=\"hl-code\">, </span><span class=\"hl-identifier\">set</span><span class=\"hl-brackets\">(</span><span class=\"hl-brackets\">[</span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">e</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">o</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">g</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">l</span><span class=\"hl-quotes\">\'</span><span class=\"hl-brackets\">]</span><span class=\"hl-brackets\">)</span><span class=\"hl-brackets\">)</span> <span class=\"hl-comment\"># 重复的被删除</span><span class=\"hl-code\"> \r\n&gt;&gt;&gt; </span><span class=\"hl-identifier\">x</span><span class=\"hl-code\"> &amp; </span><span class=\"hl-identifier\">y</span> <span class=\"hl-comment\"># 交集</span> <span class=\"hl-identifier\">set</span><span class=\"hl-brackets\">(</span><span class=\"hl-brackets\">[</span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">o</span><span class=\"hl-quotes\">\'</span><span class=\"hl-brackets\">]</span><span class=\"hl-brackets\">)</span><span class=\"hl-code\"> \r\n&gt;&gt;&gt; </span><span class=\"hl-identifier\">x</span><span class=\"hl-code\"> | </span><span class=\"hl-identifier\">y</span> <span class=\"hl-comment\"># 并集</span> <span class=\"hl-identifier\">set</span><span class=\"hl-brackets\">(</span><span class=\"hl-brackets\">[</span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">b</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">e</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">g</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">l</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">o</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">n</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">r</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">u</span><span class=\"hl-quotes\">\'</span><span class=\"hl-brackets\">]</span><span class=\"hl-brackets\">)</span><span class=\"hl-code\"> \r\n&gt;&gt;&gt; </span><span class=\"hl-identifier\">x</span><span class=\"hl-code\"> - </span><span class=\"hl-identifier\">y</span> <span class=\"hl-comment\"># 差集</span> <span class=\"hl-identifier\">set</span><span class=\"hl-brackets\">(</span><span class=\"hl-brackets\">[</span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">r</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">b</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">u</span><span class=\"hl-quotes\">\'</span><span class=\"hl-code\">, </span><span class=\"hl-quotes\">\'</span><span class=\"hl-string\">n</span><span class=\"hl-quotes\">\'</span><span class=\"hl-brackets\">]</span><span class=\"hl-brackets\">)</span><span class=\"hl-code\"> \r\n&gt;&gt;&gt;</span></pre>\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n&nbsp;', 'python 标准数据类型', '', 'inherit', 'closed', 'closed', '', '451-revision-v1', '', '', '2017-12-19 15:57:31', '2017-12-19 07:57:31', '', 451, 'http://luchanglong.com.cn/?p=455', 0, 'revision', '', 0);
