INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (457, 1, '2017-12-20 15:42:49', '2017-12-20 07:42:49', '创建项目\r\n\r\ndjango-admin.py startproject HelloWorld\r\n\r\n创建app\r\ndjango-admin.py startapp TestModel\r\n\r\n创建表结构\r\n$ python manage.py migrate   # 创建表结构\r\n\r\n$ python manage.py makemigrations TestModel  # 让 Django 知道我们在我们的模型有一些变更\r\n$ python manage.py migrate TestModel   # 创建表结构\r\n\r\n在新增表后重新执行：\r\n$ python manage.py makemigrations TestModel  # 让 Django 知道我们在我们的模型有一些变更\r\n$ python manage.py migrate TestModel   # 创建表结构', 'Django 常用命令', '', 'publish', 'open', 'open', '', 'django-%e5%b8%b8%e7%94%a8%e5%91%bd%e4%bb%a4', '', '', '2017-12-21 13:44:51', '2017-12-21 05:44:51', '', 0, 'http://luchanglong.com.cn/?p=457', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (458, 1, '2017-12-20 15:29:52', '2017-12-20 07:29:52', '', 'Django 常用命令', '', 'inherit', 'closed', 'closed', '', '457-revision-v1', '', '', '2017-12-20 15:29:52', '2017-12-20 07:29:52', '', 457, 'http://luchanglong.com.cn/?p=458', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (459, 1, '2017-12-20 15:42:49', '2017-12-20 07:42:49', '创建项目\r\n\r\ndjango-admin.py startproject HelloWorld\r\n\r\n创建app\r\ndjango-admin.py startapp TestModel\r\n\r\n创建表结构\r\n$ python manage.py migrate   # 创建表结构\r\n\r\n$ python manage.py makemigrations TestModel  # 让 Django 知道我们在我们的模型有一些变更\r\n$ python manage.py migrate TestModel   # 创建表结构\r\n\r\n', 'Django 常用命令', '', 'inherit', 'closed', 'closed', '', '457-revision-v1', '', '', '2017-12-20 15:42:49', '2017-12-20 07:42:49', '', 457, 'http://luchanglong.com.cn/?p=459', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (460, 1, '2017-12-21 13:44:51', '2017-12-21 05:44:51', '创建项目\r\n\r\ndjango-admin.py startproject HelloWorld\r\n\r\n创建app\r\ndjango-admin.py startapp TestModel\r\n\r\n创建表结构\r\n$ python manage.py migrate   # 创建表结构\r\n\r\n$ python manage.py makemigrations TestModel  # 让 Django 知道我们在我们的模型有一些变更\r\n$ python manage.py migrate TestModel   # 创建表结构\r\n\r\n在新增表后重新执行：\r\n$ python manage.py makemigrations TestModel  # 让 Django 知道我们在我们的模型有一些变更\r\n$ python manage.py migrate TestModel   # 创建表结构', 'Django 常用命令', '', 'inherit', 'closed', 'closed', '', '457-revision-v1', '', '', '2017-12-21 13:44:51', '2017-12-21 05:44:51', '', 457, 'http://luchanglong.com.cn/?p=460', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (462, 1, '2017-12-29 16:10:00', '2016-01-01 20:00:00', 'uWSGI是一个庞大的项目，能够完成比单独为Web应用程序提供更多的功能。然而，其广泛的功能，相对容易的配置，使它成为许多部署需求的一个很好的选择，尤其是当它与Nginx结合。\r\n\r\n在这篇DigitalOcean文章中，我们的目标是深入讨论uWSGI，并且不仅要安装服务器，而且要实际部署基于各种框架的Python应用程序，涵盖小型，中型甚至相对较大型的各个重要步骤生产。\r\n\r\n我们也将讨论如何使用Nginx（以及为什么），因为Nginx和uWSGI是本地构建的，彼此协同工作，形成大多数部署的完美栈。\r\n\r\n', '如何使用带有Nginx的uWSGI Web服务器部署Python WSGI应用程序', '', 'draft', 'open', 'open', '', '', '', '', '2017-12-29 16:10:00', '2017-12-29 08:10:00', '', 0, 'http://luchanglong.com.cn/?p=462', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (463, 1, '2017-12-29 16:09:49', '2017-12-29 08:09:49', 'uWSGI是一个庞大的项目，能够完成比单独为Web应用程序提供更多的功能。然而，其广泛的功能，相对容易的配置，使它成为许多部署需求的一个很好的选择，尤其是当它与Nginx结合。\r\n\r\n在这篇DigitalOcean文章中，我们的目标是深入讨论uWSGI，并且不仅要安装服务器，而且要实际部署基于各种框架的Python应用程序，涵盖小型，中型甚至相对较大型的各个重要步骤生产。\r\n\r\n我们也将讨论如何使用Nginx（以及为什么），因为Nginx和uWSGI是本地构建的，彼此协同工作，形成大多数部署的完美栈。\r\n\r\n', '如何使用带有Nginx的uWSGI Web服务器部署Python WSGI应用程序', '', 'inherit', 'closed', 'closed', '', '462-revision-v1', '', '', '2017-12-29 16:09:49', '2017-12-29 08:09:49', '', 462, 'http://luchanglong.com.cn/?p=463', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (464, 1, '2018-01-02 13:44:22', '2018-01-02 05:44:22', '&nbsp;\r\n<pre class=\"lang:js decode:true \"> &lt;p&gt;页面将在&lt;span id=\"show\"&gt;&lt;/span&gt;秒后自动跳转&lt;/p&gt;\r\n\r\n&lt;script type=\"text/javascript\"&gt;\r\n    var t=5;//设定跳转的时间\r\n    setInterval(\"refer()\",1000); //启动1秒定时\r\n    function refer(){\r\n        if(t===0){\r\n            location=\"{!! url(\'\') !!}\"; //#设定跳转的链接地址\r\n\r\n        }\r\n        if(t&gt;-1){\r\n            document.getElementById(\'show\').innerHTML=\"\"+t+\"\"; // 显示倒计时\r\n            t--; // 计数器递减\r\n        }\r\n\r\n    }\r\n&lt;/script&gt;</pre>\r\n&nbsp;', '支付成功后自动跳转页面/倒计时/定时跳转页面', '', 'publish', 'open', 'open', '', '%e6%94%af%e4%bb%98%e6%88%90%e5%8a%9f%e5%90%8e%e8%87%aa%e5%8a%a8%e8%b7%b3%e8%bd%ac%e9%a1%b5%e9%9d%a2%e5%80%92%e8%ae%a1%e6%97%b6%e5%ae%9a%e6%97%b6%e8%b7%b3%e8%bd%ac%e9%a1%b5%e9%9d%a2', '', '', '2018-01-02 13:46:47', '2018-01-02 05:46:47', '', 0, 'http://luchanglong.com.cn/?p=464', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (465, 1, '2018-01-02 13:44:22', '2018-01-02 05:44:22', '<pre class=\"lang:default decode:true  \"> &lt;p&gt;页面将在&lt;span id=\"show\"&gt;&lt;/span&gt;秒后自动跳转&lt;/p&gt;</pre>\r\n&nbsp;\r\n<pre class=\"lang:js decode:true\">&lt;script type=\"text/javascript\"&gt;\r\n    var t=5;//设定跳转的时间\r\n    setInterval(\"refer()\",1000); //启动1秒定时\r\n    function refer(){\r\n        if(t===0){\r\n            location=\"{!! url(\'\') !!}\"; //#设定跳转的链接地址\r\n\r\n        }\r\n        if(t&gt;-1){\r\n            document.getElementById(\'show\').innerHTML=\"\"+t+\"\"; // 显示倒计时\r\n            t--; // 计数器递减\r\n        }\r\n\r\n    }\r\n&lt;/script&gt;</pre>\r\n&nbsp;', '支付成功后自动跳转页面/倒计时/定时跳转页面', '', 'inherit', 'closed', 'closed', '', '464-revision-v1', '', '', '2018-01-02 13:44:22', '2018-01-02 05:44:22', '', 464, 'http://luchanglong.com.cn/?p=465', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (466, 1, '2018-01-02 13:45:06', '2018-01-02 05:45:06', 'Html部分：\r\n<pre class=\"lang:default decode:true\"> &lt;p&gt;页面将在&lt;span id=\"show\"&gt;&lt;/span&gt;秒后自动跳转&lt;/p&gt;</pre>\r\nJS部分：\r\n<pre class=\"lang:js decode:true\">&lt;script type=\"text/javascript\"&gt;\r\n    var t=5;//设定跳转的时间\r\n    setInterval(\"refer()\",1000); //启动1秒定时\r\n    function refer(){\r\n        if(t===0){\r\n            location=\"{!! url(\'\') !!}\"; //#设定跳转的链接地址\r\n\r\n        }\r\n        if(t&gt;-1){\r\n            document.getElementById(\'show\').innerHTML=\"\"+t+\"\"; // 显示倒计时\r\n            t--; // 计数器递减\r\n        }\r\n\r\n    }\r\n&lt;/script&gt;</pre>\r\n&nbsp;', '支付成功后自动跳转页面/倒计时/定时跳转页面', '', 'inherit', 'closed', 'closed', '', '464-revision-v1', '', '', '2018-01-02 13:45:06', '2018-01-02 05:45:06', '', 464, 'http://luchanglong.com.cn/?p=466', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (467, 1, '2018-01-02 13:46:47', '2018-01-02 05:46:47', '&nbsp;\r\n<pre class=\"lang:js decode:true \"> &lt;p&gt;页面将在&lt;span id=\"show\"&gt;&lt;/span&gt;秒后自动跳转&lt;/p&gt;\r\n\r\n&lt;script type=\"text/javascript\"&gt;\r\n    var t=5;//设定跳转的时间\r\n    setInterval(\"refer()\",1000); //启动1秒定时\r\n    function refer(){\r\n        if(t===0){\r\n            location=\"{!! url(\'\') !!}\"; //#设定跳转的链接地址\r\n\r\n        }\r\n        if(t&gt;-1){\r\n            document.getElementById(\'show\').innerHTML=\"\"+t+\"\"; // 显示倒计时\r\n            t--; // 计数器递减\r\n        }\r\n\r\n    }\r\n&lt;/script&gt;</pre>\r\n&nbsp;', '支付成功后自动跳转页面/倒计时/定时跳转页面', '', 'inherit', 'closed', 'closed', '', '464-revision-v1', '', '', '2018-01-02 13:46:47', '2018-01-02 05:46:47', '', 464, 'http://luchanglong.com.cn/?p=467', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (469, 1, '2018-01-22 17:02:37', '2018-01-22 09:02:37', '清理配置缓存（当你添加配置文件或修改配置文件时一定要清理，否则无法读取新增配置文件或变动）：\r\n\r\nphp artisan config:clear\r\n\r\n清理文件缓存：\r\n\r\nphp artisan cache:clear', 'Laravel 清理文件缓存、配置缓存（修改配置文件不生效可以解决）', '', 'publish', 'open', 'open', '', 'laravel-%e6%b8%85%e7%90%86%e6%96%87%e4%bb%b6%e7%bc%93%e5%ad%98%e3%80%81%e9%85%8d%e7%bd%ae%e7%bc%93%e5%ad%98%ef%bc%88%e4%bf%ae%e6%94%b9%e9%85%8d%e7%bd%ae%e6%96%87%e4%bb%b6%e4%b8%8d%e7%94%9f%e6%95%88', '', '', '2018-01-22 17:02:37', '2018-01-22 09:02:37', '', 0, 'http://luchanglong.com.cn/?p=469', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (470, 1, '2018-01-22 17:02:37', '2018-01-22 09:02:37', '清理配置缓存（当你添加配置文件或修改配置文件时一定要清理，否则无法读取新增配置文件或变动）：\r\n\r\nphp artisan config:clear\r\n\r\n清理文件缓存：\r\n\r\nphp artisan cache:clear', 'Laravel 清理文件缓存、配置缓存（修改配置文件不生效可以解决）', '', 'inherit', 'closed', 'closed', '', '469-revision-v1', '', '', '2018-01-22 17:02:37', '2018-01-22 09:02:37', '', 469, 'http://luchanglong.com.cn/?p=470', 0, 'revision', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (472, 1, '2018-02-06 13:22:49', '2018-02-06 05:22:49', 'css3 transition 和 animation实现横向、纵向 滚动、走马灯( marquee 在H5已被废弃)\r\n\r\n横向：\r\n\r\ntransition写法\r\n<pre class=\"lang:default decode:true \">&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n&lt;meta charset=\"UTF-8\"&gt;\r\n&lt;title&gt;marquee&lt;/title&gt;\r\n&lt;style type=\"text/css\"&gt;\r\nbody{\r\npadding: 0;\r\nmargin: 0;\r\n}\r\n#demo{\r\nwidth: 100%;\r\nheight: 50px;\r\nbackground: #eee;\r\nposition: fixed;\r\n}\r\n#demo&gt;#spa{\r\nword-break:keep-all;\r\nwhite-space:nowrap;\r\nposition: absolute;\r\nleft: 100%;\r\nfont-size: 30px;\r\nline-height: 50px;\r\n}\r\n&lt;/style&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n    &lt;div id=\"demo\"&gt;&lt;span id=\'spa\' &gt;走马灯效果&lt;/span&gt;&lt;/div&gt;\r\n&lt;/body&gt;\r\n&lt;script type=\"text/javascript\"&gt;\r\n     var spa = document.getElementById(\"spa\");\r\n     var spaw = spa.offsetWidth;\r\n     var bodyw = document.body.clientWidth;\r\n     var w = 0-(spaw+bodyw);\r\n     spa.style.transform = \'translate(\' + w + \'px, 0px)\';\r\n     spa.style.transition = \'transform 5s linear\';\r\n     window.setInterval(function(){\r\n          spa.style.transform = \'translate(0px, 0px)\';\r\n          spa.style.transition = \'transform 0s linear\';\r\n          window.setTimeout(function(){\r\n               spa.style.transform = \'translate(\' + w + \'px, 0px)\';\r\n               spa.style.transition = \'transform 5s linear\';\r\n          },100)\r\n     },5000)\r\n&lt;/script&gt;\r\n&lt;/html&gt;</pre>\r\nanimation写法\r\n<pre class=\"lang:default decode:true\">&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n&lt;meta charset=\"UTF-8\"&gt;\r\n&lt;title&gt;marquee&lt;/title&gt;\r\n&lt;style type=\"text/css\"&gt;\r\n#demo{\r\nwidth: 100%;\r\nheight: 50px;\r\nbackground: #eee;\r\nposition: fixed;\r\n}\r\n#demo&gt;span{\r\nword-break:keep-all;\r\nwhite-space:nowrap;\r\nposition: absolute;\r\nleft: 100%;\r\nfont-size: 30px;\r\nline-height: 50px;\r\n}\r\n#demo&gt;.a{\r\n-webkit-animation:demo 5s infinite;\r\n-webkit-animation-timing-function:linear;\r\n}\r\n&lt;/style&gt;\r\n&lt;style id=\'asty\'&gt;&lt;/style&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n    &lt;div id=\"demo\"&gt;&lt;span id=\'spa\' class=\'a\'&gt;走马灯效果&lt;/span&gt;&lt;/div&gt;\r\n&lt;/body&gt;\r\n&lt;script type=\"text/javascript\"&gt;\r\n    var spa = document.getElementById(\"spa\");\r\n    var width = 0-(spa.offsetWidth);\r\n    var style = document.getElementById(\"asty\");\r\n    style.innerHTML = \'@-webkit-keyframes demo{from {left: 100%;}to {left: \'+width+\'px;}}\';\r\n    spa.className = \'a\';\r\n&lt;/script&gt;\r\n&lt;/html&gt;</pre>\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n纵向：\r\n\r\n一、逐条无缝滚动（无闪动）\r\n<pre class=\"lang:default decode:true \">&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n    &lt;meta charset=\"UTF-8\"&gt;\r\n    &lt;title&gt;marquee&lt;/title&gt;\r\n    &lt;style type=\"text/css\"&gt;\r\n        @-webkit-keyframes scrollText1 {\r\n            0%{\r\n                -webkit-transform: translateY(0px);\r\n            }\r\n            20%{\r\n                -webkit-transform: translateY(-30px);\r\n            }\r\n            40%{\r\n                -webkit-transform: translateY(-60px);\r\n            }\r\n            60%{\r\n                -webkit-transform: translateY(-90px);\r\n            }\r\n            80%{\r\n                -webkit-transform: translateY(-120px);\r\n            }\r\n            100%{\r\n                -webkit-transform: translateY(-150px);\r\n            }\r\n        }\r\n\r\n        @keyframes scrollText1 {\r\n            0%{\r\n                transform: translateY(0px);\r\n            }\r\n            20%{\r\n                transform: translateY(-30px);\r\n            }\r\n            40%{\r\n                transform: translateY(-60px);\r\n            }\r\n            60%{\r\n                transform: translateY(-90px);\r\n            }\r\n            80%{\r\n                transform: translateY(-120px);\r\n            }\r\n            100%{\r\n                transform: translateY(-150px);\r\n            }\r\n        }\r\n\r\n        .box3{\r\n            position: relative;\r\n            top: 20px;\r\n            left: 20px;\r\n            width: 200px;\r\n            height: 30px;\r\n            overflow: hidden;\r\n            border:1px solid #ccc;\r\n        }\r\n\r\n        .border3{\r\n            top: 0px;\r\n            -webkit-animation:scrollText1 8s infinite  cubic-bezier(1,0,0.5,0) ;\r\n            animation:scrollText1 8s infinite  cubic-bezier(1,0,0.5,0) ;\r\n        }\r\n\r\n        .border3 div{\r\n            height: 30px;\r\n        }\r\n\r\n        .border3:hover{\r\n            animation-play-state:paused;\r\n            -webkit-animation-play-state:paused;\r\n        }\r\n    &lt;/style&gt;\r\n\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n&lt;div class=\"box3\"&gt;\r\n    &lt;div class=\"border3\"&gt;\r\n        &lt;div&gt;This is a test 1.&lt;/div&gt;\r\n        &lt;div&gt;This is a test 2.&lt;/div&gt;\r\n        &lt;div&gt;This is a test 3.&lt;/div&gt;\r\n        &lt;div&gt;This is a test 4.&lt;/div&gt;\r\n        &lt;div&gt;This is a test 5.&lt;/div&gt;\r\n        &lt;div&gt;This is a test 1.&lt;/div&gt;\r\n    &lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;/body&gt;\r\n\r\n&lt;/html&gt;</pre>\r\n&nbsp;\r\n\r\n二、\r\n<pre class=\"lang:default decode:true\">&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n    &lt;meta charset=\"UTF-8\"&gt;\r\n    &lt;title&gt;marquee&lt;/title&gt;\r\n    &lt;style type=\"text/css\"&gt;\r\n        .marquee div {\r\n            display: block;\r\n            width: 100%;\r\n            text-align: center;\r\n            position: absolute;\r\n            overflow: hidden;\r\n            -webkit-animation: marquee 5s linear infinite;\r\n            animation: marquee 5s linear infinite;\r\n        }\r\n        @keyframes marquee {\r\n            0% {\r\n                transform: translateY(-50);\r\n            }\r\n            100% {\r\n                transform: translateY(-230px); // 每行高50\r\n            }\r\n        }\r\n    &lt;/style&gt;\r\n\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n&lt;div class=\"marquee\"&gt;\r\n    &lt;div&gt;\r\n        &lt;p class=\"label_txt\"&gt;我是最后一个&lt;/p&gt;\r\n        &lt;p&gt;让我掉下眼泪的 不止昨夜的酒&lt;/p&gt;\r\n        &lt;p&gt;让我依依不舍的 不止你的温柔&lt;/p&gt;\r\n        &lt;p&gt;余路还要走多久 你攥着我的手&lt;/p&gt;\r\n        &lt;p&gt;走到玉林路的尽头 坐在(走过)小酒馆的门口&lt;/p&gt;\r\n        &lt;p class=\"label_txt\"&gt;我是最后一个&lt;/p&gt;\r\n    &lt;/div&gt;\r\n&lt;/div&gt;</pre>\r\n&nbsp;', 'css3 transition 和 animation实现横向、纵向 滚动、走马灯( marquee 在H5已被废弃)', '', 'publish', 'open', 'open', '', 'css3-transition-%e5%92%8c-animation%e5%ae%9e%e7%8e%b0-%e8%b5%b0%e9%a9%ac%e7%81%af-marquee-%e5%9c%a8h5%e5%b7%b2%e8%a2%ab%e5%ba%9f%e5%bc%83', '', '', '2018-03-07 11:31:50', '2018-03-07 03:31:50', '', 0, 'http://luchanglong.com.cn/?p=472', 0, 'post', '', 0);
INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES (473, 1, '2018-02-06 13:22:49', '2018-02-06 05:22:49', 'transition写法\r\n<pre class=\"lang:default decode:true\">&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n&lt;meta charset=\"UTF-8\"&gt;\r\n&lt;title&gt;marquee&lt;/title&gt;\r\n&lt;style type=\"text/css\"&gt;\r\nbody{\r\npadding: 0;\r\nmargin: 0;\r\n}\r\n#demo{\r\nwidth: 100%;\r\nheight: 50px;\r\nbackground: #eee;\r\nposition: fixed;\r\n}\r\n#demo&gt;#spa{\r\nword-break:keep-all;\r\nwhite-space:nowrap;\r\nposition: absolute;\r\nleft: 100%;\r\nfont-size: 30px;\r\nline-height: 50px;\r\n}\r\n&lt;/style&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n    &lt;div id=\"demo\"&gt;&lt;span id=\'spa\' &gt;走马灯效果&lt;/span&gt;&lt;/div&gt;\r\n&lt;/body&gt;\r\n&lt;script type=\"text/javascript\"&gt;\r\n     var spa = document.getElementById(\"spa\");\r\n     var spaw = spa.offsetWidth;\r\n     var bodyw = document.body.clientWidth;\r\n     var w = 0-(spaw+bodyw);\r\n     spa.style.transform = \'translate(\' + w + \'px, 0px)\';\r\n     spa.style.transition = \'transform 5s linear\';\r\n     window.setInterval(function(){\r\n          spa.style.transform = \'translate(0px, 0px)\';\r\n          spa.style.transition = \'transform 0s linear\';\r\n          window.setTimeout(function(){\r\n               spa.style.transform = \'translate(\' + w + \'px, 0px)\';\r\n               spa.style.transition = \'transform 5s linear\';\r\n          },100)\r\n     },5000)\r\n&lt;/script&gt;\r\n&lt;/html&gt;</pre>\r\nanimation写法\r\n<pre class=\"lang:default decode:true \">&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n&lt;meta charset=\"UTF-8\"&gt;\r\n&lt;title&gt;marquee&lt;/title&gt;\r\n&lt;style type=\"text/css\"&gt;\r\n#demo{\r\nwidth: 100%;\r\nheight: 50px;\r\nbackground: #eee;\r\nposition: fixed;\r\n}\r\n#demo&gt;span{\r\nword-break:keep-all;\r\nwhite-space:nowrap;\r\nposition: absolute;\r\nleft: 100%;\r\nfont-size: 30px;\r\nline-height: 50px;\r\n}\r\n#demo&gt;.a{\r\n-webkit-animation:demo 5s infinite;\r\n-webkit-animation-timing-function:linear;\r\n}\r\n&lt;/style&gt;\r\n&lt;style id=\'asty\'&gt;&lt;/style&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n    &lt;div id=\"demo\"&gt;&lt;span id=\'spa\' class=\'a\'&gt;走马灯效果&lt;/span&gt;&lt;/div&gt;\r\n&lt;/body&gt;\r\n&lt;script type=\"text/javascript\"&gt;\r\n    var spa = document.getElementById(\"spa\");\r\n    var width = 0-(spa.offsetWidth);\r\n    var style = document.getElementById(\"asty\");\r\n    style.innerHTML = \'@-webkit-keyframes demo{from {left: 100%;}to {left: \'+width+\'px;}}\';\r\n    spa.className = \'a\';\r\n&lt;/script&gt;\r\n&lt;/html&gt;</pre>\r\n&nbsp;', 'css3 transition 和 animation实现 走马灯( marquee 在H5已被废弃)', '', 'inherit', 'closed', 'closed', '', '472-revision-v1', '', '', '2018-02-06 13:22:49', '2018-02-06 05:22:49', '', 472, 'http://luchanglong.com.cn/?p=473', 0, 'revision', '', 0);
